css:
@-moz-document domain("play.google.com") {
body.qp {
    background-color: #101010;
}
.paper-toolbar-0 {
    background-color: #505050;
}
a,
.simple-dialog a {
    color: #f1f1f1;
}
.sj-search-box-0 {
    background-color: #a0a0a0;
}
#nav-container .nav-toolbar {
    background-color: #a0a0a0;
}
.paper-drawer-panel-0 iron-selector.paper-drawer-panel > #drawer.paper-drawer-panel {
    background-color: #a0a0a0;
    border-bottom: 1px solid #707070;
}
.nav-item-container.selected {
    background-color: #c0c0c0;
}
.new-playlist-drawer .nav-section-divider {
    border-bottom: 1px solid #707070;
}
#left-nav-close-button {
    color: #606060;
}
.paper-toolbar-1 {
    background-color: #a0a0a0;
}
.paper-header-panel-0[mode=waterfall] #mainContainer.paper-header-panel {
    background-color: #a0a0a0;
}
#playlist-drawer .autoplaylist-section,
#playlist-drawer #recent-playlists-container {
    border-bottom: 1px solid #707070;
}
#playlist-drawer paper-header-panel[at-top] paper-toolbar:not([style-scope]):not(.style-scope) {
    border-bottom: 1px solid #707070;
}
paper-header-panel#content-container:not([style-scope]):not(.style-scope):not(.transparent)[at-top] paper-toolbar#material-app-bar {
    border-bottom: 1px solid #707070;
}
sj-home[selected="3"] .home-palette-id-1 {
    color: #212121;
}
#player {
    background-color: #505050;
}
a,
.simple-dialog a {
    color: #212121;
}
sj-home[selected="2"] .home-palette-id-1 {
    color: #212121;
}
#player-artist,
.player-album,
.player-dash {
    color: #212121;
}
#time_container_current,
#time_container_duration {
    color: #212121;
}
#material-player-right-wrapper .player-top-right-items .time-label {
    color: #212121;
}
}
