css:
@-moz-document domain('fanfiction.net')
{

.nocopy
{
    -webkit-touch-callout: text !important;
    -webkit-user-select: text !important;
    -khtml-user-select: text !important;
    -moz-user-select: text !important;
    -ms-user-select: text !important;
    user-select: text !important;
}

.xlight
{
    color: #C9C9C9 !important;
}

.dropdown-menu
{
    background-color: #515151 !important;
}

.disabled
{
    background-color: #000 !important;
}

body
{
    background-color: #797979 !important;
    color: #C9C9C9 !important;
}

textarea, input[type="text"], input[type="input"], select, .lc, .btn, .modal-header, .modal-body, .modal-footer
{
    background-color: #797979 !important;
    color: #C9C9C9 !important;
    background-image: linear-gradient(to bottom, #797979, #515151) !important;
}

.lc
{
    background-color: RGBA(0, 0, 0, 0) !important;
    background-image: none !important;
}

div.lc, .lc-left, span.lc
{
    background-image: linear-gradient(to bottom, #797979, #515151) !important;
    border-radius: 10px !important;
    border: 1px solid !important;
    border-color: #C9C9C9 !important;
    padding: 3px 5px 3px 5px !important;
}

.lc-wrapper {
    margin-bottom: 10px !important;
}

.stitle, a
{
    color: #C9C9E9 !important;
}

.xicon-section-arrow, #l_fs
{
    background-color: #777 !important;
}

.zmenu, .tcat, .table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th
{
    background-color: #666 !important;
}

.xgray, .gray
{
    color: #D9C9D9 !important;
}

#content_wrapper
{
    width: 90% !important;
}

#content_wrapper_inner
{
    background-color: #333 !important;
}

.panel_error, .panel_success, .panel_warning, .panel_normal, .panel_plain, .gui_success, .gui_success
{
    padding: 10px !important;
    border: 1px outset !important;
    margin-bottom: 5px !important;
    text-align: left !important;
}

.gui_error, .gui_warning, .panel_error, .panel_warning
{
    background: #773333 !important;
    color: #999922 !important;
}

.panel_success
{
    background: #225522 !important;
    color: #C9C9E9 !important;
}

.panel_normal, .panel_plain
{
    background-color: #797979 !important;
    color: #C9C9C9 !important;
}

ul li.active {
    background-color: #000000 !important;
}

ul.topnav li ul li a.underline:hover{
    background-color: #5673b2 !important;
}

ul.topnav li a.underline:hover{
    background-color: #675c7c !important;
}

img.lazy {
    margin-bottom: 3px !important;
}

.bubbledNNormal {
    overflow: auto !important;
}
}
