css:
@-moz-document domain("friends.nico"), domain("mstdn-bike.net"), domain("pawoo.net"), domain("mstdn.jp") {
.drawer {
    overflow : visible ;
    width : 25% !important ;
    height : 420px !important ;
}
.emoji-dialog {
    z-index : 1 !important  ;
    position : fixed !important ;
    top : 5% !important ;
    left : 0px !important ;
    margin-left : 25% !important ;
    height : 90% !important ;
}
.column {
    flex : auto  !important ;
}
.column:nth-child(3) {
    position : fixed !important ;
    width : 25% !important ; 
    top : 390px !important ;
    height : 100% ;
    padding-left : 10px !important ;
}
.drawer__inner,
.ui {
    background-color: #444B5D;
}
.scrollable ,
.drawer__inner.darker,
.search-results__section *,
.tabs-bar__link:hover,
.drawer__header>a:hover,
.drawer__header>a:hover * {
    background-color: #F2F2F2 !important;
color: #222 !important;
}
.scrollable *:not(.fa){
    background-color: #F2F2F2;
    color: #222;
}
.account__header div *,
.account__header span,
.account__header__content,
.account__header .fa,
.muted a.status__content__spoiler-link,
.status__content__spoiler-link:hover >span,
.drawer__tab > .fa,
.drawer__tab:hover > .fa  {
    background: rgba(0, 0, 0, 0) !important;
}
.account__header>div {
    background: rgba(245, 245, 255, 0.7)!important;
}
.account__header *{
    color:#222 !important;
}
.status__content a,
.status__content a>span,
span.ellipsis,
.active,
.search__icon * {
    color: #2196F3 !important;
}
.tabs-bar__link.active{
    border-color: #2196F3 ;
}
.compose-form__buttons .active{
    background-color: #282c37;
}
.button{
    background-color: #2196F3;
    opacity: 1;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.button:hover{
    background-color: #2196F3;
    opacity: 0.8;
}
.search * {
    border-radius: 0.4em;
}
.search__input,
.search__input:focus {
    background-color: #F2F2F2;
}
.status__content__spoiler-link *:hover {
    color: #FFF;
}
.column-header,
.column-icon,
.tabs-bar,
.drawer__header,
.drawer__header *,
.column-back-button,
.search-results__header {
    background-color: #2F3442b;
    color: #FFF;
}
.status__content__spoiler-link:hover >span {
    color: #fff !important;
}
.dropdown__content>ul,
.dropdown__content>ul *{
	width:25vw !important;
}
}
