css:
@-moz-document regexp(".*(pawoo\\.net|friends\\.nico|social\\.tchncs\\.de|octodon\\.social|mamot\\.fr|social\\.lou\\.lt).*"), regexp(".*\\.*ma*sto*do*n.*\\.*") {
@media (min-width: 1900px) {
html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,article,aside,canvas,details,embed,figure,figcaption,footer,header,hgroup,menu,nav,output,ruby,section,summary,time,mark,audio,video
{ font-size: 12pt !important; line-height: 1.25em; }
.column,.drawer { width: 475px; }
}
@media (min-width: 2540px) {
html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,article,aside,canvas,details,embed,figure,figcaption,footer,header,hgroup,menu,nav,output,ruby,section,summary,time,mark,audio,video
{ font-size: 16pt !important; line-height: 1.25em; }
.column,.drawer { width: 633px; }
}
@media (min-width: 3820px) {
html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,article,aside,canvas,details,embed,figure,figcaption,footer,header,hgroup,menu,nav,output,ruby,section,summary,time,mark,audio,video
{ font-size: 20pt !important; line-height: 1.25em; }
.column,.drawer { width: 840px; }
}

body {
	font-family: SanSerif, Roboto, '源ノ角ゴシック Regular';
}

.status__avatar
{
	border-radius: .5em;
}

/*
	background-color
*/

.account__header > div
{
	background: rgba(0, 0, 0, 0.6) !important;
}

.status__avatar, .button, .fa-eye, .icon-button.overlayed
{
	background-color: #2c2c2c !important;
}

.button:hover,.button:active,.button:focus
{
	background-color: #d3d3d3 !important;
}

.button:disabled, .privacy-dropdown.active .privacy-dropdown__value
{
	background-color: #2c2c2c;
}

.ui
, .react-toggle-track
{
	background-color: #2c2c2c !important;
}

.column-header, .column-icon, .column-back-button
, .drawer__tab, .search-results__header
, .drawer__inner, .navigation-bar, .status__content > p
{
	background-color: #d3d3d3;
}

div.status__content
{
	margin-top: .2em;
	padding: .1em;
	border-top: 1px solid #c0c0c0 !important;
}

.column>.scrollable, .detailed-status, .detailed-status__action-bar
, .privacy-dropdown__option:hover,.privacy-dropdown__option.active
, .scrollable *:not(.fa):not(.status__avatar):not(.avatar)
:not(.account__header):not(.account__header__display-name):not(.account__header__username):not(.account__header__content)
{
	background-color: #d3d3d3;
}

.column-link
, .search-results__section *, textarea.autosuggest-textarea__textarea, .compose-form__modifiers, .compose-form__buttons
, .emoji-dialog.with-search, .spoiler-input__input
, .search__input, .search__input:hover, .search__input:active
{
	background-color: #fff !important;
}

img.emojione
, .fa-user-times
, div.account__header > div > div:nth-child(5)
, div.account__header > div > div:nth-child(5) > button
, div.account__header > div > div:nth-child(5) > button > i
, div.account__header > div > div:nth-child(6)
, div.account__header > div > div:nth-child(6) > button
, .account--follows-info > span
, .account__header__content > a
, .account__header__content > p
, .account__header__content > p > a
, .account__header__content > p > a > span:not([class='invisible'])
, .account__header__content > a > span:not([class='invisible'])
{
	background-color: transparent !important;
}

.empty-column-indicator, .column-subheading
, .scrollable, .drawer__inner.darker
, .drawer__header>a:hover, .drawer__header>a:hover *
, .column-settings--outer
{
	background-color: #9e9e9e;
}

/*
	text-color
*/
::-webkit-input-placeholder
, .notification__message .fa
, div.notification__message > div > i
{
	color: #9e9e9e !important;
}

.scrollable *:not(.fa):not(.account__header__display-name):not(.account__header__username):not(.account__header__content):not(.account--follows-info)
, .column-header, .column-link, .column-icon, .column-back-button
, .empty-column-indicator
, .drawer__tab, .search-results__header
, .scrollable, .drawer__inner.darker, .search-results__section *
, .drawer__header>a:hover, .drawer__header>a:hover *
, .text-icon-button
, .muted .status__content p,.muted .status__content a
, .muted .status__display-name strong
, .spoiler-input__input
, .column-settings--section, .setting-toggle
, .setting-text, .tabs-bar
, .drawer__header, .drawer__header *
, .search__input, .search__input:hover, .search__input:active
, .column-header.active .fa, .column-subheading
,.status__content a .fa,.reply-indicator__content a .fa
, .privacy-dropdown__option:hover,.privacy-dropdown__option.active
{
	color: #2c2c2c;
}

.empty-column-indicator a, div.privacy-dropdown.active > div.privacy-dropdown__value > button
, .account__header .account__header__display-name
, .account__header .account__header__username
, .account__header .account__header__content
, .fa-user-times, .fa-eye, .icon-button.overlayed
, .account--follows-info > span
, .account__header > div > div > button.icon-button
, .account__header__content > p
, .account__header__content > p > a > span:not([class='invisible'])
, .account__header__content > a > span:not([class='invisible'])
{
	color: #d3d3d3 !important;
}

.status__content > p
{
	display: inline;
}

.status__content > p > a > span[class='invisible']
, .account__header__content > p > a > span[class='invisible']
{
	display: none;
}

.status__display-name.muted > strong
{
	color: #9e9e9e;
}

.setting-text, .setting-text:focus, .setting-text:active
{
	border: 1px solid #2c2c2c !important;
}

.icon-button
, .emoji-category-title, .emoji-search
, .fa-retweet, .search-results__section *
, .privacy-dropdown__option, .privacy-dropdown__option__content *
{
	color: #2c2c2c !important;
}

/* Scroll bar thumb default color */
::-webkit-scrollbar-thumb	{
 	background: #9e9e9e !important;   
}

/* Scroll bar thumb is acrive and hover */
::-webkit-scrollbar-thumb:hover,
::-webkit-scrollbar-thumb:active{
	background: #fff !important;
}

.react-toggle-track-check > svg > path
{ fill: #d3d3d3; }

.react-toggle-track-x > svg > path
{ fill: #d3d3d3; }

.react-toggle-thumb
{
	border: 1px solid #fff !important;
	background-color: #d3d3d3;
}


/* Scroll bar background  */
::-webkit-scrollbar-track{
	background: #2c2c2c !important;
}

/* Left sidebar */
.drawer__inner, .navigation-bar
{
	color: #000;
}

.autosuggest-textarea *{ color: black; }

/* Toot button */
.button {
	background-color: #2c2c2c;
}

/* Search Icon */
.status__content a,
.status__content a>span,
span.ellipsis,
.active,
.search__icon * {
	color: #000 !important;
}

/* Left header */
.tabs-bar,
.drawer__header,
.drawer__header *
{
	color: #2c2c2c;
}

}
