css:
@-moz-document domain("www.wikipendium.no") {
h1,
h2,
h3,
h4,
h5,
h6,
p,
td,
li,
dt,
dd {
    color: #dddddd !important;
}
body {
    background-color: #272727 !important;
}
.toc {
    background: rgb(12, 12, 12);
}
a {
    color: #4962af !important;
}
table th,
table td {
    border-bottom: 1px solid rgba(255, 255, 255, 0.13);
}
#toggle-toc {
    background: #004a65;
    border: 1px solid #005c7b;
}
body.fixed-header .header-wrapper {
    background: rgba(0, 0, 0, 0.4);
}

#suggestions .active {
    background: rgba(123, 224, 184, 0.8);
}

.article-all-list .date {
    color: #444 !important;
}
}
