css:
@-moz-document url-prefix("https://www.ziptiez.com/") {
::-moz-selection {
    color: #262626;
    background: #AB0913
}
::selection {
    color: #262626;
    background: #AB0913
}
html,
body {
    font-family: Roboto, Arial, sans-serif;
    background: #262626;
    color: #262626
}
h1,
h2,
h3,
h4,
h5,
h6 {
    font-weight: 700;
    font-family: Montserrat, Arial, sans-serif
}
a,
button {
    -webkit-transition: all 0.25s ease-in-out;
    -o-transition: all 0.25s ease-in-out;
    transition: all 0.25s ease-in-out
}
a:focus,
a:active,
button:focus,
button:active {
    outline: none !important
}
a {
    color: #919191
}
a:hover,
a:focus {
    color: #AB0913;
    text-decoration: none
}
label {
    font-size: 12px;
    font-family: Montserrat, Arial, sans-serif
}
label sup {
    color: red
}
img {
    max-width: 100%;
    height: auto
}
.btn,
.search-submit,
.alert,
.tooltip-inner,
.form-control {
    -webkit-border-radius: 0;
    border-radius: 0
}
.white {
    color: #fff !important
}
.dark {
    color: #919191 !important
}
.gray {
    color: #898989 !important
}
.bg-lighten {
    background-color: #f9f9f9 !important
}
.bg-gray {
    background-color: #f4f4f4 !important
}
.bg-dark {
    background-color: #919191 !important
}
.bg-darken {
    background-color: #262626 !important
}
.tiny {
    font-size: 10px
}
.small {
    font-size: 12px
}
.medium {
    font-size: 14px
}
.large {
    font-size: 16px
}
.f-right {
    float: right
}
.f-left {
    float: left
}
.light {
    font-weight: 300
}
.normal {
    font-weight: 400
}
.bold {
    font-weight: 700
}
.center {
    text-align: center
}
.left {
    text-align: left
}
.right {
    text-align: right
}
.upper,
.text-upper {
    text-transform: uppercase
}
.spacing-5 {
    letter-spacing: 5px
}
.spacing-10 {
    letter-spacing: 10px
}
.block {
    display: block !important
}
.inline-block {
    display: inline-block !important
}
.inline {
    display: inline !important
}
.border-left {
    border-left: solid 1px #262626
}
.border-right {
    border-right: solid 1px #262626
}
.border-top {
    border-top: solid 1px #262626
}
.border-bottom {
    border-bottom: solid 1px #262626
}
.fp-table {
    display: table;
    table-layout: fixed;
    text-align: center
}
.fp-table.text-left {
    text-align: left
}
.fp-table,
.fp-table-cell {
    width: 100%;
    height: 100%
}
.fp-table-cell {
    display: table-cell;
    vertical-align: middle
}
.col-centered {
    float: none;
    display: block;
    margin-left: auto;
    margin-right: auto
}
@media (min-width: 769px) {
    .cols-border>[class*='col-'] {
        border-right: solid 1px #262626
    }
    .cols-border>[class*='col-']:last-child {
        border-right: transparent
    }
}
.no-gutter {
    margin-left: 0;
    margin-right: 0
}
.no-gutter>[class*='col-'] {
    padding-right: 0;
    padding-left: 0
}
.white-popup {
    position: relative;
    background: #fff;
    padding: 20px;
    width: auto;
    max-width: 500px;
    margin: 20px auto
}
.embed-responsive-1by1 {
    padding-bottom: 100%
}
.underline:after {
    content: '';
    display: block;
