.img_ef1 {
    position: relative;
    overflow: hidden
}
.img_ef1 img {
    width: 100%;
    height: auto
}
.img_ef1:before {
    position: absolute;
    content: '';
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    z-index: 1;
    background: rgba(67, 69, 113, 0.1);
    -webkit-transition: all 0.6s ease-in-out;
    transition: all 0.6s ease-in-out;
    -moz-transition: all 0.6s ease-in-out;
    -ms-transition: all 0.6s ease-in-out;
    -o-transition: all 0.6s ease-in-out
}
.img_ef1:after {
    position: absolute;
    content: '';
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: rgba(67, 69, 113, 0.1);
    -webkit-transition: all 0.6s ease-in-out;
    transition: all 0.6s ease-in-out;
    -moz-transition: all 0.6s ease-in-out;
    -ms-transition: all 0.6s ease-in-out;
    -o-transition: all 0.6s ease-in-out
}
@media (min-width: 768px) {
    .img_ef1:hover:before {
        right: 50%;
        left: 50%;
        width: 0;
        background: rgba(255, 255, 255, 0.5)
    }
    .img_ef1:hover:after {
        height: 0;
        top: 50%;
        bottom: 50%;
        background: rgba(255, 255, 255, 0.5)
    }
}
