css:
@-moz-document domain("portal.kirkebakkeskolen.dk") {
body {
    font-family: Helvetica, Arial, sans-serif;
    font-size: 13px;
    color: #ffffff;
    line-height: 130%;
    margin-top: 10px;
    background-color: #737373
}
td {
    font-family: Helvetica, Arial, sans-serif;
    font-size: 13px;
    color: #ffffff;
    margin-bottom: 3px
}
p {
    line-height: 130%
}
a {
    font-size: 13px;
    color: #0478c6;
    text-decoration: none
}
h1 {
    font-family: Trebuchet MS, Helvetica, Arial, Verdana, sans-serif;
    font-size: 200%;
    margin-top: 3px;
    margin-bottom: 3px;
}
h2 {
    font-family: Trebuchet MS, Helvetica, Arial, Verdana, sans-serif;
    font-size: 150%;
    margin-top: 2px;
    margin-bottom: 2px;
}
h3 {
    font-family: Trebuchet MS, Helvetica, Arial, Verdana, sans-serif;
    font-size: 133%;
    margin-top: 0px;
    margin-bottom: 0px;
}
h4 {
    font-family: Trebuchet MS, Helvetica, Arial, Verdana, sans-serif;
    font-size: 116%;
    margin-top: 0px;
    margin-bottom: 0px;
}
h5 {
    font-family: Trebuchet MS, Helvetica, Arial, Verdana, sans-serif;
    font-size: 100%;
    margin-top: 0px;
    margin-bottom: 0px;
}
.forside {
    font-family: Trebuchet MS, Helvetica, Arial, sans-serif;
    font-size: 16px;
    text-align: left;
    font-weight: bold;
    color: #0D6C9E;
    background-color: #808080;
    margin-top: 2px;
    padding: 6px 12px 6px 12px;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
}
.felt {
    font-family: Helvetica, Arial, sans-serif;
    color: #ffffff;
    background-color: #808080;
    font-weight: bold;
    margin-top: 2px
}
.felt2 {
    font-family: Helvetica, Arial, sans-serif;
    color: #ffffff;
    background-color: #262626
}
.bjaelke {
    font-family: Helvetica, Arial, sans-serif;
    font-weight: bold;
    background-color: #808080
}
.tilfoej {
    font-family: Helvetica, Arial, sans-serif;
    font-weight: bold;
    background-color: #808080
}
.linje1 {
    background-color: #737373
}
.linje2 {
    background-color: #808080
}
.dagsdato {
    background-color: #808080
}
.fridag {
    background-color: #808080
}
.hit {
    font-family: Helvetica, Arial, sans-serif;
    font-variant: small-caps;
    font-style: normal;
    font-weight: bold;
    font-size: 13px;
    color: white;
    background-color: #808080
}
.faelles {
    font-family: Arial, Helvetica;
    font-style: normal;
    font-size: 8pt;
    color: white;
}
.smal {
    font-family: 'Arial Narrow', Arial, Helvetica;
    font-size: 6pt;
    line-height: 100%;
    margin-top: 0;
    margin-bottom: 0
}
li {
    margin-top: 4px;
    margin-bottom: 4px
}
hr {
    border-style: solid none none none;
    border-color: #606060;
    height: 0px;
    margin-top: 6px;
    margin-bottom: 6px
}
@media print {
    .noprint {
        visibility: hidden;
        position: absolute;
        top: 0px;
        left: 0px
    }
    .overskrift {
        height: 30px
    }
}
/* unvisited link */

a:link {
    color: #0059b3;
}
/* visited link */

a:visited {
    color: #5900b3;
}
/* mouse over link */

a:hover {
    color: #1a8cff;
}
/* selected link */

a:active {
    color: #0059b3;
}
td {
    font-family: Helvetica, Arial, sans-serif;
    font-size: 13px
}
body {
    font-family: Helvetica, Arial, sans-serif;
    font-size: 13px;
    background-color: #737373 !important
}
.menu {
    background-color: #737373 !important;
    font-family: Helvetica, Arial, sans-serif;
    font-size: 13px;
}
.menuhjaelp {
    font-family: Helvetica, Arial, sans-serif;
    font-size: 13px;
    font-weight: bold;
    color: #ffffff
}
}

@-moz-document regexp("portal.kirkebakkeskolen.dk") {
body         { font-family: Helvetica, Arial, sans-serif; font-size: 13px; background-color: #c0c0c0 }
.menu        { background-color: #EBEBE4; font-family: Helvetica, Arial, sans-serif; font-size: 13px }
.menuhjaelp  { font-family: Helvetica, Arial, sans-serif; font-size: 13px; font-weight: bold; color: #C0C0C0 }
}
