css:
@-moz-document domain("www.twitch.tv") {
.warp__item.warp__item--anchor-beta
{
    background: #282828;
}

.brick.brick--theme-white.metadata-box.qa-metadata-box.ember-view
{
    background-color: rgb(30,30,30)!important;
    border-color: rgb(30,30,30)!important;
}

.cn-metabar__title
{
    color: #706a7c!important;
}

.chat-container
{
    border-left: none;
}

.chat-interface
{
    border-top: none!important;
}

.social-column.js-social-column.flex.flex--column
{
    box-shadow: -0.1rem 0 0 #201c2b;
}

.new-search__input.form__input.focused
{
    background-color: #201c2b;
}

.activity-card__reason
{
    background: #282828;
    box-shadow: inset 0 1px 0 #333333, inset -1px 0 0 #333333, inset 1px 0 0 #333333;
}

.activity-card__comments
{
    padding-bottom: 0.5rem;
}

img#bttv_logo_new
{
    display: none;
}

img#bttv_logo
{
    display: none;
}

.warp__drawer.toggled
{
    box-shadow: 0 -1px 0 #282828!important;
}

.js-chat_input.chat_text_input.ember-text-area.ember-view
{
    border: solid 1px #282828!important
}

.warp__logo.ember-view:hover
{
    background: rgb(50,50,50)!important;
}

.warp__logo.ember-view
{
	padding-bottom: 8px;
    padding-top: 10px;
}

.cn-metabar__more.flex.flex__item.flex--verticalCenter.flex__item--noShrink
{
    padding-top: 0rem!important;
    border-top: none!important;
}

.warp__drawer
{
    box-shadow: 0 -1px 0 #282828!important;
}

.tw-tabs
{
    box-shadow: inset 0 -1px 0 #444444!important;
}

.list-load-more.mg-b-1
{
    border: none;
}

.new-search__input.form__input.focused
{
    border: none!important;
}

div#site_header.clearfix
{
    border-bottom: none;
}

.header_divider
{
    border-right: 1px solid #444444
}

.js-composer-emotes.activity-create__emotes
{
    box-shadow: none!important;
    position: absolute;
    top: 0px;
    right: 0px;
}

.button.button--icon-only.chat-header__button.chat-header__button--left.tooltip
{
    left: 10px
}

.activity-create--oneline.activity-create__textarea
{
	height: 5rem;
}

.button--icon-only:focus
{
    box-shadow: none!important;
}

.friend-search__input.js-friend-search.form__input.pd-l-3.ember-text-field.ember-view
{
    box-shadow: #282828 0 0 0 1px inset!important;
}

.mg-l-4.activity-create.activity-create--oneline.pos-relative.js-activity-create.ember-view
{
    border-color: #282828;
}

.player-placeholder
{
    background: #000000;
}

.offer-list .offer-item--title
{
    border-bottom: .1rem solid #dad8de;
}

.filter-bar__balloon-footer
{
    background-color: white;
}

.brick.c-background.metadata-box.qa-metadata-box.ember-view
{
    background-color: #1c1c1c!important;
    border-color: #1c1c1c!important;
}

.sticky-message.sticky-message--pinned-cheers.pinned-cheers.showModIcons.showTimestamps.ember-view
{
    border-color: #181818;
    padding-top: 4px;
}

.sticky-message.sticky-message--pinned-cheers.pinned-cheers.hideModIcons.showTimestamps.ember-view
{
    border-color: #181818;
    padding-top: 4px;
}

.pinned-cheer__top-bar--delimiter
{
    border-color: #323232
}

.pinned-cheer__expando-arrow
{
    right: 31px;
    padding-bottom: 5px;
}

.pinned-cheers
{
    padding-bottom: 3px;
}

.fp-container__pulse
{
    padding-top: 35px;
}

.tower--240
{
    padding-right: 0px!important;
}

.js-composer-emotes.activity-create__emotes
{
    top: -2px;
}

.dash-widget
{
    border: none;
}

.border-t.clearfix.mg-x-2
{
    margin-right: 5px;
    border-top: 1px solid #323232;
    margin-bottom: 5px!important;
    margin-top: 4px!important;
}

.pinned-cheers__message-wrapper.pinned-cheers--padding
{
    padding-bottom: 14px;
}

.profile-card.mg-b-2.profile-card--show-actions.ember-view
{
    margin-bottom: 0!important;
}

.button--icon.button--hollow figure svg
{
    fill: #4b317b!important;
}

#ember2099.ember-view
{
    margin-top: 30px;
}

js-chase-content
{
    position:fixed!important;
    top:80px!important;
    width:1050px!important;
}

.brick.brick--theme-grey.cmrc-channel-box.qa-cmrc-channel-box.flex.flex__item.flex__item--grow.flex--verticalCenter.balloon-wrapper.pd-b-0.mg-b-1
{
    top: 1px;
    background-color: #1c1c1c!important;
    border-color: #1c1c1c!important;
    box-shadow: 0px -1.1rem 0px -10px #282828
}

.icon-platform-windows--black svg
{
    fill: #b8b5c0!important;
}

.activity-create__textarea.form__input.pd-r-4.pd-b-2.js-post-message.ember-text-area.ember-view
{
    background-color: #191919
}

.panel.qa-panel.ember-view.masonry-brick
{
    border: #282828
}

.card__boxpin
{
    border-color: #141414;
}

.cn-metabar__more.flex.flex-shrink-0.flex-nowrap.align-items-center
{
	border-color: #444444!important;
}

.flex.flex--nowrap.flex__item.flex--verticalCenter.cmrc-igc-line.qa-cmrc-igc-line.js-cmrc-igc-line.cmrc-igc-line--with-separators.ember-view
{
    height: 30px;
    margin-bottom: 10px;
}

.cmrc-igc-line--with-separators
{
    border-left: 1px solid #282828;
    border-right: 1px solid #282828;
}

.warp__item.mg-t-1.mg-b-1.is-drawer-closed--hide
{
    margin-top: 0px!important;
}

.cn-vod-info__divider
{
    background: #1c1c1c;
}

.cn-chat-replay-header
{
	box-shadow: inset 0 -1px 0 0 #282828;
}

.chat-room .sticky-message
{
	border-color: #181818;
}

.activity-card
{
    color: #D3D3D3
}
.svg-trash
{
    margin-top: 6px;
}

.pinned-cheers:before
{
    content: "Close"!important;
    color: #898395!important;
    margin: 0px 11px 0px 0!important;
}
}
