css:
@-moz-document domain("www.emeraldchat.com") {
/*/ body wall /*/

body {
    background: url(http://i.imgur.com/dzbGBaT.jpg);
    background-repeat: no-repeat; 
    background-position: center; 
    background-attachment: fixed;
    background-size: cover, auto;
    color: #fddfdd;
}

/*/Login/*/
.emerald-header { background-color: #B38481 !important; }
.twenty-px-padding { 
    background-color: #B38481;
}


/*/ Post /*/

.nanopost-plain-post {
    border-color: rgba(127, 78, 82, 0.6);
    background-color: rgba(127, 78, 82, 0.6);
}

.micropost-header { 
    color: #fff !important;
}

.post-content {
    color: #fff
}

/*/ text area cam border/*/

.nanopost-plain-unpadded div {
    background: rgba(127, 78, 82, 0.6) !important;
}


/*/comment box /*/

.nanopost-comment {
    border-color: #7F4E52 !important;
    background-color: rgba(127, 78, 82, 0.6) !important;
}
.comment-text {
    color: #fff !important;
}

.comment-label {
    color: #fff;
} 

/*/Recommeneded groups/*/

.selectable-element {
    background: rgba(127, 78, 82, 0.6) !important;
}
/*/main bg /*/

.nanopost-plain {
    border-color: rgba(127, 78, 82, 0.6);
    background-color: rgba(127, 78, 82, 0.6);
}
/*/ Text area pad/*/

.nanopost-plain-unpadded {
    color: #B38481;
    border-color: rgba(127, 78, 82, 0.6);
    background-color: rgba(179, 132, 129, 0.6);
}
/*/ pic view lower /*/

.photo-viewer-scroll {
    background-color: rgba(127, 78, 82, 0.6);
}
/*/ pic view upper/*/

.photo-viewer-right div {
    background-color: rgba(127, 78, 82, 0.6) !important;
    border-color: rgba(127, 78, 82, 0.6) !important;
}
/*/meet-menu main button/*/

.btn-giga {
    background: rgb(127, 78, 82);
    border-color: rgb(127, 78, 82);
}
.btn-giga:hover {
    background: rgba(127, 90, 88, 0.9);
    border-color: rgba(127, 90, 88, 0.9);
}
/*/ meet menu bg /*/

.fullscreen-container {
    background: url(http://i.imgur.com/dzbGBaT.jpg);
    background-repeat: no-repeat; 
    background-position: center; 
    background-attachment: fixed;
    background-size: cover, auto;
    
}
.notification-menu-nobg {
    background: #7F4E52 !important;
}

.avatar-icon { background: #7F4E52;
}

.avatar-icon.bounce { background: #7F4E52;
}

.avatar-icon.bounce:hover { 
    background: rgba(127, 90, 88, 0.9);
    border-color: rgba(127, 90, 88, 0.9);
}

/*/loading/*/

.cube1 { background-color: #ECC5C0;} 
.cube2 { background-color: #ECC5C0;} 

/*/message box/*/

.room-messages {
    background-color: rgba(127, 78, 82, 0.6);
    border-color: rgba(127, 78, 82, 0.6);
}
/*/ message top border/*/

.message-element {
    border-top-color: rgb(65, 56, 57);
}
/*/border /*/

.room-container {
    background: url(http://i.imgur.com/dzbGBaT.jpg);
    background-repeat: no-repeat; 
    background-position: center; 
    background-attachment: fixed;
    background-size: cover, auto;

}
/*/textbox/*/

textarea {
    background-color: rgba(127, 90, 88, 0.9);
    border-color: rgba(127, 90, 88, 0.6);
    color: #FFDFDD;
}
/*/textbox border/*/

.room-input {
    border-color: rgba(65, 56, 57,0.10);
}
/*/who is typing/*/

div#typing_field {
    background: #7E354D !important;
}
/*/side panel/*/

.side-panel-desktop {
    background: url(http://i.imgur.com/dzbGBaT.jpg);
    background-repeat: no-repeat; 
    background-position: center; 
    background-attachment: fixed;
    border: 0px;
   	bottom: -47px
}

/*/online list/*/

.room-component {
    background: rgba(65, 56, 57, 0.6);
    border-color: rgba(65, 56, 57, 0.6);
}
/*/ view button /*/

.friend-element {
    background: #7F4E52;
    border-color: #7F4E52;
}
.friend-element:hover {
    background: rgba(196,129,137,0.6);
    border-color: rgba(196,129,137,0.6);
}
/*/ scrollbar/*/

::-webkit-scrollbar-thumb {
    background-color: rgba(126, 53, 77,0.6);
    border: 0px groove #7F525D;
}
/*/ scrollbar-hover/*/

::-webkit-scrollbar-thumb:hover {
    background-color: #7F525D;
    border: 0px groove #7F525D;
}
/*/ scrollbar track/*/

::-webkit-scrollbar-track {
    background: rgba(196, 135, 147,0.8);
    border: 0px none #FFC8E3;
}
/*/ scrollbar track-hover/*/

::-webkit-scrollbar-track:hover {
    background: rgb(179, 132, 129);
    border: 0px none #C48793;
}
/*/ button /*/

.room-button-micro {
    background: #7E354D;
}
/*/ button-hover /*/

.room-button-micro:hover {
    background: rgba(197,144,142,0.6);
}
a.btn-primary {
    background: #7F525D;
}
a.btn-primary:hover {
    background: rgba(197,144,142,0.6);
}
/*/dropdown/*/

.dropdown-menu {
    background-color: #7F525D;
    border-color: rgb(65, 56, 57);
}
.notification-text {
    color: #fff !important;
}
/*/room name/*/

a.interest-bubble {
    background-color: #7F4E52;
}
/*/exit chat/*/

.close-button {
    background: #7F525D;
}
/*/meet-menu return/*/

.btn-primary {
    background: #ECC5C0;
}
/*/ mute disabled/*/

.mute-button-enabled {
    background: #ECC5C0;
}
/*/notification icons/*/

.navbar-icon-element-inactive {
    color: #6b3051 !important;
    position: relative;
    top: 8px
}
.glyphicon.navbar-icon-element.glyphicon-cog {
    color: #6b3051 !important;
    position: relative;
    top: 8px
}

a.notification-text:hover {background-color: rgba(236,197,192,0.6) !important; }

/*/header/*/

.navbar { 
    border: solid 0px; 
    background: url(http://i.imgur.com/zau02wt.png), url(http://i.imgur.com/Dabo5W0.jpg) ; 
    background-size: 122px, cover, auto; 
    background-repeat: no-repeat; 
    background-position: center, 100% 0%; 
}

#logo { 
    visibility: hidden; }

.notification-menu-micro { 
    background-color: rgb(127, 78, 82); 
    border-color: rgb(127, 78, 82);
}

.profile-container-center {
    width: 70%;
    display: inline-block;
    max-width: 600px;
    margin-left: 26%;
    overflow: hidden;
}

/*/ user viewer/*/

.notification-menu {
    background-color: #7F5A58;
}

/*/meet menu bg/*/

.container-beta {
    background: url(http://i.imgur.com/dzbGBaT.jpg);
    background-repeat: no-repeat; 
    background-position: center; 
    background-attachment: fixed;
    background-size: cover, auto;
  
}


/*/ party invite /*/

.action-tray {
    border-color: #413839;
    background: #7F4E52;
}
/*/options menu/*/

.form-control-emerald {
    border-color: #413839;
    background-color: #7F525D;
}
option {
    background-color: #7F4E52
}
/*/ like comment tray/*/

.micropost-component-tray {
    background: #B38481;
}
/*/profile pic updater/*/

.btn-profile-picture-update {
    color: #f9f9f9;
    background: #B38481;
}
/*/friend control panel/*/

.btn-primary {
    background: #7F4E52;
}
.btn-primary:hover {
    background: rgba(197,144,142,0.6);
}
div.interest-bubble {
    background-color: #B38481
}

.micro-button {
    background-color:  #B38481; 
}

/*/ font options /*/

.beryl-drop-content {
    border-color: rgb(65, 56, 57);
    background-color: #7F4E52;
}
.font-color {
    background-color: rgb(65, 56, 57)
}
/*/side panel lines/*/

hr-blue {
    border-bottom: 1px solid #B38481 !important;
}
}
