  /* =======================[ my patch ]====================================== */
  
  table.files { font-family: 'Liberation Mono' !important;}
  
  .header {
    border-bottom: solid 1px #1e1f21 !important;
    background-image: linear-gradient(to bottom, #404041, #373839) !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#404041), to(#373839)) !important;
    background: linear-gradient(#404041, #373839) !important;
    box-shadow: 0 1px 1px rgba(10,10,10,.01) !important;
  }
  .main-content {
    background-color: #313335;
  }

  /*3d effect for reponav tabbar */
  .reponav-item.selected, .pagehead-tabs-item.selected {
    border-top: 1px solid #666666 !important;
    border-left: 1px solid #666666 !important;
    border-right: 1px solid #666666 !important;
    border-bottom: none !important;
    background-image: linear-gradient(to bottom, #404041, #313335) !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#404041), to(#313335)) !important;
    background: linear-gradient(#404041, #313335) !important;
    box-shadow: 0 -1px 0 #292B2D !important;
    margin-top: 2px !important;
    position: relative !important;
    z-index: 11113 !important; /*more than div.pagehead.repohead:after z-index*/
  }
  div.pagehead.repohead, header.org-header, .pagehead-orgs {
    position: relative !important;
  }
  div.pagehead.repohead:after, header.org-header:after, .pagehead-orgs:after {
    position: absolute;
    bottom: -2px;
    left: 0;
    background-color: #666666;
    height: 1px;
    right: 0;
    z-index: 11111; /*less than .reponav-item.selected z-index*/
    display: block;
    content: "";
  }
  div.pagehead.repohead:before, header.org-header:before, .pagehead-orgs:before {
    position: absolute;
    bottom: -1px;
    left: 0;
    background-color: #292B2D;
    height: 1px;
    right: 0;
    z-index: 11111; /*less than .reponav-item.selected z-index*/
    display: block;
    content: "";
  }

div.showcase-page-pattern {
    display: none;
}

header {
    background-color: transparent !important;
}
