css:
@-moz-document url-prefix("https://modx.ru/") {
* {
	box-sizing: border-box;
}
html {
	font-size: 16px;
}
body {
    background: #fff;
}
.wrapper {
    margin: 0 auto;
    width: 1024px;
    clear: both;
}

#header-repeat {
	background-color: #fff;
	background-image: none;
}
#header {
	background-color: #fff;
	background-image: none;
    padding: 10px;
    position: static;
}
#header > a {
	display: block;
    float: left;
    height: 80px;
    overflow: hidden;
    width: 200px;
}
#bg-top-left, #bg-top-right, #bg-top-repeat {
	display: none;
}
#main {
	background-image: none;
}
.main-shadow {
	display: none;
}
#main-menu {
    background-color: transparent;
	background-image: none;
	padding: 0 0 0 0;
}
#main-menu .nav {
	align-items: stretch;
	display: flex;
	justify-content: center;
    flex-direction: row;
    justify-content: space-between;
    height: auto;
    margin: 0 0 0 0;
    overflow: hidden;
}
.nav>li {
    background-color: #fff;
	background-image: none;
    height: auto;
	width: 100%;
}

.nav > li > a {
    color: #000;
    font-size: 14px;
    text-transform: none;
    
    height: auto;
    display: block;
    padding: 10px 5px;
	text-align: center;
	width: auto;
}
.nav > li > a > br {
	display: none;
}
.nav>li.first {
    background-color: #fff;
}
.nav>li.current {
	background-color: #53254c;
}
.nav>li:hover {
	background-color: #53254c;
}
.nav > li.current > a {
	background-color: #53254c;
	background-image: none;
}
#nav-repeat {
	background-color: #fff;
	background-image: none;
	display: none;
}
#inner {
	background-color: #fff;
	background-image: none;
}
#content-inner .button-nav-tabs {
	margin: 0 0 10px 0;
}
#inner .button-1 {
    background-color: #4caf50;
	background-image: none;
    border-radius: 2px;
    color: #fff;
	display: inline-block;
	float: none;
	margin: 0 0 0 0;
    padding: 10px 15px 10px 15px;
    position: static;
    text-align: center;
}
.button-h1 {
	margin: 0 0 10px 0;
	position: static;
}
#inner .button-1:hover {
	background-color: #00796b;
}
#content .green-button {
    background-color: #4caf50;
	background-image: none;
    border-radius: 2px;
    color: #fff;
	display: inline-block;
	float: none;
	margin: 0 0 0 0;
    padding: 10px 15px 10px 15px;	
}
#content .green-button:hover {
	background-color: #00796b;
}
.button-nav-tabs {
	margin: 0 0 10px 0;
	overflow: hidden;
	position: static;
	width: 100%;
}
#content-inner .button-nav-tabs, #content-inner .tab-in-content, #content-inner .inner-wrap {
	margin: 0 0 0 0;
}
#content ul.nav-tabs-2 li a:hover {
	background-image: none;
}
#inner .button-1:hover {
    background-color: #53254c;
}
.tab-in-content {
	background-image: none;
}
.tab-in-content .tab-pane {
	padding: 10px 0;
}
#content h1 {
	background-image: none;
	color: #53254c;
	line-height: 1;
	margin: 10px 0 10px 0;
	padding: 0 0 0 0;
}
#content ul.nav-tabs {
	width: 100%;
}
#content ul.nav-tabs  li a {
	border-radius: 2px;
}
#content ul.nav-tabs-2 li.active {
	background-image: none;
}
#content ul.nav-tabs-2 {
	margin: 0 0 10px 0;
}
#h1-icon {
	display: none;
}
.breadcrumb {
	display: none;
}
#content {
	margin: 0 0 0 0;
	width: 66%;
}
#inner-column {
	margin: 0 0 0 0;
	width: 33%;
}
#content-inner {
	margin: 0;
}


#content-inner > p {
    margin: 0 0 1rem 0;
}
#content ul.nav-tabs-2 li a {
	background-color: #9adc3a;
	background-image: none;
	color: #222;
	line-height: 1;
    padding: 4px 12px 4px 12px;
}
#content ul.nav-tabs-2 li a span {
	background-image: none;
}
#content ul.nav-tabs-2 li a {
	height: auto;
	margin: 0;
}
#content ul.nav-tabs-2 li.active a {
	background-color: #53254c;
	background-image: none;
	color: #fff;
	height: auto;
	line-height: 1;
    margin-right: 0;
    margin-left: 0;
    padding: 4px 12px 4px 12px;
}
.site-gallery .site-gallery-rating {
	background-color: #f44336;
	background-image: none;
	border-radius: 15px;
    padding: 0 0 0 0;
    line-height: 30px;
    right: 25px;
}
.site-gallery .site-gallery-comment {
	background-color: #00bcd4;
	background-image: none;
    border-radius: 15px;
    width: 30px;
    height: 30px;
    padding: 0 0 0 0;
    right: 25px;
    line-height: 30px;
}
.rating-company {
	display: none;
}
.add-company {
	float: none;
    margin: 0 0 10px 0;
}
.two-block p {
	display: none;
}
.tab-in-content-2 .site-gallery p.logo-company span a {
    height: 18px;
    line-height: 18px;
    display: block;
}
.wrapper > footer {
	background-image: none;
	background-color: #06191f;
	height: auto;
    overflow: hidden;
}
.tab-in-content-2 .site-gallery p img {
	border-radius: 50%;
    margin: 0 5px 0 0;
}
#bg-footer-left {
	display: none;
}
#bg-before-footer-left {
	display: none;
}
#bg-before-footer-right {
	display: none;
}
#bg-before-footer-repeat {
	display: none;
}
#bg-footer-right {
	display: none;
}
#bg-footer-repeat {
	display: none;
}
#logo-footer {
	display: none;
}
#main-menu .menu-hop {
	background-color: #53254c;
	background-image: none;
}
#main-menu .menu-hop {
	left: 0;
	width: 100%;
}
.main-menu-2 {
	background-color: #391b48;
	background-image: none;
}
.table-black {
	width: 100%;
}
.table-black thead th {
	background-color: #53254c;
	background-image: none;
}
.table-black thead th:first-child {
	border-radius: 0;
}
.table-black thead th:last-child {
	border-radius: 0;
}
#content .sort-group-label-up {
	overflow: hidden;
    margin: 0 0 10px 0;
    padding: 10px 0;
}

#content .form-parent, #header .form-parent {
	border: none;
	border-radius: 2px;
	margin: 0 0 10px 0;
}

#header-logo {
	background-repeat: none;
    background-size: contain;
	position: static;
    height: 80px;
    width: 200px;
}
.list-question-item {
	background-color: #fff;
	border-radius: 0;
}
.list-question-item h3 a {
	text-decoration: none !important;
}
.list-question-item p:last-child {
	margin: 0 0 0 0;
}
.tab-in-content .list-question-item a {
	font-weight: normal;
}
.tab-in-content .list-question-item a:hover {
	color: #53254c;
}
.tab-in-content .list-question-item h3 {
	font-weight: normal;
    line-height: 1;
	margin: 0 0 10px 0;
}
.list-question-author {
	background-image: none;
}
.records-list-author img {
	border-radius: 50%;
}
.tab-in-content .list-question-author {
	padding: 10px 0 0 0;
}
.list-question-author img {
	border-radius: 50%;
    display: block;
    float: left;
}
.list-question-author a {
	display: block;
    float: left;
	text-decoration: none !important;
}
.list-question-author a:hover {
	color: #53254c;
}
.list-question-author time {
	display: block;
    /*float: left;*/
}
.author-div br {
	/*display: none;*/
}
#auth-panel {
	position: static;
    float: right;
    padding: 30px 0;
    height: 80px;
}
#auth-panel a {
	font-size: 16px;
	text-transform: none;
}
.i-login {
	background-image: none;
}
#auth-panel li>a:hover, #auth-panel .link-social:hover {
	background-image: none;
    color: #53254c;
}
.i-div {
	background-image: none;
}
.orange {
	font-weight: normal;
}
#bg-footer-repeat {
	background-color: #06191f;
	background-image: none;
	/*height: auto;*/
}
.bl1 {
    margin: 0 0 10px 0;
    padding: 0 10px;
    width: 50%;
}
.bl2 h2 {
	height: auto;
    margin: 0 0 10px 0;
    padding: 0;
    text-align: center;
}
.bl2 h2.work {
    background-image: none;
    color: #2e1243;
}
.bl2 h2.qa {
    background-image: none;
    color: #2e1243;
}
.bl1 h2 {
	background-image: none;
    color: #2e1243;
    text-align: center;
}
.bl1 h2 img {
	display: none;
}
@media only screen and (max-width: 960px) {
	header {
		width: 100%;
	}
	.wrapper {
		/*padding: 0 10px;*/
        padding: 0 0;
		width: 100%;
	}
	#content {
		margin: 0 0 0 0;
		width: 100%;
	}
	#inner-column {
		margin: 0 0 0 0;
		width: 100%;
	}
	#main-menu .nav {
		display: block;
	}
	.nav>li {
		float: none;
	}
    .records-list article {
    	width: 100%;
    }
    #inner {
    	padding: 0 10px;
    }
    .bl1 {
        margin: 0 0 10px 0;
        padding: 0 10px;
    	width: 100%;
    }
    .bl2 h2 {
    	margin: 0 0 10px 0;
        padding: 0;
        text-align: center;
    }
    .bl2 h2.work {
    	background-image: none;
        color: #f00;
    }
}

@media only screen and (max-width: 600px) {
	#header {
		height: auto;
	}
	#header > a {
    	display: block;
    	float: none;
    	height: 80px;
    	overflow: hidden;
    	width: 100%;
	}
    #header-logo {
    	margin: 0 auto;
    }
    #auth-panel {
    	float: none;
        height: auto;
        padding: 10px 0;
        text-align: center;
    }
    #auth-panel ul {
    	margin: 0;
        padding: 0;
    }
    .i-login {
    	padding: 0;
    }
}
}
