css:
@-moz-document domain("www.anbient.com") {
/*
    * Nome:....Anbient - Dark Green Theme
    * Autor:...VivianoTRZ
    * Versão:..v1.0.1

    * Início:..............27/04/2017
    * Publicação:..........28/04/2017
    * Ultima atualização:..28/04/2017
*/
/* BACKGROUND */

main#panel {
    box-shadow: #333 2px 6px 10px;
    background: #1d1d1d !important;
}
/* CABEÇALHO /// HEADER */

#main-prefix {
    background: #124a00 !important;
}
/*titulo*/

.page-anime .panel-pane .pane-title,
#downloads>h2>div {
    font-size: 28px;
    color: #72c557 !important;
    text-transform: uppercase;
}
/* TITULO /// TITLE */
/*background*/

.green,
#animes-com-termo .view-filters {
    background: #16310d !important;
}
/*texto dos titulos*/

.anime-full .titulos-alt {
    color: #72c557 !important;
    font-size: 16px;
    font-weight: bold;
}
/*texto das tags*/

.node-anime .field-generos a {
    color: #72c557 !important;
    border-radius: 24px;
    border: 1px solid #72c557 !important;
    font-weight: bold;
    font-size: 11px;
    padding: 5px 10px;
    text-transform: uppercase;
}
/*bg das info*/

.anime-full .info-right {
    background: #124a00 !important;
    display: block;
    padding: 20px;
    position: relative;
    margin: 20px 0 20px 278px;
    min-height: 112px;
}
/*nota*/

#rating,
.node-anime-search .field-nota {
    position: absolute;
    right: 0;
    top: 0;
    text-align: center;
    color: #71c557;
    font-size: 12px;
    text-transform: uppercase;
    vertical-align: middle;
    background: #243f1c;
    padding: 8px;
    font-weight: bold;
    cursor: default;
}
/* DOWNLOADS */
/*opções de downloads*/

.dl-tabs .dl-tab a.padrao {
    padding: 6px 22px;
    background: #124a00;
    width: 120px;
}
/*text*/

.dl-tabs li>span {
    color: #72c557 !important;
}
.download .field-label {
    color: #72c557 !important;
}
.download .field-items,
.download .item,
.download .inject path {
    color: #fff !important;
    fill: #fff !important;
}
.field-atributos .field-item:after {
    color: #fff !important;
}
.download a {
    color: #fff !important;
}
/*texto "Softsub"*/

.download .field-atributos .field-item {
    color: #72c557 !important;
}
/*bg dos episódios*/

.servidor li a {
    background: #1d1d1d !important;
}
/*bg downloads*/

.servidor {
    background: #124a00 !important;
}
/*texto colabore*/

.vantagens li {
    display: block;
    color: #eee !important;
    font-weight: normal;
}
/*texto "Ajude com apenas R$ 10,00."*/

.vantagens small {
    font-size: 18px;
    display: block;
    margin-top: 8px;
    color: #eee !important;
    font-weight: bold;
}
/*servidor free*/

.servidor .servidorfree {
    background: #16310d;
    font-size: 16px;
    color: #fff;
    padding: 5px 10px;
    margin: 10px 30px 25px 30px;
}
/*texto "Seja um colaborador e ajude-nos a manter o site!"*/

.button,
form .form-submit,
form .register,
#revisioning-revisions-summary .submenu a {
    padding: 8px 18px;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 13px;
    display: inline-block;
    background: #16310d;
    color: #FFF;
}
/*
	/*icone check//
	.vantagens li.icon:after {
    	float: left;
    	line-height: inherit;
    	margin-right: 10px;
    	color: #72c557;
	}
	*/
/* LISTA PLANA */
/*texto*/

button,
input,
optgroup,
select,
textarea {
    color: #fff;
    /* font: inherit; */
    
    margin: 0;
}
/*bg contorno*/

.mfp {
    background: #16310d;
}
/*bg*/

textarea {
    font-family: monospace;
    border-color: #72c557 !important;
}
textarea {
    -webkit-appearance: textarea;
    background-color: #1d1d1d !important;
}
/* RODAPÉ /// FOOTER */
/*background*/

#footer {
    background: #16310d !important;
}
/*text*/

#footer .block-views a {
    color: #72c557 !important;
}
/*title*/

#footer .block-views h2 {
    font-weight: normal;
    font-size: 20px;
    color: #fff !important;
    text-transform: uppercase;
}
/* SELEÇÃO */

::selection {
    background: #72c557 !important;
    color: #000 !important;
    text-shadow: none;
}
}

@-moz-document domain("www.anbient.com") {
    /* Scrollbar Chrome*/
    
    *::-webkit-scrollbar {
        width: 10px !important;
        height: 10px !important;
        background: #1e1e1e !important;
        border: 1px solid #252525 !important;
    }
    *::-webkit-scrollbar-button {
        display: none !important;
    }
    *::-webkit-scrollbar-thumb,
    *::-webkit-scrollbar-track {
        border: 0 !important;
        box-shadow: none !important;
    }
    *::-webkit-scrollbar-thumb {
        min-height: 28px !important;
        background: #72c557 !important;
    }
    *::-webkit-scrollbar-corner,
    *::-webkit-scrollbar-track {
        background: #1e1e1e !important;
    }
    /*	Créditos:  Nass O	*/
}

@-moz-document domain("www.anbient.com") {
/* |HOME| www.anbient.com */
/* LANÇAMENTOS */

h2 {
    color: #72c557 !important;
}
/*texto do lançamento*/

.lancamento.node-sticky .content {
    padding-top: 10px;
    background: #16310d !important;
    border: 0;
    height: auto;
    color: #fff !important;
}
/*bg lançamento*/

.lancamento.node-sticky {
    background: #16310d !important;
}
.lancamento img {
    display: block;
    width: 100%;
    padding-top: 10px;
    padding-left: 10px;
    padding-right: 10px;
    padding-bottom: 10px;
}
/* |LEIA MAIS| www.anbient.com/lancamento/ */
/*texto*/

p {
    color: #fff !important;
}
/*texto da data*/

time {
    color: #fff;
}
/*texto "por"*/

address.author {
    color: #fff;
}
/*rotulo*/

span.field-label {
    color: #fff;
}
/*item do rotulo*/

.item {
    color: #fff;
}
/* |USER - EDITAR| */
/*texto rotulo*/

form label {
    font-size: 16px;
    color: #72c557;
    display: block;
    margin-bottom: 3px;
}
/*texto descrição*/

.description {
    color: #aaa;
}
/*input*/

input#edit-name {
    color: #000;
}
input#edit-current-pass {
    color: #000;
}
input#edit-mail {
    color: #000;
}
input#edit-pass-pass1 {
    color: #000;
}
input#edit-pass-pass2 {
    color: #000;
}
select#edit-timezone--2 {
    color: #000;
}
/*textos senhas*/

.password-strength-title {
    color: #ccc;
}
.password-confirm {
    color: #ccc;
}
.password-strength-text {
    color: green;
}
/*titulo das caixas*/

span.fieldset-legend {
    color: #72c557;
}
/*texto "Se você tiver um Gravatar associado..."*/

div#edit-gravatar {
    color: #bbb;
}
/*consertar o espaçamento na caixa Usuario*/

input[name="name"],
input[name="pass"] {
    padding-left: 5px;
}
/* |BUSCAR| */
/*subtitulo*/

.node-anime-search .field-titulos-alt {
    color: #71c557;
    margin-right: 20px;
    margin-bottom: 20px;
}
/*infos*/

.field.inline .field-label,
.field.inline .field-item,
.field.inline .item {
    display: inline;
    margin-right: 8px;
    color: #ccc;
}
}
