/* CODE SPESIFIC TO FACTORIO PLACED HERE */
  /* For the ContentCell Template */
  .factorio-contentcell {
    background: #333 !important;
  }
  .factorio-contentcell .titlebar {
   background: #3d3d3d;
   color: #c1c1c1 !important;
   font-size: 120%;
  }
  
  /* Languages container on the main page */
  .languages-container{
    background: #333;
  }
  
  /* Search Widget on the top of the page */

  div#simpleSearch input{
    background-color: #333 !important;
  }
  #simpleSearch{
    background-color: #333 !important;
  }
  #searchInput{
     color: #ffffff !important;
  }
  
  /* Search page */
  .mw-search-profile-tabs{
     background: #333 !important;
  }
  .search-types .current a{
     color: #c1c1c1 !important;
  }
  .oo-ui-textInputWidget input, .oo-ui-textInputWidget textarea {
    background-color: #333 !important;
    color: #c1c1c1 !important;
    box-shadow: inset 0 0 0 0.1em #C0C0C0 !important;    
  }
  .oo-ui-widget.oo-ui-widget-enabled input:focus{
    border-color: #cb662e;
  }
  .oo-ui-menuSelectWidget {
    background-color: #333;
    border: 1px solid #a2a9b1;
    border-radius: 0 0 2px 2px;
    box-shadow: 0 0.15em 0 0 rgba(0,0,0,0.15);
  }
  .oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button {
    color: #333;
    background-color: #cb662e;
    border-color: #cb662e;
  }

  /* --- Navbox --- */
  .navbox th {
   background: #4c4c4c;
   border-radius: 10px;
  }

  .navbox .navbox-header {
    background: #4c4c4c !important;
    color: #c1c1c1 !important;
  }
  .navbox .list-data {
   background: #3a3939 !important;
  }

  .navbox .group-data {
   background: #1c1c1c !important;
  }

  /* Boilerplate */
  .boilerplate{
    background-color: #1c1c1c !important;     
  }
  
 /* --- Infobox --- */
 .infobox .combat {
  background: #611616;
   color: #c1c1c1;
 }

 .infobox .liquids {
  background: #326177;
   color: #c1c1c1;
 }

 .infobox .logic {
  background: #3b155e;
   color: #c1c1c1;
 }

 .infobox .intermediates {
  background: #525252;
   color: #c1c1c1;
 }

 .infobox .machinery {
  background: #9c6500;
   color: #c1c1c1;
 }

 .infobox .resources {
  background: #221f1f;
