 }

  /* Disambiguation Template */
  .factoriodisambig{
    background: #333 !important;
  }
  .factoriodisambig i{
    color: #c1c1c1 !important;
  }
  /* factorio cleanup box */
  .factoriocleanup{
    background: #1c1c1c !important;
  }
  /* Delete Box */
  .factoriodelete{
    background: #1c1c1c !important
  }
  /* doc template */
  .template-documentation{
    background: #1c1c1c !important;
  }
  /* Game Image */
  #license{
    background: #1c1c1c !important;
  }
  /* Template:Key */
  .keyboard-key{
    background-color: #1c1c1c !important;
  }
  /* Template Merge */
  .factoriomerge{
    background-color: #1c1c1c !important;   
  }
  /* Template Move */
  #move{
    background-color: #1c1c1c !important;  
  }
  /* Template Shortcut */
  .shortcutbox{
    background-color: #1c1c1c !important;   
  }
  /* Spoilers */
  .spoiler-container .button{
    background: #3a3939 !important;
  }
  /* Preview Note */
  .previewnote{
    color: #caaee8 !important;
  }
  /* Code View */
  pre, .mw-code{
    color: #ffffff;
    background-color: #333 !important;
    border: 1px solid #ddd;
  }
  /* DataTable Fix */
  .mw-datatable th{
    background-color: #4c4c4c !important;
  }
  .mw-datatable td{
    background-color: #3a3939 !important;
  }
  .mw-datatable tr:hover td{
    background-color: #4c4c4c !important;    
  }
  
  /* File List Overide */
  /* Active first */
  .TablePager_nav td.TablePager_nav-first .TablePager_nav-enabled{
    background-image: url(https://wiki.factorio.com/resources/dark/active-first.svg);
  }
  
  /* Active Previous */
  .TablePager_nav td.TablePager_nav-prev .TablePager_nav-enabled{
    background-image: url(https://wiki.factorio.com/resources/dark/active-prev.svg);
  }
  
  /* Active Next */
  .TablePager_nav td.TablePager_nav-next .TablePager_nav-enabled{
    background-image: url(https://wiki.factorio.com/resources/dark/active-next.svg);
  }
  
  /* Active Last */
  .TablePager_nav td.TablePager_nav-last .TablePager_nav-enabled{
    background-image: url(https://wiki.factorio.com/resources/dark/active-last.svg);    
  }
  
  /* First */
  .TablePager_nav td.TablePager_nav-first .TablePager_nav-disabled{
   background-image: url(https://wiki.factorio.com/resources/dark/first.svg);
  }
  
  /* Previous */
  .TablePager_nav td.TablePager_nav-prev .TablePager_nav-disabled{
    background-image: url(https://wiki.factorio.com/resources/dark/prev.svg);
  }
  
  /* Next */
  .TablePager_nav td.TablePager_nav-next .TablePager_nav-disabled{
    background-image: url(https://wiki.factorio.com/resources/dark/next.svg);
  }
  
  /* Last */
  .TablePager_nav td.TablePager_nav-last .TablePager_nav-disabled{
    background-image: url(https://wiki.factorio.com/resources/dark/last.svg);
  }
  
  /* up small */
  .TablePager .TablePager_sort-ascending a{
    background-image: url(https://wiki.factorio.com/resources/dark/s-up.svg);
  }

  /* down small */
  .TablePager .TablePager_sort-descending a{
    background-image: url(https://wiki.factorio.com/resources/dark/s-down.svg);
  }
  
  /* Tabber Navigation */
    ul.tabbernav li a {
   background:  #595959;
  }
  ul.tabbernav li a:link {
   color: #e69710;
  }
  ul.tabbernav li.tabberactive a {
