css:
@-moz-document
  domain("skythewood.blogspot.sg"),
  domain("skythewood.blogspot.com"),
  domain("skythewood.blogspot.de")
{
.navbar {display:none}
.header-inner a img {display:none!important}
.descriptionwrapper {margin-bottom:10px!important}
.descriptionwrapper .description {margin:0}
.tabs-inner .widget ul#icbabdrop {padding: 5px 4px 7px 0!important}
.tabs-inner .widget ul#icbabdrop li {padding:5px 20px!important; background: #555;color: #b9b9b9}
.tabs-inner .widget ul#icbabdrop li:hover {background: #6d6c6c;color: #fff}
.tabs-inner .widget li a {color: #b9b9b9}
.date-header span {background-color:#464444}

body {min-width: 600px;font-family: 'Open Sans',sans-serif!important;color:#b9b9b9;background:#272525!important}
.Header .description {color:#b9b9b9}
.content-inner {background-color: rgb(46, 45, 45)}
.content-outer, .content-fauxcolumn-outer, .region-inner {max-width: 950px!important;min-width:inherit!important}
@media screen and (max-width: 1000px) {
   .content-outer, .content-fauxcolumn-outer, .region-inner {max-width: none!important;min-width:500px!important}
}
@media screen and (max-width: 700px) {
    .content-inner {padding:10px 20px!important}
}

span {background-color:transparent!important}
.main-inner {padding-bottom: 0!important;padding-top: 15px!important}
.main-inner .column-right-outer {display:none!important}
.main-inner .fauxcolumn-right-outer {display:none!important}
.main-inner .columns {padding:0!important}

.main-inner .column-center-inner .section {margin:0}
.post {margin-bottom:25px}
.post-body div {line-height:1.3!important;margin-top: -4pt}
.post-body span {font-family: 'Open Sans',sans-serif!important;color:rgb(255, 255, 255)!important}
.post-body img {max-width:100!important;height:auto!important;max-height:700px!important}
b + br {display:none}
b > span {font-size:16px}
span > b {font-size:16px}

/* comments iframe */
#dsq-app1 {display:none!important}
.post-footer {display:none!important}
.footer-outer {display:none!important}
.blog-pager {display:none!important}
.post-feeds {display:none!important}
}
