    border: 1px solid #66d!important;
    background: #F4F4FF!important;
  }
  .note-body {
    color: #DDD!important;
    background: #333!important;
    background-color: #333!important;
  }
  .alert-info {
    background-color: #333!important;
    color: #DDD!important;
    border-width: 1px;
  }
  .alert-info a {
    color: #EEE!important;
  }
  .contain-push{
    color: #DDD!important;
  }
  div div[style][id]{
    color: #DDD!important;
    background-color: #333!important;
    border-color: #000!important;
  }
  div div[style][id] a{
    color: #4fc3f7!important;
  }
  .ui-menu-item{
    color: #DDD!important;
    background-color: #333!important;
  }
  .comment-box, #comment, .tagBox, #source, #title{
    color: #DDD!important;
    background-color: #333!important;
    border-color: #000!important;
  }
  /* 2021 update */
  footer {
    background: #333!important;
  }
  footer *{
    color: #DDD!important;
    line-height: 0px!important;
    opacity: 0.7
  }
  .aside{
    color: #DDD!important;
    background-color: #333!important;
    border-right-color: #000!important;
    border-bottom-color: #000!important;
    margin: 0px 1px 0px 0px!important;
  }
  /* re-align search area and color it */
  .searchArea {
    margin: 0px!important;
    height: 52px!important;
    padding: 10px!important;
    background-color: #333!important;
  }
  /* favorite heart */
  .searchArea div form a img {
    filter: invert(40%)!important
  }
  /* fade thumbnails */
  @keyframes fadeInAnimation { 
    0% { 
      opacity: 0; 
    } 
    100% { 
      opacity: 1; 
    }
  } 
  .thumbnail-container img {
    animation: fadeInAnimation ease 1s;
    animation-iteration-count: 1; 
    animation-fill-mode: forwards; 
    transition: opacity 0.5s ease;
  }
  .thumbnail-container img:hover {
    opacity: 0.9!important;
    transition: opacity 0s ease;
  }

/* style the new image page */
.mainBodyPadding {
    color: #AAA!important;
}
.mainBodyPadding a {
    color: #4fc3f7!important;
}
/* make profile pics darker */
.comments-user-thumbnail {
    opacity: 0.7;
}
}

