css:
@-moz-document domain("vk.com") {
.vkmd_download_btn{
    background:transparent;
}
.vkmd_download_btn:before{
    background:#383838 !important;
    color:#b9b384;
    border:0;
    transition:all .2s ease;
    transform:scale(.7);
}
.vkmd_download_btn:hover:before{
    transform:scale(1)
}
.vkmd_download_all_btn{
    color:white;
}
.vkmd_audio_stats{
	color:white;
    opacity:.6;
}
}
