css:
@-moz-document domain("joyreactor.cc"), domain("reactor.cc"), domain("joyreactor.com") {
html {
    background-color: #333333;
}
body {
    background-color: #333333;
}
div#container {
    background-color: #333333;
    background-image: none;
}
div#container:before {
    background-color: #333333;
    background-image: none;
}
div#container:after {
    background-color: #333333;
    background-image: none;
}
div#topbar {
    background: none;
    background-color: #333333;
    color: #aaaaaa;
}
div#topbar a {
    color: #aaaaaa;
    background-color: #333333;
}
div#navcontainer:after {
    background: #333333;
}
#navcontainer ul li a {
    color: #aaaaaa;
}
div#header {
    visibility: hidden;
    width: 0px;
    height: 0px;
}
div#searchBar {
    background-color: #aaaaaa;
}
a.top_logo {
    background: none;
    text-transform: uppercase;
}
a.commentnum {
    background: #aaaaaa;
    border: 1px solid #333333;
}
.article .ufoot span.comments a:before {
    background: none;
    width: 0px;
}
div.post_random a {
    background: #333333;
    color: #aaaaaa;
}
div.post_random a:after {
    background: none;
}
div#navcontainer ul li.current_page_item a {
    color: #00aa00;
    background: #333333;
}
div#navcontainer ul li.current_page_item a:after {
    background: #00aa00;
}
div#submenu div.submenuitem.active a {
    background: #aaaaaa;
    border: 1px solid #333333;
    color: #333333;
}
div#searchmenu {
    border: 1px solid #00aa00;
}
div.sswither {
    border: 1px solid #00aa00;
}
div.sswither span {
    background: #00aa00;
}
a.big_button {
    background: #aaaaaa;
    border: 1px solid #333333;
    color: #333333;
}
div.progress_bar div {
    background-color: #aaaaaa;
}
input[type="submit"] {
    background: #aaaaaa;
    border: 1px solid #333333;
    color: #333333
}
div#sidebar div.tabs_titles ul li.active a {
    background: #aaaaaa;
    color: #333333;
}
div#sidebar div.tabs_titles ul li.active a:hover {
    background: #f0f0f0;
    color: #303030;
}
div#sidebar div.tabs_titles ul li a:hover {
    background: #aaaaaa;
    color: #333333;
}
.pagination .current,
.pagination .next,
.pagination .prev {
    background: #aaaaaa;
    border: 1px solid #333333;
    color: #333333;
}
.pagination .prev:before,
.pagination .next:after {
    background: none;
    width: 0px;
}
.pagination a:hover {
    background: #f0f0f0;
    color: #303030;
}
p.login_link a {
    background: #aaaaaa;
    border: 1px solid #333333;
    color: #333333;
}
div#blogHeader .add_to_fav a {
    background: #aaaaaa;
    border: 1px solid #333333;
    color: #333333;
}
}
