css:
@-moz-document domain("www.listenvideo.com") {
.middle-view {
	background-color: #fafafa;
}
#artist-page .artist-header > .header-overlay {
	background: linear-gradient(to bottom, transparent -80%, white 100%);
	background-color: white;
}
#artist-page .artist-header .artist-info > h1 {
	color: #202020;
}
#artist-page .artist-header .artist-info > .genres > .genre {
	color: rgba(0, 0, 0, 0.54)
}
#artist-page {
	background-color: #fafafa;
}
.app-container {
	color: #2d2d2d
}
.filter-bar {
	padding: 20px 75px 5px;
	margin: -25px -25px 30px;
	border-bottom:1px solid #e0e0e0 !important;
}
#player-controls {
	z-index: 50;
}
#player-controls .volume-container > .volume-bar,
#player-controls .volume-container > .volume-bar > .volume-level {
	height: 2px;
}
#player-controls > .buttons > .toggle-play:hover,
button > .icon {
	/*color=white*/
	
	color: white
}
#player-controls > .buttons > .toggle-play,
#player-controls > .buttons > .track-loading {
	background: #e62117;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	text-align: center;
	line-height: 20px;
	vertical-align: middle;
	padding: 10px;
	padding-top: 7px;
	padding-left: 1px;
	margin: 0 10px;
	font-size: 2rem;
	color: white
}
#player-controls > .buttons > i:hover,
#player-controls .lyrics:hover,
#player-controls > .buttons > i {
	color: black;
}
#player-controls .volume-container > .volume-bar > .volume-handle {
	top: -4px
}
.filter-bar > .sort {
	padding-top: 12px
}
.filter-bar > .filter,
#left-panel .search-bar {
	/*google input bar google input field*/
	
	color: #212121;
	height: 45px;
	flex: 1 1 15%;
	background-color: #f5f5f5;
}
.progress-bar {
	margin-bottom: 46px;
	z-index: 100
}
.progress-bar > .elapsed,
.progress-bar>.progress {
	height: 4px
}
.progress-bar > .progress,
#player-controls .volume-container > .volume-bar > .volume-level,
.progress-bar > .handle,
#player-controls .volume-container > .volume-bar > .volume-handle {
	/*background-color=red*/
	
	background-color: #e62117
}
.current-user-widget .user-email,
#album-page .album-header > .info .artist-name {
	/*color=red*/
	
	color: #e62117
}
#player-controls .volume-container > .volume-bar {
	background-color: #bdbdbd
}
.progress-bar > .handle:active {
	width: 20px;
	height: 20px;
	top: 11px;
	right: 7px;
	transition: width .18s ease, height .18s ease, top .18s ease, right .18s ease;
}
.filter-bar > .filter > input {
	background-color: #f5f5f5;
	padding-left: 40px
}
#left-panel,
.progress-bar > .progress,
#player-controls,
.flex-table-header,
#artist-page .navigation,.filter-bar {
	/*bgcolor=white*/
	
	background-color: white;
}
#context-menu,
.flex-table,
#left-panel,
#player-controls,
.search-panel > .view-all,
.search-panel > .result-container > .track,
.search-panel > .result-container > .media {
	/*google card shadow*/
	/*google card normal shadow*/
	
	box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.37);
	transition: box-shadow 0.28s cubic-bezier(0.4, 0, 0.2, 1);
	background-color: white;
	outline: none;
}
.search-panel > .result-container > .media > .media-body > .media-text,
.search-panel > .result-container > .track > .track-name {
	color: black
}
.search-panel > .result-container > .media > .media-body .media-subtext {
	color: #898B8C;
}
h2 {
	text-transform: uppercase;
	text-align: center;
}
#context-menu,
#left-panel,
.search-panel > .view-all:hover,
.filter-bar > .filter > input:focus,
#left-panel .search-bar > input:focus,
.search-panel > .result-container > .track:hover,
.search-panel > .result-container > .media:hover {
	/*google card strong shadow*/
	
	transition: box-shadow 0.28s cubic-bezier(0.4, 0, 0.2, 1);
	box-shadow: 0 2px 7px 0 rgba(0, 0, 0, 0.37);
	background-color: white;
	outline: none;
}
#player-controls {
	/*google card strong up shadow strong shadow up*/
	
	box-shadow: 0 -1px 7px 0 rgba(0, 0, 0, 0.37);
}
.search-panel {
	background-color: #e5e5e5;
}
#artist-page .artist-header {
	min-height: 200px;
}
#artist-page .navigation {
	padding: 25px 40px 15px;
	margin: inherit;
}
.progress-bar > .progress {
	/*display=none*/
	
	visibility: hidden;
}
.progress-bar:hover > .progress {
	visibility: visible;
	background-color: #e6e6e6;
}
div,
section,
span {
	border-width: 0px !important
}
.flex-table {
	padding: 20px 40px 20px;
	margin: 0 50px;
}
* {
	color: #6d6d6d;
}
.album-table .playing,
#left-panel .main-menu .menu-item.active,
#left-panel .your-music .menu-item.active,
#left-panel .playlists .menu-item.active,
#left-panel .main-menu .menu-item:hover,
#left-panel .your-music .menu-item:hover,
#left-panel .playlists .menu-item:hover,
.flex-table-header:hover,
.flex-table-row:hover,
.progress-bar > .progress:hover {
	background-color: #f5f5f5 !important;
}
img[alt='logo'] {
	content: url("https://upload.wikimedia.org/wikipedia/commons/e/ef/YouTube_logo_2015.svg");
	height: 50%!important;
	width: 50%!important;
	margin-left: 25px;
}
.logo:after {
	content: ' Play';
}
#left-panel .main-menu,
#left-panel .your-music,
#left-panel .playlists {
	font-weight: normal;
}
.divider,
#left-panel .content .divider {
	background-color: #dedede;
	width: 100%
}
#left-panel .new-playlist,
div {
	border-top-width: 0px;
	border-bottom-width: 0px;
}
.filter-bar > .filter > .icon {
	top: 13px;
	left: 15px
}
}
