css:
@-moz-document domain("www.dsrpg.uk"), domain("dsrpg.uk") {
/*http://www.colorzilla.com/gradient-editor/#000000+0,052649+73&0.75+0,0.75+100*/

body {
    background: #050a0d url("https://i.imgur.com/cnX7l3H.jpg") top center no-repeat fixed;
    background-size: 100%;
    color: #11b4f4;
    overflow-x: hidden;
}

body > div.container {
    width: 100%;
    max-width: 99%;
    background: rgba(0,0,0, 0.5);
}

div.container > div.row {
    margin: 10px 5px;
}

a, a > u { text-decoration: none !important; }
a:link, a:visited { color: #78c0d8; }
font[color="darkorange"] { color: #78c0d8; }
font[color="red"] { color: #ff4444; }

#chatdisplay { border: 1px solid #496a8d; border-bottom: 0; background: rgba(0,0,0,0.75); }

div.addBorder, div.panel, div.panel-body, div.modal-content, div.modal-body, .btn, input[type="button"], input[type="submit"], button[type="submit"], .well {
    color: #11b4f4 !important;
    border-color: #496a8d !important;
    background-color: rgba(0,0,0,0) !important;
background: -moz-linear-gradient(top,  rgba(5,38,73,0.75) 0%, rgba(5,38,73,0.75) 27%, rgba(0,0,0,0.75) 100%);
background: -webkit-linear-gradient(top,  rgba(5,38,73,0.75) 0%,rgba(5,38,73,0.75) 27%,rgba(0,0,0,0.75) 100%);
background: linear-gradient(to bottom,  rgba(5,38,73,0.75) 0%,rgba(5,38,73,0.75) 27%,rgba(0,0,0,0.75) 100%);
}

input[type="text"], input[type="password"], select, textarea, input.form-control {
    color: #11b4f4 !important;
    border-color: #496a8d !important;
    background-color: rgba(0,0,0,0) !important;
    background: -moz-linear-gradient(top,  rgba(0,0,0,0.75) 0%, rgba(5,38,73,0.75) 73%, rgba(5,38,73,0.75) 100%);
background: -webkit-linear-gradient(top,  rgba(0,0,0,0.75) 0%,rgba(5,38,73,0.75) 73%,rgba(5,38,73,0.75) 100%);
background: linear-gradient(to bottom,  rgba(0,0,0,0.75) 0%,rgba(5,38,73,0.75) 73%,rgba(5,38,73,0.75) 100%);

}
div.panel-heading, div.modal-header, div.panel-footer {
    color: #11b4f4 !important;
    border-color: #496a8d !important;
    text-align: center;
    font-size: 12px;
    padding: 5px 15px;
    font-weight: 700;
    background-color: rgba(0,0,0,0) !important;
    background: -moz-linear-gradient(top,  rgba(0,0,0,0.75) 0%, rgba(5,38,73,0.75) 73%, rgba(5,38,73,0.75) 100%);
background: -webkit-linear-gradient(top,  rgba(0,0,0,0.75) 0%,rgba(5,38,73,0.75) 73%,rgba(5,38,73,0.75) 100%);
background: linear-gradient(to bottom,  rgba(0,0,0,0.75) 0%,rgba(5,38,73,0.75) 73%,rgba(5,38,73,0.75) 100%);
}
select option { background: #000; }

table, table.forums, table.table td {
    border-color: #496a8d !important;
}
table > tbody > tr > td {
    padding: 2px 4px;
}

table[frame=box], table[frame=void] {
/*    border-color: #496a8d;
    border: 0;*/
    border-color: transparent;
}
table[frame=box] > tbody {
    border-top-color: #496a8d;
}
table[frame=box] > tbody > tr > td {
    padding: 2px 4px;
}
table[frame=box] > tbody:nth-child(odd), table.forums > tbody > tr:nth-child(odd), table.travelers_shop > tbody > tr:nth-child(odd), table.members_list > tbody > tr:nth-child(odd), table.table > tbody > tr:nth-child(odd) {
    background: rgba(0,0,0,0.50);
}
table[frame=box] > tbody:nth-child(even), table.forums > tbody > tr:nth-child(even), table.travelers_shop > tbody > tr:nth-child(even), table.members_list > tbody > tr:nth-child(even), table.table > tbody > tr:nth-child(even) {
    background: rgba(120,192,216,0.10);
}

.table .table { background: none; }
}

@-moz-document url("https://www.dsrpg.uk/members.php?action=view&id=1613"), url("https://dsrpg.uk/members.php?action=view&id=1613") {
img[src^="/images/"] {
    width: 0;
    height: 0;
    background: url(http://i.imgur.com/EFBxYDn.png) center center;
    background-size: cover;
    padding-left: 400px;
    padding-top: 225px;
    border-radius: 50%;
    opacity: 0.9;
    border: 1px solid #496a8d;
}
