css:
@-moz-document domain("0chan.hk") {

html body {
  background-color: #2e343e;
}
body {
  color: lightgray;
}
#content {
  background: #2e343e;
}
.headmenu {
  position: fixed;
  z-index: 99;
  background: #2e343e none repeat scroll 0 0;
  border-bottom: 1px solid #e63f97;
}
.headmenu-title {
  right: 100px;
  font-family: rage italic;
}
.headmenu-title span, .headmenu-title a {
  color: #e63f97 !important;
}
a {
  color: #e63f97;
}
a, span.a {
  color: #e63f97;
}
a:hover, a:focus {
  color: #e61197;
}
.fa-mail-reply::before, .fa-reply::before {
  color: #e63f97;
}
.fa-minus-square-o::before {
  color: #e63f97;
}
.fa-exclamation::before {
  color: #e63f97;
}
.fa-plus-square-o::before {
  color: #e63f97;
}
blockquote, .quote {
  color: #e66697;
}
.sidebar {
  z-index: 100;
}
.sidebar .sidemenu-boards-list-box .sidemenu-boards-list {
  overflow-x: hidden;
}
.sidebar .sidemenu-boards-search input:focus {
  border-color: #e63f97;
  box-shadow: 0 0 3px #e63f97;
}
.sidebar .sidemenu-boards-search.filled .search-clear:hover {
 color: #e63f97;
}
.btn-link {
  color: #e63f97;
}
.btn-link:hover, .btn-link:focus {
  color: #e61197;
}
.login-form input {
  border: 1px solid #2e343e;
  background-color: #3f424f;
}
.alerts-item {
  margin-top: 15%;
}
.threads-scroll-spy{
  display: none;
}
.thread-reply-chain {
  display: none;
}
.nav-pills > li.active > a, .nav-pills > li.active > a:hover, .nav-pills > li.active > a:focus {
    background-color: #e63f97;
}
.nav > li > a:hover, .nav > li > a:focus {
    background-color: rgba(204, 204, 204, 0.25);
}
.separator {
  border-top: 1px solid #e63f97;
  border-bottom: 1px solid #e63f97;
}
.block {
  background: #3f424f none repeat scroll 0 0;
  border: 1px solid #e63f97;
  border-radius: 2px;
  box-shadow: 0 1px 4px #e63f97;
}
.post .post-footer post-date {
  border-top: 1px solid #e63f97;
  color: #e63f97;
}
.post .post-header .post-date {
  color: #e63f97;
}
.post .post-header .post-thread-options .active {
  color: #e63f97;
}
.post .post-body .post-referenced-by, .post .post-body .post-replied-by {
  color: #e63f97;
}
.post-popup .post {
  box-shadow: 0 2px 6px #e63f97;
}
.post .post-footer {
  border-top: 1px solid #e63f97;
}
.reply-form .reply-form-message {
  background: #3f424f none repeat scroll 0 0;
  border: 1px solid #e63f97;
}
.reply-form .reply-form-message textarea {
  background-color: #3f424f;
}
.btn-default {
  background-color: #e63f97;
  border-color: #e63f97;
  color: #fff;
}
.btn-default:hover {
  background-color: #e61197;
  border-color: #e61197;
  color: #fff;
}
.btn:focus, .btn:active, .btn:focus:active {
  outline: medium none;
  background-color: #e61197;
  border-color: #e61197;
  color: #fff;
}
code {
  background: #ccc none repeat scroll 0 0;
}
mark, .spoiler {
  background: rgba(204, 204, 204, 0.25) none repeat scroll 0 0;
}
.empty-page {
  text-shadow: 0 0 0;
}
.panel {
  background-color: #2e343e;
}
.panel-default > .panel-heading {
  background-color: #2e343e;
  border-color: #2e343e;
  color: #ccc;
}
.panel-footer {
  background-color: #2e343e;
  border-top: 1px solid #2e343e;
}
.panel-default {
  border-color: #e63f97;
}
.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a:hover, .sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a.active {
  background: #3f424f none repeat scroll 0 0;
  border-color: #3f424f;
  border-radius: 30px;
}
.post-img figcaption {
  background: #e63f97 none repeat scroll 0 0;
}
.post-img .post-img-buttons {
  background: #e63f97 none repeat scroll 0 0;
}
.pull-left {
  float: left;
  color: black;
}
.captcha-form {
  background: #2e343e none repeat scroll 0 0;
}
.captcha-form input {
  border: 1px solid #e63f97;
  background-color: #3f424f;
}
.captcha-form-wrapper {
  background: rgba(0, 0, 0, 0.5) none repeat scroll 0 0;
}
a:hover, span.a:hover {
  color: #e66697;
}
.post .post-footer .post-button-reply {
  color: #e63f97;
}
.post .post-footer .post-button-reply:hover {
  color: #e66697;
}
.post-button:hover, .post-button.active {
  color: #e66697;
}
.btn-primary {
  background-color: #e63f97;
  border-color: #e63f97;
  color: #fff;
}
.btn-primary:hover {
  background-color: #e61197;
  border-color: #e61197;
  color: #fff;
}
.btn-primary.disabled:hover, .btn-primary.disabled:focus, .btn-primary.disabled.focus, .btn-primary[disabled]:hover, .btn-primary[disabled]:focus, .btn-primary.focus[disabled], fieldset[disabled] .btn-primary:hover, fieldset[disabled] .btn-primary:focus, fieldset[disabled] .btn-primary.focus {
  background-color: #e61197;
  border-color: #e61197;
}
.btn-info:focus, .btn-info.focus {
  background-color: #e61197;
  border-color: #e61197;
}
.btn-info:hover {
  background-color: #e61197;
  border-color: #e61197;
}
.btn-info {
  background-color: #e63f97;
  border-color: #e63f97;
}
.post-highlighted {
  border-color: #e66697 !important;
  box-shadow: 0 0 20px #e66697 inset, 0 1px 4px #e66697 !important;
}
.im-box .dialog-view {
  background: #2e343e none repeat scroll 0 0;
}
.input-group .form-control:first-child, .input-group-addon:first-child, .input-group-btn:first-child > .btn, .input-group-btn:first-child > .btn-group > .btn, .input-group-btn:first-child > .dropdown-toggle, .input-group-btn:last-child > .btn:not(:last-child):not(.dropdown-toggle), .input-group-btn:last-child > .btn-group:not(:last-child) > .btn {
  background-color: #3f424f;
}
.has-feedback .form-control {
  background-color: #3f424f;
}
.list-group-item:last-child {
  background-color: #3f424f;
}
.fa, address::before, .im-address::before {
  color: #fff;
}
.form-control:focus {
  border-color: #e63f97;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.075) inset, 0 0 8px #e63f97;
}
address, .im-address {
  border: 1px solid #fff;
  color: #fff !important;
}
.sidebar ul.list li a:hover i, .sidebar ul.list li a:hover::before {
  color: #e61197;
}
.im-button:hover {
  color: #e61197 !important;
}
*::-moz-selection {
  background: #e66697 none repeat scroll 0 0;
}
::selection {
  background: #e66697;
}
:focus {
  outline-color: #e61197;
}
.__cov-progress {

    background-color: #e63f97!important;
}

}
