@-moz-document domain("acfun.cn") {
/**************************************通用***************************/
html {
    background-color: transparent;
}
body {
    background-image: url(http://imgs.aixifan.com/live/1494746650220/1494746650220.jpg) !important;
    background-position: center bottom !important;
    background-size: cover !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important; 
}

.header .header-top {
    background: pink!important;
}
.search-box .search-btn {
    background: rgba(76, 253, 115, 0.21);
}
.search-box .search-btn:hover {
    background: rgba(76, 253, 115, 0.5);
}
.download-app a,
.gameIcon a,
#header-guide a {
    text-decoration: none;
}
.header-banner .point {
    display: none;
}
.header-banner .header-pendant {
    background: none;
    height: 0px;
    width: 0px;
    
    
    
}
.header .header-banner {
    height: 0px;
}
.header .header-banner a.banner-href {
    background-image: none;
    height: 0px;
}
.header .clearfix.wp.nav-parent {
    background: transparent;
}
.header .nav {
    top: 0px;
    background-color: transparent;
    border-radius: 0 0 20px 20px;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 4px 4px 1px;
}
#nav li a {
    text-decoration: none;
    border-radius: 20px 20px 0 0;
}
/************************************滑条***************************************/
/*字体*/
@font-face{
	font-family: 'Material Icons';
	font-style: normal;
	font-weight: normal;
	src: url(http://onox.qiniudn.com/maverick/materialicons_safari_v16.woff) format('woff');
}
/*字体*/
::-webkit-scrollbar{
    margin-right: -10px !important;
    width: 6px;
    height: 6px;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-track{
    -webkit-box-shadow: 0 0 3px rgba(0,0,0,0.3) inset;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-thumb{
    border-radius: 3px;
    -webkit-box-shadow: 0 0 3px rgba(0,0,0,.3) inset;
    background-color: #999;
}
::-webkit-scrollbar-thumb:hover{
    background-color: #777;
}
::-webkit-scrollbar-thumb:active{
    background-color: #555;
}


.win .footer {
    margin-top: 90px;
    border-radius: 20px;
    padding-top: 30px;
    box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 10px 1px;
    background: rgba(250, 250, 250, 0.5);
}
.win .footer:hover {
    background-color: rgba(255, 255, 255, 0.8);
    transition: background-color 0.5s ;
    box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px 2px;
}
}

@-moz-document regexp("^https?://(www.)?acfun.cn/v/list\\d+/index.htm") {
/********************************其他模块*************************************/

.win.chrome .main {
    background: transparent !important; 
}
.win .main nav.nav {
	background:none;
}

}
