css:
@-moz-document domain("ucertify.com") {
body
{
  /* darken the page background */
  background-color: black;
  color: #eee;
}

div.container.page_wrapper #container #main-page,
div.container.page_wrapper #container #main-page #frm_studyplanner > table,
table
{
  background-color: #111;
}

.whi .d
{
  color: white;
}

#frontpage ul li a.menu-item
{
  color: lightgrey;
}

#frontpage ul li small
{
  color: grey;
}

.modal-dialog .modal-content
{
  color: grey;
  background-color: #111;
  border: 4px solid red;
}


/***** course list *****************/
.pC .item
{
  background-color: #111;
}

input#search_course
{
  background-color: black;
}

table#list1
{
  background-color: black;
}

.table thead tr th
{
  background-color: #222;
  color: lightgrey;
}

table#list1 > tbody > tr
{
  /* override inline style */
  background-color: #222 !important;
}

table#list1 > tbody > tr.active_course
{
  /* highlight active courses */
  background-color: #232 !important;
}


/****** study planner sidebar ********/
.box
{
  background-color: #222;
  background-image: none;
  color: lightgrey;
}


/*************************************/

/********** Flash Cards ********************************************************************/
div.flipbox
{
  /* "click to flip" box */
  background-color: rgba(0, 0, 0, 0.5);
  color: rgba(255, 255, 255, 0.5);
}

.card_wrapper #flash_card .show_it,
.card_wrapper #flash_card .hide_it,
.card_wrapper #flash_card,
#main-page.bla #flash_card,
div.show_it,
.show_it_heading,
#main-page.bla .hide_it,
#main-page.bla .show_it,
#no-main.bla .hide_it,
#no-main.bla .show_it
{
  /* darken the flashcard */
  background-color: rgba(10, 10, 10, 0.5);
  background-image: none;
  color: #ddd;
}

.hide_it,
.show_it
{
  background-color: #333 !important;
  background-image: none !important;
  border: 1px solid #555;
}

#flash_card .show_it_heading
{
  /* darken the question on the answer side of the flashcard */
  background-color: #aaa;
  color: black;
}

table.sorttable
{
  background-color: #111;
}

.table1 tr:nth-child(2n),
.table5 tr:nth-child(2n+1),
.table5 thead > tr
{
  background-color: #222 !important;
}

.bla .sk-spinner-three-bounce div,
.bla table,
.sep table
{
  background-color: #111 !important;
}

.bla #columnize .setImage
{
  background-color: #333;
}

#main-page.bla .def_factbox
{
  background-color: rgb(53, 53, 53);
}

#main-page.bla .def_factbox .title
{
  background-color: rgb(123, 123, 110);
}

#no-main.whi
{
  background-color: #333;
}

#sortable li
{
  /* reorder list */
  background-color: rgb(30, 30, 20);
}


/**************** Footer *****************************************************************/
#testq,
div .bottom-toolbar,
div .bottom-toolbar div
{
  /* darken the bottom toolbar */
  background-color: #222;
  background-image: none;
  /*opacity: 0.8;*/
  border: none;
}

footer#bottombar .bottom-toolbar
{
  background-color: #222;
  border: none;
  margin: 0;
}

footer#bottombar
{
  /* darken the bottom toolbar */
  background-color: black;
  border-color: black;
  border: none;
}

footer div div,
footer div button
{
  /* brighten footer text and buttons */
  color: #aaa;
}

footer#bottombar .globalGoback
{
  color: darkred;
}

footer#bottombar div.btn-group
{
  border: 0;
  margin: 0;
}

footer#bottombar div.btn-group button,
footer#bottombar div.btn-group a
{
  border: 0;
  margin: 0;
}

footer#bottombar .dropup ul.settings-menu
{
  border-color: black;
  border: 0;
  margin: 0;
}

footer#bottombar div.bottom-toolbar
{
  border: none;
}

footer#bottombar div#tools_gotit,
footer#bottombar div#tools_gotit .popover-title
{
  background-color: #444;
}

footer#bottombar div#tools_gotit .arrow
{
  color: #444;
  border-top-color: black;
}

#bottombar .btn-group button.btn:disabled *
{
  color: grey;
}

#d2
{
  color: grey;
}


/********** Header ***********************************************************/
#mainbar #top header#titlebar
{
  /* darken the top header / toolbar */
  background-color: #222;
  color: #aaa;
}

img.logo-v
{
  /* lighten the logo background so we can see it */
  background-color: #855;
}

header .dropdown.feedback
{
  /* brighten and theme the feedback button */
  /* background-color: #07b; */
}

header .navbar-nav .dropdown.feedback a
{
  /* change the feedback button text color to make it readable */
  color: #888;
}

header .navbar-nav .dropdown.success-active
{
  /* darken the library / user button */
  /* !important is required here to override the page's use of !important */
  background-color: #059 !important;
}

header .navbar-nav li.dropdown.success-active > a
{
  /* darken the library / user button text */
  color: #ccc !important;
}

a.np > img:nth-child(1)
{
  /* fade the ucertify logo so it doesn't stick out */
  opacity: 0.5;
}

.nav li.max-77 h3.pull-left a
{
  /* brighten the course name text */
  /* again, important to override the page */
  color: #aaa !important;
}

center div .heading
{
  /* lighten the headings in labs */
  color: #aaa;
}


/************ Labs ************************************************************************/
div.ui-dialog.ui-widget.tasks-dialog,
div.ui-dialog.ui-widget.tasks-dialog #taskTestWindowDiv,
div.ui-dialog.ui-widget.tasks-dialog .tasks-footer.testTaskFooter
{
  background-color: #bbb;
}

.dragable
{
  color: black;
}


/********** Main Page *********************************************************************/
.table-striped > tbody > tr:nth-of-type(2n+1)
{
  background-color: #555;
}

table tbody tr td
{
  color: #ddd;
}

#main-page.whi,
#no-main.whi,
.settings.whi,
div.sh,
div
{
  color: #ddd;
}

h1,
h2,
h3,
h4,
h5,
h6
{
  color: #ddd;
}

h3.heading
{
  color: lightgreen;
}

a
{
  color: pink;
}

a:hover,
a:focus {}

table.table-hover > tr:hover
{
  background-color: #222;
}


/********** Exercises **********************************************************************/
#testq div.container.page_wrapper #container #main-page
{
  background-color: #555;
}

input[type=checkbox],
input[type=radio]
{
  /* fix messed-up input boxes */
  -moz-appearance: none;
  -webkit-appearance: none;
  appearance: none;
  -webkit-transform: scale(2);
  -moz-transform: scale(2);
  transform: scale(2);
  /*   background-color: orange;
    border: 5px solid orange;*/
  border: 2px solid black;
  border-radius: 50%;
  color: purple;
}

input:checked
{
  border: 5px solid orange;
  color: orange;
  background-color: orange;
}
}
