css:
@-moz-document domain("oransns.com"), domain("mstdn.jp"), domain("friends.nico"), domain("pawoo.net"), domain("mastodon.nara.jp"), domain("md.yutasan.co") {
div.column-back-button span {
    color: white !important;
}
div.ui {
    background: #eeeeee !important;
}
div.column-header,
div.drawer__header,
div.collapsable-collapsed,
div.column-icon,
div.column-back-button,
div.tabs-bar,
div.detailed-status__action-bar {
    background-color: #db4437 !important;
    background: #db4437 !important;
}
div.tabs-bar > a[class^="tabs-bar"],
div.detailed-status__action-bar {
    border: none
}
div.drawer__header a:hover,
div.collapsable-collapsed:hover,
div.column-icon:hover,
div.column-back-button:hover,
div.tabs-bar > a[class^="tabs-bar"]:hover,
div.tabs-bar__link:hover,
div.tabs-bar__link:focus,
div.tabs-bar__link:active {
    background-color: #ab3427 !important;
    background: #ab3427 !important;
}
div.drawer__inner {
    background-color: #eee !important;
}
div.compose-form__buttons {
    background-color: #ddd !important;
}
.navigation-bar strong {
    color: #222 !important;
}
div.drawer__inner > div > div > div > div > span {
    color: #bbb !important;
}
a.tabs-bar__link.active,
div.column-header.active .fa,
a.drawer__tab i[class^="fa"],
div.column > div+div > i[class^="fa"],
div.column > div+div > div > i[class^="fa"],
.detailed-status__action-bar button.icon-button,
.detailed-status__action-bar a[class="dropdown__trigger icon-button"] {
    color: white !important;
}
div.search i[class^="fa"] {
    color: #999 !important;
}
.search__input {
    color: #111 !important;
    background-color: #ddd !important;
}
.column>.scrollable {
    background-color: #eeeeee !important;
}
.status__content a,
.reply-indicator__content a {
    color: #6666FF !important;
}
div.ui .status__display-name strong,
div.ui .account__display-name strong,
a.detailed-status__display-name strong {
    color: #000000 !important;
    font-weight: bold;
}
a.detailed-status__display-name {
    color: #444 !important;
}
div.ui .status .status__display-name,
p {
    color: #000000 !important;
}
div.notification__message,
div.account__action-bar__tab>span,
div.account__action-bar__tab strong {
    color: #111 !important;
}
div.scrollable > div > div.status,
div[style^="cursor"] > div+div.status,
div[class^="notification"] > div.status,
div.detailed-status {
    margin-top: 6px;
    border-radius: 2px;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
    background-color: #fdfdfd !important;
    border: none;
}
div.account__header> div {
    background-color: #fff !important;
}
div.account__header div.account__header__content {
    color: #222 !important;
}
.account__action-bar__tab strong {
    color: #111 !important;
}
.status__content .status__content__spoiler-link,
.reply-indicator__content .status__content__spoiler-link {
    background-color: transparent !important;
}
}

