css:
@-moz-document domain("video.sibnet.ru") {
	BODY {
		background-color: #302e2e;
	}
	A:hover, BODY {
		color: #ccc;
	}
	BODY, TD, TH {
		background-color: #1b1919e6;
	}
	div.video_cell {
		background-color: #636363;
		margin: 2px;
	}
	.related-video .video_cell {
		padding: 0 0 0 0;
		background-color: #535454;
		margin: 2px;
	}
	.b-sibnet-head__item {
		border: 3px double #ccc;
		background: #434344;
		margin: 2px;
	}
	a.b-sibnet-head__item__link {
		color: #ccc;
	background: #434344;
	}
	.b-sibnet-head__sub-menu {
		background: #3d3d3ecc repeat-x;
		margin: 2px;
	}
	.b-sibnet-head__sitemap {
		border: 2px solid #3d3d3ecc;
		background: #3d3d3ecc repeat-x;
	}
	.b-sibnet-head__sitemap__block {
		background: #3d3d3e66;
	}
	.b-sibnet-head-wrapper a {
		color: #ccc;
	}
	.b-sibnet-head__sitemap__top-link {
		color: #ccc;
	}
	.b-sibnet-links__item__text {
		font-size: 10px;
		color: #ccc;
	}
	.b-sibnet-links__item__icon {
		background: no-repeat;
	}
	.b-sibnet-head__logo-wrapper {
		background: #8f8f8fb3;
	}
	a.b-sibnet-head__sitemap__service-link {
		color: #ccc;
	}
	.totopdiv:hover {
		background: rgba(240, 240, 240, 0.39);
		color: #c8c8c84d;
	}
	div.tit_fon {
		background: #cccccc1a left center repeat-x;
	}
	div.block_line_grey {
		background-color: #cccccc1a;
	}
	table.main td.under_content div.under_content_menu {
		border-top: 13px solid #7e7d7d;
		background-color: #dcdcdc1a;
	}
	A, A:visited {
		color: #999;
	}
	table.video_menu td {
		background: #535454 center left repeat-x;
		border: 3px double #ccc;
		margin: 2px;
	}
	table.video_menu td {
		background: #474747 center left repeat-x;
	}
	p.multipagesnavig a:hover, p.multipagesnavig b {
		background: #333 repeat-x;
		color: #ccc;
		border: 3px double #ccc;
		margin: 2px;
	}
	p.multipagesnavig a, p.multipagesnavig b {
		color: #ccc;
		background: #636363 repeat-x;
		border: 3px double #5d5757;
		margin: 2px;
	}
	.totopdiv span {
		background: #555 no-repeat;
	}
}
