css:
@-moz-document domain("video.sibnet.ru") {
	BODY {
		background-color: #333;
	}
	table.main {
		background-color: #333;
	}
	table.video_menu td {
		background: #666 center left repeat-x;
	}
	a.b-sibnet-head__item__link {
		color: #ccc;
		background: rgb(102, 102, 102) no-repeat 50% -40px;
		}
	.b-sibnet-head__sub-menu {
		border: 2px solid rgba(59, 178, 200, 0.45);
		background: rgba(49, 152, 169, 0.41) repeat-x;
	}
	.b-sibnet-head__logo-wrapper {
		background: rgba(130, 130, 130, 0.58);
	}
	table.main td.under_content div.under_content_menu {
		border-top: 13px solid #666;
		background-color: #666;
	}
	A, A:visited {
		color: #dcdcdc;
	}
	.b-sibnet-links {
		background: #666;
	}
	.b-sibnet-links__item__icon {
		background: #666 no-repeat;
	}
	.b-sibnet-links__item__text {
		font-size: 10px;
		color: #999;
	}
	div.video_cell {
		background: #666;
		margin: 2px;
	}
	.b-sibnet-head__sitemap {
		background: rgba(79, 184, 202, 0.38) repeat-x;
	}
	.b-sibnet-head__sitemap__block {
		background:rgba(49, 152, 169, 0.41);
	}
	.b-sibnet-head__sitemap__contacts {
		background: rgba(58, 156, 174, 0);
	}
	.b-sibnet-head-wrapper a {
		color: #fdfdfd;
	}
	A:hover, BODY {
		color: #f9f9f9;
	}
	a.b-sibnet-head__sitemap__service-link {
	color: #ffffff;
	}
	p.multipagesnavig a:hover, p.multipagesnavig b {
		background: #666 repeat-x;
		border: 3px double #5d5757;
	}
	p.multipagesnavig a, p.multipagesnavig b {
		color: #575757;
		background: #999 repeat-x;
	}
}
