css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("coub.com") {
body, .box--banner, .header.-scrolled > .header__bg, .-bg-color--white, .sb.-string, .main-menu__wrapper, .page-menu::before, .modal--dialog .dialog, .channels-block.channels-block--list .list__item.ui-sortable-helper, .modal--dialog .dialog ,.dropdown__content * {
	color: white;
	background-color: #123 !important;
}
p {
	color: #777;
}
.channel__relationships__message,.follow-button__container button,.coub__description .description__stamp__community,.coub__tags-tag.tags-community, .story__tags-tag.tags-community,.modal--simple .modal__content,
.animation-wrap .list__item.fade, .sb.-grey, .sb.-grey.active, .sb.-grey:active.active, .sb.-grey:active, .sb.-st, .badge-counter, .-bg-color--blue-ribbon, .header-channel__notify-badge .sb.-notify-handler:active, .header-channel__notify-badge .sb.-notify-handler:hover, .header__create-btn .sb.create.cutter-blue, .hero-cover__progress-bar, .timeline-controls__editor-switch > span:before, .search-popup .dropdown__inner li.selected a, .search-popup .dropdown__inner li a:hover, .viewer.viewer--v2 .viewer__hand:hover:before, .viewer.viewer--v2 .custom-ad-overlay > .banner-fold.close-small.close-small--round:hover, html:not(.mobile) .viewer.viewer--mobile .viewer__hand:active, .coub.coub--small-card .coub__stub, .coub .coub__stub, .coub__tags-tag:hover, .share-popup__option .option__size > .frame.active, .viewer.viewer--v2 .viewer__hand:hover:before, body.-vk-embed .viewer--mobile .viewer__open-in-app, .viewer.viewer--v2 .custom-ad-overlay > .banner-fold.close-small.close-small--round:hover, .list.list--selectable .list__item:not(.disabled).active, .list.list--selectable .list__item:not(.disabled):hover, .header-channel__notify-badge .sb.-notify-handler, .coub__tags-tag {
	background-color: #234;
}
.header__create-btn .sb.create.cutter-blue {
	background: #555;
}
html:not(.mobile) .sb.-bb:hover {
	box-shadow: 0px 0px 0px 3px #555;
}
.header.-scrolled, .profile__channel-panel {
	box-shadow: 0px 1px 0px #555;
}
.channels-block.channels-block--list .list__item, .coub__tags-tag {
	border-color: #555;
}
.header.-transparent .header__search .input-field, .header.-transparent .header__create-btn .sb.create.cutter-white {
	background: transparent;
}
a, a:hover, .dropdown__handler div, strong, .hbold, .timeline-controls .timeline-controls__sort .list__item.active {
	color: beige !important;
}
.suggest__item {
	border-radius: 6px;
	border: 1px dashed #555;
}
p, span, .suggest__item-stamp > .-color--emperor, footer.footer .footer__copyright {
	color: #777;
}
.nav__menu, .header__bg, .dropdown__content, .modal--dialog .dialog, .inline--container li > *, .dd-notification-list .list__item .object-media__img .object-media__img__badge, .nav__menu.-detached, .list.list--selectable .list__item:not(.disabled):hover.-no-hover, .tooltip.-music-prompt-with-image, .header__mobile-menu, .header.-scrolled > .header__bg, .viewer.viewer--v2 .custom-ad-overlay .html5-player__white-overlay, .viewer.viewer--v2 .viewer__copylink__layer > input, body:not(.-vk-embed) .coub__embed .viewer.viewer--mobile .viewer__copy__input, .slider, .ui-slider-handle, .ui-slider-range, .search-popup .dropdown__inner li.selected a, .search-popup .dropdown__inner li a:hover, .search-popup .dropdown__content, .search-popup__results__more {
	background: #012 !important;
	color: white !important;
}
}
