css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("j-lyric.net") {
  div .h60, div .bdy, div .bdyc, div .lbdy, div .sns, #cpw, #idx, #ttb, #captionBand, #Index, #mainBlock .body, #leftBlock .body, #rightBlock .body
  { background-color: #121212 !important; }
  div .cap, #ftb, a[style="background-color: rgb(255, 255, 255);"], img.i5r, #mainBlock .caption, #leftBlock .caption, #rightBlock .caption, #sideLyricRank .cdimage, #fontBlock, #bottomBand, a.arrow1, #newreleaseBlock .cdimage, #pagerBlock a:hover, #idx a, #idx .blk, #idx .sel, #rankingBlock .image, img.ill, #lyricList .title, #menuBand, #nav, #mnu
  { background-color: #2D2D2D !important; }
  #pagerBlock a, #pager a
  { background-color: #2D2D2D; }
  #pagerBlock a.selected, #pager a.sel, #idx .sel, #Index .selected, #pager a:hover
  { background-color: #CCCCCC !important; }
  #ttb, div .cap, a.am-m, #captionBand, #Index, #mainBlock .body, #leftBlock .body, #rightBlock .body, #idx a, #idx .blk, #idx .sel, a.am-d, a.am-b, a.alw
  { background-image: none !important; }
  
  [color="#000"], [color="#000000"], [color="Black"], p.ss
  { color: #AAAAAA !important; }
  #mnu .m1a, #mnu .m2a, #menuBand .menu_1a, #menuBand .menu_2a, #fontBlock, #sbox ul li, #sideLyricRank .rank, #rankingBlock .rank, div .rnk, #rankingBlock .rank, div .rno
  { color: #FFFFFF !important; }
  #mnu .m2b, a[style="background-color: rgb(255, 170, 0);"], #menuBand .menu_2b, #pagerBlock a.selected, #mnu .m1b, #pager a.sel, #idx .sel, #Index .selected, #pager a:hover
  { color: #000000 !important; }
  #idx a
  { color: #E0BC2D !important; }
  [color="#00F"], [color="#0000FF"], [color="Blue"], [color="Navy"], [color="#000080"], [color="#0000F0"], [color="#0000ee"], #rtb .bdy p.sml:nth-of-type(1), #newreleaseBlock .status:nth-of-type(2) a
  { color: #3399FF !important; }
  #ftb, div .sns, [style="width:320px;height:100px;margin:15px auto;"], [style="width:250px;height:250px;margin:0px auto 15px auto;"], #bookmarks, #bottomBand, [style="margin:15px auto;"], div .ad, #ltb #cpw
  { display: none !important; }
  #ftb
  { border: 0 !important; }
  div .h60, div .bdy, div .bdyc, div .lbdy, div .sns, #cpw, #idx, img.ill, img.i5r, img.i4l, img.i6l, #Index, #mainBlock .body, #leftBlock .body, #rightBlock .body, #sideLyricRank .cdimage, #newreleaseBlock .cdimage, #rankingBlock .cdimage, #lyricList .cdimage
  { border: 1px solid #121212 !important; }
  img.ill
  { width: 160px !important; }
  img.i6l
  { width: 60px !important; }
  #newreleaseBlock .cdimage
  { width: 77px !important; }
  #newreleaseBlock .title, img.i5r, p.mid, .bdy p.sml
  {
    text-overflow: ellipsis !important;
    overflow: hidden !important;
    white-space: nowrap !important;
  }
  #sideLyricRank .body a:nth-of-type(2), #Index a, #Index .selected, #Index .blank, #lyricList .status a, #newreleaseBlock .status:nth-of-type(1) a, p.sts:nth-of-type(2) a
  { font-size: 14px !important; }

  body * { font-family: "苹方-简", "蘋方-簡", "蘋方-繁", "文泉驛微米黑", "WenQuanYi Micro Hei", "Droid Sans Fallback", "Noto Sans CJK TC Regular", "Microsoft YaHei UI", "Microsoft YaHei", "微软雅黑", Sans-serif; }
  body * { text-decoration: none !important; text-shadow: none !important; box-shadow: none !important; }
  html, body { color: #AAAAAA; background: #121212 !important; background-color: #121212 !important; background-image: none !important; }
  body h1, body h2, body h3, body h4, body h5, body h6, body ul, body li, body ol, body span, body p, body dt, body dd, body dl { background-color: transparent !important; }
  body h1, body h2, body h3, body h4, body h5, body h6, body th, body em, body b, body strong, label { color: #FFFFFF; }
  body div, body p  { color: #AAAAAA; }
  body blockquote { color: #AAAAAA !important; background-color: #2D2D2D !important; background-image: none !important; }
  body textarea { color: #E0BC2D !important; background-color: #2D2D2D !important; background-image: none !important; }
  body table, body tr, body td { background-color: transparent; color: #AAAAAA; background-image: none !important; }
  body select, body option { background-color: #2D2D2D !important; color: #E0BC2D !important; }
  body input { color: #E0BC2D !important; background-color: #2D2D2D !important; }
  body button, body input[type="button"], body input[type="submit"] { color: #000000 !important; background-color: #CCCCCC !important; }
  body a { color: #E0BC2D !important; }
  body a:link { color: #E0BC2D; }
  body a:visited { color: #AAAAAA; }
  body a:hover { color: #3399FF !important; }
  body a:active { color: #423F3F !important; }
  lightbox * { background: transparent !important; background-color: transparent !important; }
}
