css:
@-moz-document domain("dhtls.net") {
::-webkit-scrollbar {
    display: none;
}
body {
    color: #29e6e4;
    /*フォント指定PCに"ＤＦＰ勘亭流"がない場合は以下の通りに置き換えて下さい*/
     /*Font designation: If there is no "DFKanTeiRyu-XB-MP-RKSJ-H" on the PC, please replace it as follows*/
     /*Win →　  font-family: 'Yu Gothic' !important;  */
     /*Mac →　  font-family: ''HiraKakuProN-W6'!important;  */
    font-family: 'DFKanTeiRyu-XB-MP-RKSJ-H' !important;  
}
.navigation-bar {
    /*プロフィールを編集*/
        color:#62cdbf  ;
}
.ui {
    /*カラムの背景*/
    background: #0b2226;
}
.table a {
    color: #32353e;
}
.column {
    width: 380px;
    font-size: 10% !important;
    text-shadow: 0 0 8px #dc163b;
}
.column > .scrollable {
    background: #0b2226;
}
.column-header {
    /*カラム上*/

    background: #0b2226;
}
.column-icon {
    /*カラム右上のアイコン*/
        color: #d85128;
    background: #0b2226;
}

.column-settings--section, .setting-toggle{  /*カラムドロップダウン文字*/
     color: #fff;
}
.column-settings--outer,.column-link  { /*カラムドロップダウン背景ブロック*/
    background: #025354;
}

.icon-button,
.button .icon-button {
    /*ファボなどのアイコン*/

    color: #6b9b9b;
}
.icon-button.active {
    color: #fef11d;
    text-shadow: 0 0 8px #dc163b;
}
.column-back-button {
    /*カラム戻る*/

    background: #0b2226;
    color: #cb1953!important;
}
.column-icon:hover {
    color: #dc163b;
    text-shadow: 0 0 8px #dc163b;
}
.status__content {
    /*TLの本文*/

    color: #2ef7e2;
    font-size: 12px !important;
}

.drawer__tab {  /*左上のタブの一番上のアイコン*/
    color: #28e0d9;
      text-shadow: 0 0 8px #fcfcf6;
}
.autosuggest-textarea__textarea,
.compose-form__modifiers ,.compose-form__buttons,.search__input ,.search__input:focus,.autosuggest-textarea__suggestions,
.autosuggest-textarea__textarea{
    /*ツゥーと入力欄と検索*/
    background: #06665e;
        color: #ffffda ;
    text-shadow: 0 0 8px #fcfcf6;
}

.compose-form__buttons {  /*入力欄の写真やCWタブ*/
    background: #0b2226;
}


.button {
    background-color: #851a24 !important;
    color: #ffffff;
    font-size: 11px !important;
}
.button:hover {
    background-color: #fa051c;
    text-shadow: 0 0 12px #fff;
}
.button:disabled {
    background-color: #851a24;
}
.emoji-dialog {
    background: #37e8f3;
    border-radius: 20px;
    box-shadow: 0 0 18px rgba(10, 241, 255, 0.9);
    opacity: 0.8;
}

.drawer__inner {
    /*左のタブ*/
     background: #0b2226;
    color: #dd4968;
}
.navigation-bar strong {
    color: #2bede6;
}
.drawer__tab {
    /*左のタブ上のとこ*/

    background: #0b2226;
}
.drawer__header {
    /*左のタブ下背景*/

    background: #0b2226;
}
.status__display-name strong ,.muted .status__display-name strong {
    /*TLグローバルのディスプレイネーム*/
    color: #dd4968;
}
.status .status__display-name ,.account .account__display-name {
    /*全部の＠ネーム*/
      color: #cb777d;
}
.status .status__relative-time {  /*時間　*/
    color: #cb777d;
}

.notification__message {
    /*通知のメッセージ*/

    color: #d29a2e;
}
.muted .status__content p,
.muted .status__content a {
    /*通知本文*/

    color: #29e6e4;
}
.account {
    /*通知した文*/
     color: #29e6e4;
}
.owner .name .username {
    display: block;
    color: #12f607;
}
.status.light .status__relative-time {
    color: #12f607;
}

.dropdown__sep { /*...のドロップダウンメニューの区切り線*/
     background: #000;

}
.dropdown--active .dropdown__content>ul {/*...のドロップダウンメニューの背景*/
    background: #37e8f3;
    border-radius: 15px;
    box-shadow: 0 0 18px rgba(10, 241, 255, 0.9);
    opacity: 0.9;
}
.dropdown--active .dropdown__content>ul>li>a {/*...のドロップダウンメニューの文字*/
     color: #fff;
	background: transparent;
}

}
