css:
@-moz-document url-prefix("https://onics.me/") {
/*Cuerpo de los posteos y barra superior */
  .brz-white, .brz-hover-white:hover
  {
    color: rgba(255, 255, 255, 0.85) !important;
    background-color: rgba(0, 0, 0, 0.6)!important;
  }

/* Cuerpo background y colores de letras izquierda */
  body
  {
    color: rgb(41, 186, 254);
    margin: 0;
    padding: 0;
    background: rgba(0, 0, 0, 0.73);
    word-wrap: break-word;
  }

/* Barra Izquierda home */
  .brz-main-nav-side
  {
    box-shadow: none!important;
    margin-top: 50px !important;
    z-index: 3;
    width: 250px;
    display: none;
    background: rgba(0, 0, 0, 0.65);
  }


/* Cuadro de estado, color y tamaño */
  .brz-card
  {
    border: 20px solid rgb(69, 69, 69);
  }


/* tamaño iconos love,com,den, trends y cuadro de estado */
  .brz-padding-medium, .brz-padding, .brz-form
  {
    padding: 4px 13px!important;
  }

/* espacio img de perfil entre bordes home */
  .brz-container
  {
    padding: 0.01em 10px;
  }

/* linea superior en estados home */
  hr
  {
    height: 0;
    border: 0;
    border-top: 1px solid #29bafe;
    margin: 1px 0;
  }

/* borde superior trends,tags, y letras de tags home */

  .brz-light-grey, .brz-hover-light-grey:hover
  {
    color: rgb(255, 255, 255) !important;
    background-color: #29bafe!important;
  }

/* color letras estado y trends home*/
  .brz-text-grey, .brz-hover-text-grey:hover
  {
    color: rgba(255, 255, 255, 0.9)!important;
  }

/* borde cuadros trend,tags,etc home */
  .brz-border-light-grey, .brz-hover-border-light-grey:hover
  {
    border-color: #454545!important;
  }

/* color y tamaño iconos estado home */
  .brz-border
  {
    border: 2px solid #29bafe!important;
  }

/* tamaño entre espacios de estados home */
  .brz-margin
  {
    margin: 18px!important;
  }

/* borde imagenesperfil estados home */
  .brz-round, .brz-round-medium
  {
    border-radius: 3px!important;
  }

/* iconos estados home tamaño y color de comentario */
  .brz-btn, .brz-btn-new, .brz-btn-love, .brz-btn-block
  {
    border: none;
    display: inline-block;
    outline: 0;
    padding: 5px 20px;
    vertical-align: middle;
    overflow: hidden;
    text-decoration: none!important;
    color: #ccc;
    background-color: #29bafe;
    text-align: center;
    cursor: pointer;
    white-space: nowrap;
  }

/* color "añadio una nueva foto estado" home */
  .brz-text-blue-grey, .brz-hover-text-blue-grey:hover
  {
    color: #29bafe!important;
  }

/* color y borde buscadores home */
  brz-input
  {
    padding: 8px;
    display: block;
    border: none;
    border-bottom: 1px solid rgba(255, 255, 255, 0.65);
    width: 100%;
  }

/* color linea baja barra superior home */
  .brz-navbar
  {
    list-style-type: none;
    margin: 0;
    padding: 0;
    overflow: hidden;
    transition: box-shadow 100ms ease 0s;
    border: 0px none;
    border-radius: 0.3em;
    background: none repeat scroll 0% 0% #FFF;
    box-shadow: 0px 0px 0px 1px rgb(38, 38, 38);
  }

/* color linea entremedia de trends,tags home */
  ul.brz-ul li
  {
    padding: 6px 2px 6px 20px;
    border-bottom: 1px solid #454545;
  }

  .brz-padding-16, .brz-padding-hor-16
  {
    padding-top: 10px!important;
    padding-bottom: 16px!important;
  }

/* color nombrusuario notificaciones */
  .brz-text-pink-light, .brz-hover-text-pink-light:hover
  {
    color: rgb(221, 221, 221) !important;
  }

/* color notificaciones boton home */
  .brz-active
  {
    color: #29bafe !important;
    box-shadow: 0px -3px 0px #29bafe inset;
    font-weight: 500;
  }

/* color inicio boton home */
  element.style
  {
    margin-top: 20px;
    color: #29bafe;
  }

/* color tendencias ahora boton home */
  element.style
  {
    margin-top: 20px;
    color: #29bafe;
  }








/* botones perfil tamaño */
  .brz-btn, .brz-btn-new, .brz-btn-love, .brz-btn-block
  {
    border: none;
    display: inline-block;
    outline: 0;
    padding: 5px 10px;
    vertical-align: middle;
    overflow: hidden;
    text-decoration: none!important;
    color: #ccc;
    background-color: #29bafe;
    text-align: center;
    cursor: pointer;
    white-space: nowrap;
  }

/* botones perfil tamaño2 */
  a
  {
    font-size: 12px;
    color: #ffffff;
    padding: 0.1em .5em;
    border-radius: 0.05em;
    text-decoration: none;
    -webkit-transition: background 0.2s;
    -moz-transition: background 0.2s;
    transition: background 0.2s;
  }

  .brz-padding-16, .brz-padding-hor-16
  {
    padding-top: 20px!important;
    padding-bottom: 30px!important;
  }


/* color tendencias ahora boton perfil */
  element.style
  {
    margin-top: 20px;
    color: #29bafe;
  }

/* color notificaciones boton perfil */
  element.style
  {
    margin-top: 20px;
    color: #29bafe;
  }





/* tamaño nombre usuario perfil */
  .brz-responsive-xbig-styled
  {
    font-family: "Raleway", sans-serif;
    font-size: 25px;
  }

/* tamaño cuadros perfil */
  .brz-col.l8, .brz-twothird
  {
    width: 66.66666%;
  }

/* color transparente portada */
  element.style
  {
    background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.12);
    padding: 1%;
    border-radius: 3px;
  }

/* color transparente nombreusuar portada */
  Style Attribute
  {
    width: 100%;
    background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.12);
    padding: 1%;
    border-radius: 3px;
    color: black;
  }

.brz-light-grey, .brz-hover-light-grey:hover {
    color: rgb(255, 255, 255) !important;
    background-color: rgb(100, 99, 99)!important;
}


}
