css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("peggo.tv") {
  
  html, body, .body.wrap {
    background: #222 !important;
  }

  review, .center-col .left-col .right-col{
    background: #222 !important;
    color: #aaa !important;
  }
  
   #subtrack, .form {
    background: #222 !important;
    color: #444 !important;
  }
  
   #id3, .form {
    background: #222 !important;
    color: #444 !important;
  }
  
  #record-audio-tab, #record-video-tab, .a  {
    background: #333 !important;
  }
  
  input, #id3-title, .text, #record-audio-tab, .text, #record-video-tab, .text, #id3, .left-label, .right-label, #example, .p, .a{
  color: #dbdbdb !important;
  }

  input, id3-title {
  background: #333 !important;
  }
  
  #search-box, .ui-autocomplete-input, .text {
    background: #222 !important;
  }
  
input {
    -webkit-text-fill-color: #fff !important;
}
  
  #example, .a{
    display: none;
    
  }
  
  #videos, .container div{
    background: #222 !important;
  }
}
