css:
@-moz-document domain("volafile.io"), domain("volafile.org") {
html {
    --main-bg-color: #1f1f1f;
    --snd-bg-color: #232323;
    --input-color: #232323;
    --input-disabled-color: #8e8e8e;
    --snd-input-color: #1f1f1f;
    --box-color: #1f1f1f;
    --slider-color: #1f1f1f;
    --hi-color: #1f1f1f;
    --hi-hover-color: #323232;
    --file-list-color: #1f1f1f;
    --file-color: #2e2e2e;
    --file-hover-color: #3d3d3d;
    --header-hi-color: #454545;
    --tag-color: rgba(160, 150, 150, 0.3);
    --button-color: #01f3c9;
    --button-hi-color: #4fd8c0;
    --button-light-color: #666666;
    --button-light-hi-color: #777777;
    --channel-color: #d880fc;
}
*, *:after {
    border-color: var(--main-bg-color) !important;
}

html {
    background-color: var(--snd-bg-color);
}

select {
    background-color: #424242;
}

table th {
    background-color: var(--main-bg-color);
}

#chat_frame,
#files_frame {
    border-top-color: var(--header-hi-color) !important;
}

#chat_header,
#files_header,
#name_container,
#room_search {
    height: 2em;
    line-height: 2em;
}

#name_container {
    padding-left: 0.5ex;
}

#clearsearch,
.header_row_element {
    line-height: 2em;
}    

#volafile_icon_svg {
    height: 2em;
    width: 2em;
    padding: 0 .8em;
}

#volafile_icon_svg #v_stroke {
    stroke-width: 80px;
}

#chat_frame,
#files_frame {
    top: 2em;
}

#room_name {
    font-size: 1.3em;
}

#rename_container {
    line-height: 1.7em;
}

#rename_input {
    font-size: 1em;
    width: 20em;
}

.filelist_file {
    border-bottom: 1px solid var(--main-bg-color);
}

#file_list.even .filelist_file:nth-child(2n),
#file_list.uneven .filelist_file:nth-child(2n+1) {
    background-color: var(--file-list-color);
}

.file_selected {
    background-color: #464646 !important;
}

.filelist_file:hover:before {
    border-color: var(--file-hover-color);
}

.scroller-slider,
.nano>.pane>.slider {
    background-color: var(--slider-color);
}

#chat_hbar_container {
    box-shadow: 0 -5px 5px 0 var(--snd-bg-color) !important;
}

#file_notifier,
#filter_reminder,
#chat_hbar {
    background-color: var(--main-bg-color);
}

#file_notifier:hover,
#filter_reminder:hover {
    background-color: var(--hi-hover-color);
}

#chat_hbar {
    display: flex;
    justify-content: flex-end;
    align-items: flex-end;
    border: 0;
}

#chat_name {
    width: 16ex;
    margin: 0;
    height: auto;
    border: 0;
    margin-top: 1px;
    margin-bottom: 1px;
    padding-left: 1ex;
    padding-right: 1ex;
}

#user_count {
    margin-left: 1ex;
    margin-right: 1ex;
    min-width: 5ex;
}

#chat_input {
    padding: 0.5ex 1ex;
    font-size: inherit;
}

#toggles span.toggle_icon {
    color: var(--input-disabled-color) !important;	
}

#toggles>.toggle.enabled span.toggle_icon {
    color: #f6f1f1 !important;    
}

#search_input:hover,
#toggles>.toggle:hover {
    background-color: rgba(120, 120, 120, 0.1);
    border-radius: 3px;
}

body:not(.mobile_filelist) #toggles span.toggle_container {
    margin-right: 1ex;
}

#toggles>.toggle:active {
    background: transparent;
}

#room_search {
   background-color: var(--input-color);
}

body:not(.mobile_filelist) #room_search {
   margin-left: 1em;
}

#search_input:active, #search_input:focus, #search_input:valid {
    background-color: transparent;
    width: 25em;
}

.ui_frame_contextmenu {
    border-radius: 0;
    border: 0;
}

.ui_frame_contextmenu,
.ui_frame_contextmenu .button.light,
.ui_frame_contextmenu .button.light:active,
.ui_frame_contextmenu .button.light:visited {
    background-color: var(--context-bg-color);
}

input[type=text],
input[type=password],
textarea {
    background-color: var(--input-color);
}

input[type=text]::-webkit-input-placeholder,
input[type=password]::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {
    color: var(--input-disabled-color);
    opacity: 1;
}

input[type=text]::placeholder,
input[type=password]::placeholder,
textarea::placeholder {
    color: var(--input-disabled-color);
    opacity: 1;
}

input[type=text][disabled],
input[type=password][disabled],
textarea[disabled] {
    color: var(--input-disabled-color);
}

input[type=radio]:disabled+span._icon:before,
input[type=checkbox]:disabled+span._icon:before,
.radio_toggle {
    color: var(--input-disabled-color);
}

.main > form input[type=text],
.main > form input[type=password] {
    background-color: var(--snd-input-color);
}

input.defaultValue[type="text"],
input[type="text"][disabled],
input.defaultValue[type="password"],
input[type="password"][disabled],
textarea.defaultValue,
textarea[disabled] {
    color: #aaaaaa;
}

.chat_message > i {
    color: var(--channel-color);
}

.chat_message {
    white-space: nowrap;
}

.chat_message a[target],
.chat_message i:not([class]),
.chat_text {
    white-space: normal;
}

.chat_file {
    background-color: var(--file-color);
}

.chat_file:hover,
.chat_file:active {
    background-color: var(--file-hover-color);
}

#chat_messages_frame:before {
    border-left-color: var(--snd-bg-color);
}

.chat_message.highlight {
    border-left-color: var(--hi-color) !important;
    background-color: var(--hi-color);
}

.ui_frame,
#overlay {
    background-color: var(--box-color);
}

.dropdown:hover,
a.header_row_element:hover {
    background-color: var(--header-hi-color);
}

a.header_row_element:active {
    background-color: var(--header-hi-color);
}

.header_row_element.clickable:hover,
a.header_row_element_disabled,
a.header_row_element_disabled:active,
a.header_row_element_disabled:hover,
a.header_row_element_disabled:visited {
    background-color: var(--header-hi-color);
}

.header_row_element_disabled.clickable,
.header_row_element_disabled.clickable:hover {
    color: var(--input-disabled-color);
}

.dropdown_item:hover {
    background-color: var(--hi-hover-color);
}
.dropdown_item:active {
    background-color: var(--header-hi-color);
}
.dropdown_list {
    background-color: var(--header-hi-color);
}
.dropdown:after {
    border-color: var(--header-hi-color);
}

.file_status:before,
hr {
    border: 1px solid #434343;
}

.file_tag {
    background-color: var(--tag-color) !important;
}
.button:not(.gallery_button),
input[type=submit] {
    background-color: var(--button-color);
}

.button:visited:not(.gallery_button),
input[type=submit]:visited {
    background-color: var(--button-color);
}

.button:hover:not(.gallery_button),
input[type=submit]:hover {
    background-color: var(--button-hi-color);
}

.button:active:not(.gallery_button),
input[type=submit]:active {
    background-color: var(--button-hi-color);
}

.gallery_button {
    font-size: x-small;
}

.gallery_area {
    border: 0;
}

.button.light,
.button.light:visited,
input[type=submit].light,
input[type=submit].light:visited {
    background-color: var(--button-light-color);
}
.button.light:hover,
input[type=submit].light:hover {
    background-color: var(--button-light-hi-color);
}

#name_container {
    background-color: var(--main-bg-color);
}
.ui_frame_content .button.light:hover {
    background-color: var(--button-light-hi-color);
}

#chat_header,
#files_header {
    background-color: var(--main-bg-color);
}

#chat_name_container {
    background-color: var(--main-bg-color);
}

#volafile_icon_svg {
    background-color: var(--main-bg-color);
}

#room_name_angle_down,
#chat_user_angle_up,
#chat_user_icon,
#clearsearch {
    opacity: 0.3;
}

#chat_user_angle_up {
    padding-left: 0.7ex;
    display: none;
}

#chat_user_icon {
    padding-right: 0.7ex;
}

#toggles span.toggle_text {
    display: none;
}

#chat_name_container {
    order: 1;
}

#chat_notifier {
    order: 2;
}

#chat_status {
    order: 3;
}


#gallery_title {
    font-size: small;
}

#main_small_links {
    background-color: #434343;
}

.top_nav_item,
.top_nav_item:hover,
.top_nav_item:visited,
body {
    color: rgb(231, 231, 231);
}

.chat_message .username .clickable:not(:hover) {
    color: inherit;
}

.chat_message .username {
    color: rgb(170, 170, 180);
    float: none;
}

.chat_message.user .username {
    color: #23d16f;
}

.chat_message.donator .username {
    color: #42ff96;
}

.chat_message.staff .username {
    color: #fc807e;
}

.chat_message.staff:not(.user) .username {
    color: #ff6c00;
}

.chat_message.admin .username {
    color: #d880fc;
}

.icon-image {
    color: lightskyblue;
}

.icon-film {
    color: yellowgreen;
}

.icon-book {
    color: lightsalmon;
}

.icon-archive {
    color: bisque;
}

.icon-music {
    color: orchid;
}

.chat_room_url {
    background-color: var(--hi-hover-color);
}

a.header_row_element_disabled,
a.header_row_element_disabled:active,
a.header_row_element_disabled:hover,
a.header_row_element_disabled:visited {
    color: #e0e0e0;
}

#radio_container {
    background-color: var(--main-bg-color);
}

#radio_current {
    background-color: #555555;
}

#radio_progress {
    background-color: #5e5e5e;
}

#radio_volume_bar,
#radio_load_progress {
    background-color: #535353;
}

#radio_volume_slider,
#radio_play_progress {
    background-color: #777777;
}

.file_right_part {
    text-align: right;
}

body:not(.mobile_filelist) .file_right_part {
    min-width: 166px;
}

body:not(.mobile_filelist) .file_status {
    min-width: 98px;
}

.file_buttons:before, .file_status:before {
	border-color: var(--main-bg-color) !important;
}

#uploadButton {
    font-size: 18px;
    padding: 0 .5em 1px .5em;
}

#clearsearch {
    padding-left: 0.6ex;
}

#call_to_action {
    color: #434343;
    background-image: url("data:image/svg+xml,%3C%3Fxml%20version%3D%221.0%22%20encoding%3D%22UTF-8%22%20standalone%3D%22no%22%3F%3E%0A%3Csvg%20xmlns%3Ardf%3D%22http%3A%2F%2Fwww.w3.org%2F1999%2F02%2F22-rdf-syntax-ns%23%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20height%3D%22300%22%20width%3D%22300%22%20version%3D%221.1%22%20xmlns%3Acc%3D%22http%3A%2F%2Fcreativecommons.org%2Fns%23%22%20xmlns%3Adc%3D%22http%3A%2F%2Fpurl.org%2Fdc%2Felements%2F1.1%2F%22%3E%0A%3Cmetadata%3E%0A%3Crdf%3ARDF%3E%0A%3Ccc%3AWork%20rdf%3Aabout%3D%22%22%3E%0A%3Cdc%3Aformat%3Eimage%2Fsvg%2Bxml%3C%2Fdc%3Aformat%3E%0A%3Cdc%3Atype%20rdf%3Aresource%3D%22http%3A%2F%2Fpurl.org%2Fdc%2Fdcmitype%2FStillImage%22%2F%3E%0A%3Cdc%3Atitle%2F%3E%0A%3C%2Fcc%3AWork%3E%0A%3C%2Frdf%3ARDF%3E%0A%3C%2Fmetadata%3E%0A%3Cg%20transform%3D%22translate(0%2C-752.375)%22%3E%0A%3Cpath%20fill%3D%22%23434343%22%20d%3D%22m22%2C752.38c-12.188%2C0-22%2C9.812-22%2C22v53h7v-53c0-8.31%2C6.69-15%2C15-15h53v-7h-53zm203%2C0%2C0%2C7%2C53%2C0c8.31%2C0%2C15%2C6.69%2C15%2C15v53h7v-53c0-12.188-9.812-22-22-22h-53zm-225%2C225%2C0%2C53c0%2C12.188%2C9.812%2C22%2C22%2C22h53v-7h-53c-8.31%2C0-15-6.69-15-15v-53h-7zm293%2C0%2C0%2C53c0%2C8.31-6.69%2C15-15%2C15h-53v7h53c12.188%2C0%2C22-9.812%2C22-22v-53h-7z%22%2F%3E%0A%3C%2Fg%3E%0A%3C%2Fsvg%3E%0A");
}

.page_admin_modlog table {
    table-layout: fixed;
    width: 100%;
}
.page_admin_modlog table th:nth-child(1) {
    max-width: 15%;
}
.page_admin_modlog table th:nth-child(3) {
    width: 56%;
}
.page_admin_modlog table th:nth-child(4) {
    width: 7ex;
}

#bottom_navigation_content {
    background-color: var(--main-bg-color);
}
}
