css:
@-moz-document domain("onics.me") {
/* Barra Izquierda */


.brz-main-nav-side {
    box-shadow: none!important;
    margin-top: 50px !important;
    z-index: 3;
    width: 250px;
    display: none;
    background: #2c3d4e;
}


/* color letras barra izquierda, color fondo */

body {
    color: #bfd2dd;
    margin: 0;
    padding: 0;
    background: #25303c;
    word-wrap: break-word;
    
}

/* tamaño */
html, body {
    font-size: 15px;
    line-height: 1.8;
}


/* tamaño barra superior */

element.style {
    border-radius: 1px !important;
    height: 50px !important;
}

/* color icono buscador */

.brz-text-grey, .brz-hover-text-grey:hover {
    color: #668199!important;
}

/* color nombrusuar y fondo */
.brz-white, .brz-hover-white:hover {
    color: #e3e3e3 !important;
    background-color: #2c3d4e!important;
}

/* color trend,tags */
.brz-light-grey, .brz-hover-light-grey:hover {
    color: #dededf !important;
    background-color: #2c3d4e!important;
}


/* tamaño nombusuar */
.brz-container {
    padding: 0.01em 18px;
}


/* linea izq estados */
hr {
    height: 0;
    border: 0;
    border-top: 1px solid #555e6e;
    margin: 1px 0;
}


/* bordes de actualizar estado */
.brz-card {
    border: 1px solid #25303c;
}


/* bordes de trends,tags */

.brz-border-light-grey, .brz-hover-border-light-grey:hover {
    border-color: #25303c!important;
}

.brz-margin {
    margin: 16px!important;
}

.brz-border {
    border: 1px solid #e3e3e3!important;
}



}
