css:
@-moz-document regexp("https?://www.google.([a-z]{2,3})(\.[a-z][a-z]?)?/imghp(.*)"), regexp("^https?\\:\\/\\/(www|ipv6).google.([a-z]{2,3})(.[a-z]{2})?(\\/\\?.*)?(\\/webhp((?!&q=).)*)?\\/?$"), regexp("https?://ipv6.google.com/imghp(.*)"), regexp("https?://ipv6.google.com/?") {
html{
    overflow-y: auto;
}
body {
    background-image: url(http://opkdjxaax.bkt.clouddn.com/61784598_p0.jpg) !important;
    background-repeat:no-repeat !important;
    background-position: center top !important;
    background-size: cover !important;
    background-attachment: fixed !important;
    font-family:"Microsoft YaHei" !important;
}
html,
body{
    height: 100% !important;
}
#s_foot_wrap,/*底栏*/
#fbar,/*底栏*/
#s_main/*频道*/{
	 background-color: rgba(255,255,255,0.6) !important;
}
#prm{
margin-top:210px;
}
#fbar{
    margin: 0px !important;
    padding-top: 0px !important;
    padding-bottom: 0px !important;
    border-top: 0px  !important;
    line-height: 35px !important;
}
#s_top_wrap,/*顶栏*/
#gb,/*顶栏*/
#s_mai/*频道*/{
	 background-color: rgba(255,255,255,0.6) !important;
}
#gb{
    margin-top: 125px !important;
    padding-top: 5px !important;
    padding-bottom: 5px !important;
}
/*调整搜索框位置*/
.jhp{
    top: 170px !important;
    bottom: auto !important;
}
.jhp .tsf{
    top: 0 !important;
}
#hplogo{
    /*display: none !important;*/
    margin-top: -130px !important;
}
}

@-moz-document regexp(".*www.google.com(.*)") {
/*滚动条*/
::-webkit-scrollbar{
    margin-right: -10px !important;
    width: 6px;
    height: 6px;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-track{
    -webkit-box-shadow: 0 0 3px rgba(0,0,0,0.3) inset;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-thumb{
    border-radius: 3px;
    -webkit-box-shadow: 0 0 3px rgba(0,0,0,.3) inset;
    background-color: #999;
}
::-webkit-scrollbar-thumb:hover{
    background-color: #777;
}
::-webkit-scrollbar-thumb:active{
    background-color: #555;
}
}
