css:
@-moz-document domain(wanikani.com) {
.forum-topics-list,
.blog,
.recent-unlocks,
.low-percentage,
.recent-retired {
    display: none;
}
.dashboard section.review-status span,
.dashboard section.review-status time {
    font-size: 18pt;
}
.dashboard .lattice-single-character {
    background-image: none;
}
.dashboard .lattice-single-character ul {
    display: block;
}
.additional-info h3,
.small-caps {
    display: none;
}
.dashboard section.srs-progress span {
    font-size: 14pt;
    display: inline;
}
div.chart {
    display: none;
}
.dashboard section.progression {
    padding: 8px 16px;
    background-color: inherit;
}
.dashboard section.srs-progress ul li {
    box-shadow: none;
    padding: 8px 8px 8px;
    border-radius: 0 0 0 0 !important;
    -webkit-border-radius: 0 0 0 0 !important;
}
#reviews-summary #incorrect .apprentice.active, #reviews-summary #incorrect .guru.active, #reviews-summary #incorrect .master.active, #reviews-summary #incorrect .enlightened.active, #reviews-summary #incorrect .burned.active, #reviews-summary #correct .apprentice.active, #reviews-summary #correct .guru.active, #reviews-summary #correct .master.active, #reviews-summary #correct .enlightened.active, #reviews-summary #correct .burned.active {
    display: none !important;
}
#correct {
    width: 50%;
    margin-top: -84px;
}
#reviews-summary #correct {
    box-shadow: 0px 3px 0 #e1e1e1;
}
#incorrect {
    width: 50%;
    margin-left: 50%;
}
.ui, .ui-small {
    margin: 10px !important;
    background: #f4f4f4 !important;
    box-shadow: 3px 3px #e7e7e7 !important;
    color: #888888 !important;
    border-radius: 0 !important;
}
.sortable item {
    border: none !important;
    background-color: #fbfbfb !important;
}
.radical, .kanji, .vocabulary {
    background-color: #fbfbfb !important;
}
#reorder {
    color: #888888 !important;
}
#overlay {
    background-image: none !important;
}
.icon-plus {
    margin-top: 4px !important;
}
#question #character {
    font-size: 8em !important;
    line-height: 2em !important;
}
}
