css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
  html, body { background: #000C17 !important; color: #777 !important; }
  a, a:link, a:visited { color: #00ACAF !important; }
  h4 a:link, h4 > a:visited { color: #00ACAF !important; }
  #nav-view { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  h1, h2, h3, h4 { color: #777 !important; }
  input[type="text"] { border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  select { background: none !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  .see_more { background-color: transparent !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  .see_more:hover { background-color: #000C67 !important; }
  
  ::-moz-selection { color: #00ACAF !important; background-color: #000C87 !important; }
  
  #header { background: #000C37 none !important; }
  #search_input { border: 1px solid #00ACAF !important; background: none !important; }
  #input_text_search::-moz-placeholder { color: #777 !important; }
  #search_input[style="background-color: white;"] { background-color: transparent !important; }
  #input_text_search { color: #aaa !important; font-weight: normal !important; border: none !important; }
  
  #navigation { background-color: #000C17 !important; border-right: 1px dashed #00ACAF !important; }
  .navigation_title { color: #777 !important; }
  .navigation_link { box-shadow: none !important; }
  .navigation_link:hover { background-color: #000C67 !important; }
  
  .style_card { background: #000C37 !important; }
  
  pre, code { background-color: #000C37 !important; border: 1px dashed #00ACAF !important; }
  pre *, code * { color: white !important; }
  
  /* ======== style page ======== */
  #notes_textarea { background: transparent none !important; color: #777 !important; border: 1px solid #00ACAF !important; }
  #send_feedback_button, #css_button, .white_button {
    background-color: transparent !important;
    border: 1px solid #00ACAF !important;
    color: #00ACAF !important; 
  }
  #send_feedback_button:hover, #css_button:hover, .white_button:hover { background-color: #000C67 !important; }
  #headline { color: #00ACAF !important; }
  
  #information_key_left, #infomation_value_left, #information_key, #information_value, #share_div,
  #applies_link, .text_style_page { color: #777 !important; }
  textarea#stylish-code { font-family: "Courier New" !important; }
  
  .errorExplanation { background: #500 !important; }
  
  /* ======== forum ======== */
  #Head { background-color: #000C37 !important; }
  input.DateBox, input.InputBox, input.SmallInput, textarea {
    background-color: transparent !important;
    border: 1px solid #00ACAF !important;
    color: #777 !important; 
  }
  
  .DataList.Discussions li.Item:hover { background-color: #000C47 !important; }
  
  .Button { box-shadow: none !important; text-shadow: none !important; }
  a.SignInPopup, a.ApplyButton, a.ClearConversation, a.NewConversation, input#Form_Add.Button.Action, #Form_PostComment,
  a.NewDiscussion, #Form_SavePreferences, #Form_Upload, #Form_Save, .NavButton, .PreviewButton, .DraftButton, .WriteButton {
    background: transparent none !important;
    border-color: #00ACAF !important;
    color: #00ACAF !important; 
  }
  a.SignInPopup:hover, a.ApplyButton:hover, a.ClearConversation:hover, #Form_SavePreferences:hover, #Form_Save:hover,
  a.NewConversation:hover, input#Form_Add.Button.Action:hover, a.NewDiscussion:hover, #Form_Upload:hover, .NavButton:hover,
  .PreviewButton:hover, .DraftButton:hover, #Form_PostComment:hover, .WriteButton:hover {
    background-color: #000C67 !important;
  }
  ul.token-input-list { background: #000C37 none !important; border-color: #00ACAF !important; }
  div.token-input-dropdown { background: #000C17 !important; border-color: #00ACAF !important; }
  
  .Sprite16, .SiteSearch .Button {
    background-image: url("http://i.imgur.com/uEUv93l.png") !important;
    top: 8px !important;
    right: 6px !important;
  }
  .OptionsTitle, a.Bookmark, a.Bookmarked, a.Bookmarking {
    background-image: url("http://i.imgur.com/Kv3qBr8.png") !important;
  }
  .MenuItems { background: #000C17 !important; border-color: #00ACAF !important; }
  .FlyoutMenu { background-color: #000C17 !important; border-color: #00ACAF !important; color: #777 !important; }
  .Flyout::after { border-bottom-color: #00ACAF !important; top: -7px !important; }
  .MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active { color: #00ACAF !important; }
  .FlyoutMenu ul.PopList.Conversations li:hover { background-color: #000C67 !important; }
  .MenuItems hr { border-color: #00ACAF !important; }
  
  
  /* thread */
  .MessageList.Discussion { border: 1px dashed #00ACAF !important; }
  .Comments .ItemComment:hover { background-color: #000C47 !important; }
  
  #footer { background: #000C37 !important; color: #777 !important; }
  #copyrights { color: #777 !important; }
  
  /* settings */
  table.PreferenceGroup tr:hover td { background: #000C47 !important; }
  .WarningMessage { background-color: #300 !important; color: #00ACAF !important; }
  
  /* profile */
  .Item.Read { background-color: transparent !important; }
  .SpEditProfile { background-image: url("http://i.imgur.com/PBQxJBm.png") !important; }
  .NavButton { box-shadow: none !important; font-weight: normal !important; }
  .DataList.SearchResults li:hover { background-color: #000C47 !important; }
  .ReactSprite { background-image: url("http://i.imgur.com/PBQxJBm.png") !important; }
  
  /* new thread */
  .EmoticonBox { background-color: #000C17 !important; }
  .EmotifyDropdownActive { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  .Preview { background: none !important; color: #777 !important; border: 1px solid #00ACAF !important; }
}
