css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("domain.tld") {

/* ^^ Change the domain above ^^ */


  body { font-family: "tahoma","arial","helvetica","sans-serif";

background: #253960 url("data:image/png;base64,") top left repeat-x !important;

    border: none !important;
    font-size: 8pt !important;
    color: rgb(0, 0, 0) !important;
    margin-top: 0px !important;
    margin-bottom: 0px !important;
     -moz-border-radius: 7px !important;
    }

  table { border: none !important;
    border-collapse: collapse !important;
    background-color: rgb(102, 102, 102) !important;
    -moz-border-radius: 7px !important;
    }

input, select, textarea {
background-color: #999 !important;
border: 2px solid !important;
    -moz-border-radius: 6px !important;
}

  table.main, table.mainouter { background-repeat: no-repeat !important;
    border: none !important;
    background-color: rgb(151, 153, 153) !important;
    -moz-border-radius: 7px !important;
    }

  table.bottom { background: transparent none repeat scroll 0% !important;
    border: none !important;
    }

  h1 { font-size: 14pt !important;
    }

  h2 { font-size: 10pt !important;
    margin-bottom: 6px !important;
    }

  p { font-size: 8pt !important;
    }

  p.sub { margin-bottom: 4pt !important;
    }

  td { border: 1px solid rgb(51, 51, 51) !important;
    border: none !important;
    font-size: 8pt !important;
    -moz-border-radius: 7px !important;
    }

  td.embedded { border: none !important;
    border: none !important;
    padding: 2px !important;
    text-align: left  !important;
    }

  td.bottom { border: none !important;
   -moz-border-radius: 7px !important;
    }

  td.heading { font-weight: bold !important;
   border: none !important;
   -moz-border-radius: 7px !important;
    }

  td.main2 { border: none !important; }

  td.text { padding: 10pt !important;
    border: none !important;
    text-align: left !important;
     -moz-border-radius: 7px !important;
    }

  td.comment { padding: 10pt !important;
    border: none !important;
    font-size: 8pt !important;
    text-align: left !important;
     -moz-border-radius: 7px !important;
    }

  td.colhead { font-weight: bold !important;
    color: rgb(241, 241, 241) !important;
    background-color: rgb(102, 102, 102) !important;
     -moz-border-radius: 7px !important;
    }

  td.rowhead { font-weight: bold !important;
    text-align: right !important;
    vertical-align: top !important;
     -moz-border-radius: 7px !important;
    }

  td.title { font-size: 14pt !important;
    border: none !important;
    -moz-border-radius: 7px !important;
    }

  td.navigation { border: medium none ;
    border: none !important;
    font-weight: bold !important;
    font-size: 10pt !important;
     -moz-border-radius: 7px !important;
    }

  td.clear { border: 0px none ;
    padding: 0px !important;
    background: transparent none repeat scroll 0% !important;
    }

  form { margin-top: 0pt !important;
    margin-bottom: 0pt !important;
    }

  .sublink { font-style: italic !important;
    font-size: 7pt !important;
    font-weight: normal !important;
    }

  font.gray { text-decoration: underline !important;
    color: rgb(0, 0, 0) !important;
    }

  a:link, a:visited { text-decoration: underline !important;
    color: rgb(51, 51, 51) !important;
    }

  a:hover { color: rgb(153, 0, 0) !important;
    }

  a.index { font-weight: bold !important;
    }

  a.biglink { font-weight: bold !important;
    font-size: 12pt !important;
    }

  a.altlink:link, a.altlink:visited { font-weight: bold !important;
    color: rgb(168, 56, 56) !important;
    text-decoration: none !important;
    }

  a.altlink:hover { text-decoration: underline !important;
    }

  .important { font-weight: bold !important;
    font-size: 8pt !important;
    }

  .red { color: rgb(153, 0, 0) !important;
    }

  .yellow { color: rgb(153, 119, 0) !important;
    }

  .green { color: rgb(0, 0, 0) !important;
    }

  input, select, textarea { font-family: "tahoma","arial","helvetica","sans-serif";
    font-size: 8pt !important;
    }

  .btn { width: 75px !important;
    height: 23px !important;
    }

  .small { font-size: 7pt !important;
    }

  .big { font-size: 10pt !important;
    }

  li { margin-top: 6pt !important;
    margin-bottom: 6pt !important;
    }

  ul { margin-left: 16pt !important;
    margin-top: 0px !important;
    margin-bottom: 0px !important;
    }

  img.border { border: none !important;
    }

}
