css:
@-moz-document domain("sadeemapk.com") {
.header-inner,
#colophon,
#social-icons-3,
#blog_subscription-2,
#facebook-likebox-2,
#text-5,
#redwaves_popular_posts_widget-2,
#redwaves_recent_posts_widget-2,
#archives-2,
#categories-2,
#text-3,
.category-box,
.page-title,
.related_posts {
    display: none !important;
}
/* -- Where the Magic Happens -- */

body,
article,
.screenshots,
.wpappbox,
.screenshots-only,
.slider,
.comment-box,
#related_posts > ul {
    background: #1a1a18;
    color: white;
}
.post-box,
.small-post-box,
.horizontal-container {
    background: #1a1a18;
    margin: 0;
}
#main {
    margin-top: 20px;
}
article,
.sidebar-widget,
.related-posts .horizontal-container,
.author-box,
.error404 .content-area {
    box-shadow: none;
    border-style: solid;
    border-width: 1px;
    border-color: #30302F;
}
html:hover #secondary {
    opacity: .7;
    transition-delay: 0s !important;
}
#secondary {
    margin-left: 100px;
    position: fixed;
    top: -27px;
    z-index: 10002;
    opacity: 0;
    transition: all 1s ease, transform 50ms ease;
    left: 30%;
    transition-delay: 2s;
    width: 200px!important;
    background-color: rgba(0, 0, 0, 0)!important;
}
#content {
    /* Setup Flexbox */
    
    display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    /* Reverse Column Order */
    
    -webkit-flex-flow: column-reverse;
    flex-flow: column-reverse;
}
/* -- Styling Only -- */

#content > div {
    width: 100%;
}
#content > div:last-of-type {
    width: 100%;
}
.secondary-navigation,
.secondary-navigation li:hover ul a,
.secondary-navigation ul ul li,
.secondary-navigation ul ul li:hover,
.secondary-navigation ul ul ul li:hover,
.secondary-navigation ul ul ul li,
#mobile-menu-wrapper,
a.sideviewtoggle,
.sb-slidebar {
    background: #1c1c1c;
}
.entry-content > div >:nth-child(2),
.entry-content >:nth-child(1),
div.robots-nocontent.sd-block.sd-social.sd-social-icon.sd-sharing {
    display: none !important;
}
.entry-title {
    color: white;
}
*,
*:before {
    box-sizing: border-box;
    float: none;
}
}
