css:
@-moz-document url-prefix("https://www.paidverts.com/member/paid_ads_interaction_") {
body{
	background:blue;
}
.drop-menu ul {
    background: #fff;
    position: fixed;
    text-align: center;
    left: 14%;
    top: 7%;
    width: 126px;
}
div img {
    width: 100px;
}
.buy6 {
	display:none;
    width: 428px;
    font-size: 9px;
    color: #32c75f;
    text-align: left;
    line-height: 28px;
    padding: 5px 5px 5px 15px;
    height: 28px;
    border: 1px solid #32c75f;
    font-weight: 600;
    background-color: #fef8b3;
    margin: 0 0 6px 0;
}
input.buy5[type="text"] {
    width: 364px;
    font-size: 9px;
}
.button_green_submit {
    margin: 10px 0 2px 0;
    padding: 4px 30px 4px 30px;
}
.button_green_submit, .button_green_submit2 {
    position: relative;
    text-shadow: none;
    cursor: pointer;
    font-size: 16px;
    border: 2px solid #32c75f;
    color: white;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    background-color: #32c75f;
    display: inline-block;
    -webkit-transition: all 0.2s ease-out;
    -moz-transition: all 0.2s ease-out;
    -o-transition: all 0.2s ease-out;
    -ms-transition: all 0.2s ease-out;
}
}

@-moz-document url-prefix("http://www.paidverts.com/member/paid_ads_view_") {
body{
	background:red;
}
div .top_bar{
	background:rgba(255,255,255,0);
}
}

@-moz-document url("https://www.paidverts.com/member/home.html") {
div .member_top div.style{
	background:rgba(255, 255, 255, 0);
	left:1px;
	position:fixed;
}
}

@-moz-document url-prefix("https://www.paidverts.com/member/activation_ad") {
body{
	background: #000;
}
}

@-moz-document url-prefix("https://www.paidverts.com/member/paid_ads.html") {
body{
	background:#000;
}
.drop-menu ul {
    background:#22b14c;
    position: fixed;
    text-align: center;
    left: 14%;
    top: 7%;
    width: 126px;
}
.drop-menu ul li {
    font-family: 'Open Sans', sans-serif;
    font-weight: 600;
    font-size: 15px;
    color: #ffffff;
    display: inline-block;
    position: relative;
    padding: 15px 7px;
    background: rgba(255, 255, 255, 0);
    cursor: pointer;
    -webkit-transition: all 0.2s;
    -moz-transition: all 0.2s;
    -ms-transition: all 0.2s;
    -o-transition: all 0.2s;
    transition: all 0.2s;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
}
.drop-menu ul li ul {
    padding: 0;
    position: absolute;
    top: 2px;
    left: -126px;
    display: none;
    opacity: 0;
    visibility: hidden;
    border: 1px solid #33c75e;
    background: #22b14c;
}
.drop-menu ul li ul li {
    background: #22b14c;
    display: block;
    color: #ffffff;
}
.drop-menu ul li a:hover {
    color: #00ff4a;
}
}

