css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("skytorrents.in") {

  body {
    font-weight: 300;
    font-family: Roboto, sans-serif;
    
  }
  
  .button.is-primary {
    background-color: #777;
    border-color: transparent;
    color: #fff;
}
  .button.is-primary:hover, .button.is-primary.is-hovered {
    background-color: #555;
    border-color: transparent;
    color: #fff;
}
  .input:focus, .input.is-focused, .input:active, .input.is-active, .textarea:focus, .textarea.is-focused, .textarea:active, .textarea.is-active {
    border-color: #777;
}
  .hero.is-light .nav {
    box-shadow: 0 0 0 0;
}
  .hero.is-light {
    background-color: #375661;
}
  .hero.is-light .nav:before {
    content: "SkyTorrents";
    height: 100px;
    width: 100%;
    font-size: 74px;
    color: #fff;
    font-family: Roboto, sans-serif;
    font-weight: 300;
    text-align: center;
    display: block;
    margin-left: 85px;
    margin-top: 45px;
      
}
  .hero.is-light .subtitle, .content, .content h3 {
    
    color: #777;
}
  .hero.is-light .subtitle, .content {
    
    margin-top: 25px;
}
  
  .content h2 {
    visibility: hidden;
}
  a[href^="https://www.skytorrents.in/rss/all/ad/1/"]{
    margin-left:-50px;
    visibility: hidden;
  }
  
  div.container > div.content.has-text-centered > a[href^="/search/all/ed/"]{
    margin-left:5px;
    
  }
  
  div.hero-foot:nth-child(1) > div:nth-child(1) > div:nth-child(1) > h3:nth-child(1) {
    font-variant: small-caps;
  }
  .hero-foot > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > h3:nth-child(2) {
    font-size-adjust: 0.4;
    color: #fff;
  }
  html body.is-light section.hero.is-light.is-fullheight div.hero-head div.container.has-text-centered.is-centered h4.subtitle.is-4 {
    color:#fff;
    
  }
  .hero-foot > div:nth-child(1) > div:nth-child(1) > h3:nth-child(2) > b:nth-child(1) {
    font-variant: small-caps;
    
  }
  
  .nav {
    min-height: 5.5rem;
    background-color: #375661;
}
  .nav-center {
    margin-top: 12px;
}
  
  .nav-left:after {
    content: "SkyTorrents";
    height: 0;
    width: 100%;
    font-size: 32px;
    color: #fff;
    font-family: Roboto, sans-serif;
    font-weight: 300;
    text-align: center;
    display: block;
    margin-left: -40%;
    margin-top: 15px;
  }
  
 .nav-right {
   visibility: hidden;
  }

  .nav-item a.is-brand, a.nav-item.is-brand, a.nav-item:nth-child(2) {
    visibility: hidden;
}
 a {
    border-bottom: none;
    color:  #84b15f;
    font-weight: normal;
    text-decoration: none;
   font-family: Roboto, sans-serif;
   
}
  a:active, a:focus, a:hover {
    border-bottom: thin solid;
    border-bottom-width: thin;
    color: #84b15f;
}
  a:visited {
    color: #84b15f;
    
  }
  .table tr:hover, .table.is-striped tbody tr:nth-child(2n):hover {
    background-color: #f2f2f2;
}
  .table.is-striped tbody tr:nth-child(2n) {
    background-color: #ffffff;
}
  
  .table td, .table th {
    border: none;
    border-width: 0 0 0;
    padding-bottom: 20px !important;
    vertical-align: middle;
}
  .button.is-success {
    
    background-color:#375661;
}

  
}
