css:
@-moz-document domain("intranet.cxo.name") {
#logo img {
    display:none;
}
#logo a {
    background-image:url(https://upload.wikimedia.org/wikipedia/commons/thumb/3/32/Logo-chip-online-newred.svg/320px-Logo-chip-online-newred.svg.png);
    background-repeat:no-repeat;
    width:100px;
    background-size:100%;
    background-position:0 5px;
}
.aui-header {
    background-color: rgb(235, 68, 63);
    ;
}
.aui-header .aui-dropdown2-trigger {
    color:#fff;
}
.aui-header .aui-dropdown2-trigger:hover, .aui-header .aui-dropdown2-trigger:focus, .aui-header .aui-dropdown2-trigger.active, #logo a:hover {
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.3) inset;
    border-radius:3px;
    background-color: rgb(235, 50, 50);
    color:white;
}
.aui-header .aui-button.aui-button-primary.aui-style {
    background-color:#3b73af;
}
.aui-header .aui-button.aui-button-primary.aui-style:hover {
    background-color:#2b639f;
}
.active {
    background-color: rgb(235, 68, 63);
}
.dashboard-item-header {
    background:#3b73af !important
}
.active .headerrow-priority {
    background:#ddd;
}
.aui-dropdown li.dropdown-item.active a.item-link:hover, .aui-dd-parent .aui-dropdown li.dropdown-item.active a.item-link:hover, .aui-dropdown a.item-link:hover, .aui-dd-parent .aui-dropdown a.item-link:hover, .aui-dropdown li.dropdown-item.active a.item-link:focus, .aui-dd-parent .aui-dropdown li.dropdown-item.active a.item-link:focus, .aui-dropdown a.item-link:focus, .aui-dd-parent .aui-dropdown a.item-link:focus, .aui-dropdown li.dropdown-item.active a.item-link:active, .aui-dd-parent .aui-dropdown li.dropdown-item.active a.item-link:active, .aui-dropdown a.item-link:active, .aui-dd-parent .aui-dropdown a.item-link:active {
    background-color: rgb(235, 68, 63);
    color: #fff;
}
.ghx-issue-compact .ghx-plan-extra-fields.ghx-plan-extra-fields.ghx-row {
    margin-top: -26px;
    margin-left: 5px;
}
.ghx-issue-compact .ghx-plan-extra-fields.ghx-plan-extra-fields.ghx-row .ghx-extra-field {
 
    background: #80808050;
    border-radius: 26px;
    text-align: center;
    min-width: 20px;
}

.ghx-issue-compact .ghx-row:not(.ghx-end):not(.ghx-plan-extra-fields) {
 
    padding-left: 11px;
}



.issue-body-content .module+.module {
 
    background: white;
}

@font-face { font-family: chipfont; 
			 src: url('https://www.chip.de/fec/assets/font/MarselisSlabWeb.woff2'); } 


 


#summary-val {
    font-size: 27px;
    font-family: chipfont;
}



.active {
    background-color: rgba(235, 68, 63,0.3);
}

.issue-body-content .module {
 
    background: linear-gradient(0deg, #f0f0f0, transparent);
}
}
