css:
@-moz-document domain("postnauka.ru") {
/* COLOR GRID:
    Blue Backgrounds:
        - #15161a hsb(222, 18, 10)
        - #1f2226 hsb(218, 18, 15)
        - #2a2e33 hsb(214, 18, 20)
        - #343a40 hsb(210, 18, 25)
        - #3f474d hsb(206, 18, 30)
        - #495359 hsb(202, 18, 35)
		- #546066 hsb(198, 18, 40)
		- #5e6e73 hsb(194, 18, 45)
		- #92aab3 hsb(196, 18, 70)

    Accent:
        - #e5ae5c hsb(36, 60, 90)
        - #f2bd61 hsb(38, 60, 95);

    Foreground:
        - #e9f2f2 hsb(180, 4, 95)
        - #dce5e5 hsb(184, 4, 90)
        - #d0d7d9 hsb(188, 4, 85)
        - #c4cacc hsb(192, 4, 80)
        - #b8bdbf hsb(196, 4, 75)
*/

:root {
    --color_bg1: #15161a;
    --color_bg2: #1f2226;
    --color_bg3: #2a2e33;
    --color_bg3_95p: rgba(42, 46, 51, 0.95);
    --color_bg4: #343a40;
    --color_bg5: #3f474d;
    --color_bg6: #495359;
    --color_bg7: #546066;
    --color_bg8: #5e6e73;
    --color_bg9: #92aab3;
    
    --color_bg_accent1: #3a3833;
    --color_bg_accent2: #47453f;
                                            
    --color_accent1: #e5ae5c;
    --color_accent1_op50p: rgba(229, 174, 92, 0.75);
    --color_accent2: #f2bd61;
    
    --color_fg1: #e9f2f2;
    --color_fg2: #dce5e5;
    --color_fg3: #d0d7d9;
    --color_fg4: #c4cacc;
    --color_fg5: #b8bdbf;
}

.main-page {
    background-color: var(--color_bg2);
}

.notification__wrapper {
	background-color: var(--color_bg_accent1);
}

.notification__wrapper {
	border-color: var(--color_bg_accent2);
}

.m-menu,
.m-menu__links,
.m-menu__right,
#post-list-more,
.post__content__person,
.m-menu__left,
.m-sec-level,
.grid__type__video,
.grid__content,
.skoltech-future--description,
.materials .materials__alphabet,
.post__content__inf__views p {
	background-color: var(--color_bg3);
}

.skoltech-future__courses--grid:after {
    background: linear-gradient(to top, #343a40, rgba(0,0,0,0));
}

.m-menu__links__search__input input,
.grid__type__video:hover,
.post__feedback__radio input + span {
    background-color: var(--color_bg4);
}

.m-menu__links__link:hover,
.post__content__person:hover {
    background-color: var(--color_bg5);
}

hr {
    background-color: var(--color_bg7);
}

.m-sec-level__controls .m-menu__links__search__input input {
	outline: var(--color_bg5) solid thin;
}

.m-menu__login,
.post__content__person,
.grid-update__button,
.post__container,
.post__feedback__radio input + span {
	border-color: var(--color_bg5);
}

.m-sec-level__controls .m-menu__links__search__input input:focus {
	outline: var(--color_bg7) solid thin;
}

h1, .h1, h2, .h2, h3, .h3, .donate__title, .donate__report .h2, .about__header__h1, .work__header__h1, .post__content h5, .post__content__h1, .post__content h3, .post__content h4, .post__content__post__title, .post__content__person__name, .author__name {
    color: var(--color_fg4);
}

nav .btn__close__symbol,
nav .btn__close__symbol:after,
nav .btn__search__symbol:after {
	background-color: var(--color_fg4);
}

nav .btn__search__symbol {
	border: 2px solid var(--color_fg4);
}

.post__content__inf__date,
.post__content__inf__social,
.post__content__subheader,
.m-sec-level__controls .m-menu__links__search__input input,
.m-menu__links__link, .m-menu__links__link i {
    color: var(--color_fg3);
}

.post__content__subheader,
.post__content,
p, .p, .infra__container.credits > div > div:first-child, .infra__container.credits > div > div:last-child,
.post__feedback__title, additional-posts__header, .additional-posts__header,
.grid__type__video figcaption .grid__title,
.grid__content .grid__rate,
.post__content__post__explanation,
.grid-update__button,
.skoltech-future__footer__info h2,
.skoltech-future__footer__info p,
.materials .materials__alphabet li a, .materials .materials__alphabet li a:visited, .materials .materials__alphabet li span,
.materials .materials__title,
.post__feedback__radio:hover input + span .svg__icon {
    color: var(--color_fg5);
}

.post__content__inf__social__icon path,
.grid__content .grid__rate svg g path,
.post__feedback__radio input + span .svg__icon,
.post__content__inf__views svg path, .post__content__inf__views svg circle,
.post__feedback__radio input + span svg, .post__feedback__radio input + span svg .svg__icon {
    fill: var(--color_fg3);
}

.m-sec-level__theme-item__caption__name,
.m-sec-level__text-item__header,
.post__global__read-more__title {
    color: var(--color_fg4);
}

.m-sec-level__theme-item,
.m-sec-level__theme-item__more a,
.grid__type__theme-micro,
.post__metadata-text,
.post__content__inf__views span {
    color: var(--color_fg3);
}

.grid__type__theme-micro__caption__name,
.grid__type__project .grid__title {
	color: var(--color_bg9);
}
}

@-moz-document domain("postnauka.ru") {
/* LAYOUT */

.post__global__read-more {
	border: none;
}

#post-list-more,
.post__content__post__explanation {
	border-bottom: 1px solid var(--color_bg5);
    border-top: 1px solid var(--color_bg5);
}

.grid__container.border, .special-grid-wrapper.border, additional-posts__container.border, .additional-posts__container.border {
    border: none;
}

p, .p, .infra__container.credits > div > div:first-child, .infra__container.credits > div > div:last-child,
.post__global__read-more__article a {
	text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.2);
}

.skoltech-future__courses--show-more {
	margin-top: -78px;
}
}

@-moz-document domain("postnauka.ru") {
/* FONT */

@font-face {
    font-family: 'Lato';
    font-style: normal;
    font-weight: 300;
    src:    local('Lato Light'), 
            url(https://apushkarev.github.io/font/Lato-Light.woff2) format('woff2');
}

@font-face {
    font-family: 'Lato';
    font-style: italic;
    font-weight: 300;
    src:    local('Lato Light Italic'), 
            url(https://apushkarev.github.io/font/Lato-LightItalic.woff2) format('woff2');
}

@font-face {
    font-family: 'Lato';
    font-style: normal;
    font-weight: 400;
    src:    local('Lato Regular'), 
            url(https://apushkarev.github.io/font/Lato-Regular.woff2) format('woff2');
}

@font-face {
    font-family: 'Lato';
    font-style: italic;
    font-weight: 400;
    src:    local('Lato Italic'), 
            url(https://apushkarev.github.io/font/Lato-Italic.woff2) format('woff2');
}

@font-face {
    font-family: 'Lato';
    font-style: normal;
    font-weight: 700;
    src:    local('Lato Bold'), 
            url(https://apushkarev.github.io/font/Lato-Bold.woff2) format('woff2');
}

@font-face {
    font-family: 'Lato';
    font-style: italic;
    font-weight: 700;
    src:    local('Lato Bold Italic'), 
            url(https://apushkarev.github.io/font/Lato-BoldItalic.woff2) format('woff2');
}

* {
	font-family: Lato !important;
}

.post__content__data > .ng-scope > p,
.post__content__subheader {
	font-weight: 300;
    line-height: 1.6em;
}
}
