css:
@-moz-document domain("www.youtube.com") {
.yt-live-chat-header-renderer-0 {
	background-color: #1e1e1e;
	color: #707070;
}
.yt-live-chat-header-renderer-0 #action-buttons.yt-live-chat-header-renderer > *.yt-live-chat-header-renderer, .yt-live-chat-header-renderer-0 #overflow.yt-live-chat-header-renderer {
	color: #707070;
}
.yt-live-chat-ticker-renderer-0 {
	background-color: #1e1e1e;
}
.yt-live-chat-ticker-renderer-0 #left-arrow-container.yt-live-chat-ticker-renderer {
	background: linear-gradient(to right, hsl(0, 0%, 15%) 0, hsl(0, 0%, 15%) 52px, transparent 60px);
}
.yt-live-chat-ticker-renderer-0 #right-arrow-container.yt-live-chat-ticker-renderer {
	background: linear-gradient(to left, hsl(0, 0%, 15%) 0, hsl(0, 0%, 15%) 52px, transparent 60px);
}
.yt-live-chat-pinned-message-renderer-1 #fade.yt-live-chat-pinned-message-renderer {
	background-color: hsla(0, 0%, 0%, 0.6);
}
.yt-live-chat-renderer-0 {
	background: #1e1e1e;
	color: #ffffff;
}
.yt-live-chat-text-message-renderer-0 #message.yt-live-chat-text-message-renderer {
	color: #d2d2d2;
}
.yt-live-chat-text-message-renderer-0 #timestamp.yt-live-chat-text-message-renderer {
	color: #7b7b7b;
}
.yt-live-chat-text-message-renderer-0 #menu.yt-live-chat-text-message-renderer {
	color: hsla(0, 0%, 92%, 0.6);
	background: linear-gradient(to right, transparent, hsl(0, 0%, 10%) 100%);
}
.yt-live-chat-text-message-renderer-0 #deleted-state.yt-live-chat-text-message-renderer, .yt-live-chat-text-message-renderer-0 #show-original.yt-live-chat-text-message-renderer, .yt-live-chat-text-message-renderer-0[is-deleted] #message.yt-live-chat-text-message-renderer {
	color: #888888;
}
.yt-live-chat-restricted-participation-renderer-0 #container.yt-live-chat-restricted-participation-renderer {
	background: #1e1e1e;
	color: #5d5d5d;
}
.yt-live-chat-icon-toggle-button-renderer-0 paper-icon-button.yt-live-chat-icon-toggle-button-renderer {
	color: #888888;
}
.yt-live-chat-message-buy-flow-renderer-5 {
	background-color: hsla(0, 0%, 24%, 0.4);
	color: hsl(0, 0%, 77%);
}
.yt-live-chat-message-buy-flow-renderer-5 #info.yt-live-chat-message-buy-flow-renderer,
.yt-live-chat-message-buy-flow-renderer-5 .dim.yt-live-chat-message-buy-flow-renderer,
.yt-live-chat-message-buy-flow-renderer-5 #price-input-container.yt-live-chat-message-buy-flow-renderer paper-input.yt-live-chat-message-buy-flow-renderer  #input {
	color: hsl(0, 0%, 77%);
}
.yt-live-chat-text-message-renderer-0 #content.yt-live-chat-text-message-renderer {
	color: hsl(0, 0%, 100%);
}
.yt-endpoint-3 {
	color: hsl(0, 0%, 100%);
}
.yt-live-chat-message-input-renderer-0 {
	background: hsl(0, 0%, 12%);
}
.yt-live-chat-message-input-renderer-0 #author-name.yt-live-chat-message-input-renderer {
	color: hsla(0, 0%, 100%, 0.6);
}
.yt-live-chat-text-input-field-renderer-0 #label.yt-live-chat-text-input-field-renderer {
	color: hsla(0, 0%, 100%, 0.6);
}
.yt-live-chat-message-input-renderer-0 #count.yt-live-chat-message-input-renderer {
	color: hsla(0, 0%, 100%, 0.6);
}
.ytd-button-renderer-1[is-icon-button] {
	color: hsla(0, 0%, 100%, 0.4);
}
.yt-live-chat-icon-toggle-button-renderer-0 paper-icon-button[active].yt-live-chat-icon-toggle-button-renderer {
	color: hsl(0, 0%, 100%);
}
.yt-emoji-picker-category-button-renderer-0 paper-icon-button[active].yt-emoji-picker-category-button-renderer {
	color: hsl(0, 0%, 100%);
}
.yt-emoji-picker-category-button-renderer-0 paper-icon-button.yt-emoji-picker-category-button-renderer {
	color: hsla(0, 0%, 100%, 0.4);
}
/* square avatars */
.yt-live-chat-text-message-renderer-0 #author-photo.yt-live-chat-text-message-renderer,
.yt-live-chat-legacy-paid-message-renderer-0 #author-photo.yt-live-chat-legacy-paid-message-renderer,
.yt-live-chat-paid-message-renderer-1 #author-photo.yt-live-chat-paid-message-renderer > *.yt-live-chat-paid-message-renderer,
.yt-live-chat-paid-message-renderer-2 #author-photo.yt-live-chat-paid-message-renderer > *.yt-live-chat-paid-message-renderer,
.yt-live-chat-paid-message-renderer-3 #author-photo.yt-live-chat-paid-message-renderer > *.yt-live-chat-paid-message-renderer,
.yt-live-chat-paid-message-renderer-4 #author-photo.yt-live-chat-paid-message-renderer > *.yt-live-chat-paid-message-renderer,
.yt-live-chat-paid-message-renderer-5 #author-photo.yt-live-chat-paid-message-renderer > *.yt-live-chat-paid-message-renderer,
.yt-live-chat-paid-message-renderer-6 #author-photo.yt-live-chat-paid-message-renderer > *.yt-live-chat-paid-message-renderer,
.yt-live-chat-paid-message-renderer-7 #author-photo.yt-live-chat-paid-message-renderer > *.yt-live-chat-paid-message-renderer{
	border-radius: 0px;
}
}
