@-moz-document url-prefix("https://music.yandex.ru"), url-prefix("https://music.yandex.ua"), url-prefix("https://music.yandex.kz"), url-prefix("https://music.yandex.by") {
/* COLOR */
/* GRID:
    Blue Backgrounds:
        - #15161a hsb(222, 18, 10)
        - #2F343F hsb(218, 18, 15)
        - #2a2e33 hsb(214, 18, 20)
        - #343a40 hsb(210, 18, 25)
        - #3f474d hsb(206, 18, 30)
        - #495359 hsb(202, 18, 35)
		- #546066 hsb(198, 18, 40)
		- #5e6e73 hsb(194, 18, 45)

    Accent:
        - #e5ae5c hsb(36, 60, 90)
        - #f2bd61 hsb(38, 60, 95);
		- #ffd18c hsb(36, 45, 100);

    Foreground:
        - #e9f2f2 hsb(180, 4, 95)
        - #dce5e5 hsb(184, 4, 90)
        - #d0d7d9 hsb(188, 4, 85)
        - #c4cacc hsb(192, 4, 80)
        - #b8bdbf hsb(196, 4, 75)
*/

:root {
    --color_bg1: #15161a;
    --color_bg2: #2F343F;
    --color_bg3: #2a2e33;
    --color_bg3_95p: rgba(42, 46, 51, 0.95);
    --color_bg4: #343a40;
    --color_bg5: #3f474d;
    --color_bg6: #495359;
    --color_bg7: #546066;
    --color_bg8: #5e6e73;
                                            
    --color_accent1: #e5ae5c;
    --color_accent1_op50p: rgba(229, 174, 92, 0.75);
    --color_accent2: #f2bd61;
    --color_accent3: #ffd18c;
    
    --color_fg1: #e9f2f2;
    --color_fg2: #dce5e5;
    --color_fg3: #d0d7d9;
    --color_fg4: #c4cacc;
    --color_fg5: #b8bdbf;
}

/* ACCENT */
.button_action.button,
.progress__progress .progress__line,
.slider-vert:hover .slider-vert__filled,
.select__item:hover,
.nav__popup-item:hover,
.button2,
.add-to-popup__head .add-to-popup__item:hover,
.add-to-popup__item:hover,
.button_round.button_checked, .button_round.button_checked.button_transparent.button:hover, .button_round.button_checked:hover,
.multi-auth__item:hover {
    background: var(--color_accent1);
}

.slider-vert__filled {
    background: var(--color_accent1_op50p);
}

.button_action.button:hover,
.button_action.button:active,
.button2:hover, .button2:focus {
    background: var(--color_accent2);
}

.track_selectable.track:hover:before, .track_selectable.track_hover:before, .track_selectable.track_selected:before,
.button_action.button,
.artist__content:hover:before, .artist__content_hover:before, .artist__content_selected:before,
.artist_hover .artist__content:before, .artist_selected .artist__content:before,
.concert_selectable.concert:hover:before, .concert_selectable.concert_hover:before, .concert_selectable.concert_selected:before,
.album_selectable.album:hover:before, .album_selectable.album_hover:before, .album_selectable.album_selected:before,
.button:focus,
.playlist_selectable.playlist:hover:before, .playlist_selectable.playlist_hover:before, .playlist_selectable.playlist_selected:before,
._nb-small-simple-input:focus {
    border: 1px solid var(--color_accent1);
}

.button:focus {
	box-shadow: inset 0 0 0 1px var(--color_accent1);
}

.link_major, 
.editorial__description a,
.footer .link, .footer__arrow {
    color: var(--color_accent1);
}

.nb-toggler>input:focus+._nb-toggler-view:after, .nb-toggler>input:focus+._nb-toggler-view:hover:after {
	box-shadow: inset 0 0 0 1px var(--color_accent1);
}

.link:hover,
.editorial__description a:hover,
.footer .link:hover, .footer__arrow:hover {
    color: var(--color_accent2) !important;
}

.artist__content:hover, .artist__content_hover, .artist__content_selected,
.track_selectable.track:hover, .track_selectable.track_hover, .track_selectable.track_selected,
.concert_selectable.concert:hover, .concert_selectable.concert_hover, .concert_selectable.concert_selected {
    border-top-color: var(--color_accent1) !important;
    border-bottom-color: var(--color_accent1) !important;
}

.button_action.button,
.button_action.button:hover {
	border: none;
}
/* BACKGROUNDS */
/*
body, 
.centerblock-wrapper,
.head,
.sidebar,
.event__tracks,
.artist__content,
.sidebar-cont,
.concert,
.track_selectable.track,
.artist__content,
.concert_selectable.concert,
.bar,
.progress__bg,
.tooltip_theme_light .tooltip__content,
.button_round,
.album_selectable.album {
	transition-property: background-color;
    transition-duration: 0.1s;
} 
*/

body, 
.centerblock-wrapper,
.popup-sequence__content,
.page-genre__promo-date-content,
.head__suggest-input,
.page-artist__promo-date-content,
.page-playlist__title_edit,
.action-group__item .select:hover,
.action-group__item .button {
    background: none;
    background-color: var(--color_bg2);
}

.head,
.sidebar,
.event__tracks,
.artist__content,
.sidebar-cont,
.concert,
.album_selectable.album,
.more .button,
.page-feed__more,
.popup-sequence__header,
.popup-sequence .tabs,
.popup-sequence__close-wrapper,
.nav__subnav,
.page-genre__more:active,
.playlist_selectable.playlist,
.add-to-popup__foot input,
.sidebar-track .action-group__item .button,
.event_removed {
    background: none;
    background-color: var(--color_bg3);
}

.track_selectable.track:hover,
.artist__content:hover,
.concert_selectable.concert:hover,
.album_selectable.album:hover,
.button:active,
.page-feed__more:active,
.volume__bar,
.popup-sequence .button,
.button,
.playlist_selectable.playlist:hover,
.page-artist__promo-date:after,
.add-to-popup__list,
.sidebar-track .action-group__item .button:hover,
._nb-popup,
._nb-popup-tail:before {
	background-color: var(--color_bg4);
}

.promo__controls .button {
	background-color: transparent;
}
	
.bar {
    background-color: var(--color_bg3_95p);
}

.progress__bg,
.tooltip_theme_light .tooltip__content,
.button_round:hover,
.volume__control,
.popup,
.button:hover,
.action-group__item .button:hover {
    background-color: var(--color_bg5);
}

.slider-vert__track,
.button-arrow.theme1,
.page-genre__promo-date:after {
    background-color: var(--color_bg7);
}

.slider-vert__drag,
._nb-small-toggler ._nb-toggler-view-content:after {
	background-color: var(--color_fg3);
}

/* FOREGROUND */

*, 
.event__title,
.track__title,
.link,
.head__suggest-input, .head__suggest-input:focus, .nb-is-focused .head__suggest-input {
    color: var(--color_fg5);
}

.nav__tab, .nav__tab:active,
.tooltip_theme_light .tooltip__content,
.tooltip__static-text,
.page-feed__more,
.slider-vert__track:after, .slider-vert__track:before,
.equalizer-band__min, .equalizer-band__max, .equalizer-band__zero,
.equalizer-band__hz,
.equalizer__select .button-arrow__text,
.nav__popup-item, .nav__popup-item:visited,
.footer__social-link .link,
.app-sms_theme_banner .app-sms__description,
.add-to-popup__foot input,
.sidebar-track__translate-link,
.event_removed .event__description,
.multi-auth__item, .multi-auth__item:hover, .multi-auth__item:active {
    color: var(--color_fg4);
}

.select__link, .select__text,
.editorial__title,
.event_removed .event__title {
	color: var(--color_fg2);
}

.nav__tab:hover,
.page-feed__more:hover,
.button-arrow.theme1:hover .button-arrow__text,
.link.popup-sequence__history-tab:hover,
.tabs__tab .link:hover,
.title_hover:hover .title__label,
.footer__social-link .link:hover,
.page-artist__more:hover,
.page-playlist__desc-text_empty:hover {
    color: var(--color_fg1) !important;
}

.promo a, .promo a:link,
.button:hover .button__label,
.teaser__content_type_full .teaser__text {
	color: var(--color_fg1);
}
.promo__description,
.event .editorial__title {
	color: var(--color_fg4);
}

.button_action.button .button__label,
.progress__left, .progress__right,
._nb-toggler-on,
.select__item:hover .select__link, .select__item:hover .select__text,
.nav__popup-item:hover,
.button2_rounded .button2__label,
.add-to-popup__head .add-to-popup__item:hover,
.add-to-popup__item:hover,
.notice_negative,
.notice_positive,
.multi-auth__item:hover {
	color: var(--color_bg1) !important;
}

.multi-auth__premium {
    color: #77d709;
}

.multi-auth__item:hover .multi-auth__premium {
    color: #356104;
}
}

