.promo_colored,
.teaser__content {
	background-image: url('data:image/png;base64,') !important;
}

.links__link .link {
    background-position: 0px -2px;
}

.add-to-popup__item {
    padding: 7px 18px 7px 46px;
}

.button2_rounded.button2_w-icon.button2_w-icon-centered .icon {
    left: 52%;
    top: 54%;
    margin-left: -12px;
}
}

