css:
@-moz-document url-prefix("http://jira"), url-prefix("https://jira"), domain("jira.atlassian.com") {
body,
html {
    min-height: 100%!important;
}
* {
    color: #c0c0c0!important;
    border-color: #60666a;
    box-shadow: 0 0 0 #333!important
}

/** Issue Search Table Borders */
.aui-page-panel,
.navigator-search,
.saved-search-selector,
#issuetable>tbody>tr>td,
.issue-table>tbody>tr>td,
#issuetable>tbody>tr>th,
.issue-table>tbody>tr>th,
#issuetable>tfoot>tr>td, 
.issue-table>tfoot>tr>td,
table.aui>tbody>tr>td, 
table.aui>tbody>tr>th, 
table.aui>tfoot>tr>td,
table.aui>tbody>tr, 
table.aui>tfoot>tr,
.ghx-issue,
.ghx-issue-compact,
.ghx-detail-contents,
/* Agile Views */
#ghx-plan, 
#ghx-work, 
#ghx-report-scrollarea,
#ghx-backlog,
.ghx-backlog-container,
.ghx-backlog-container .ghx-backlog-header,
.ghx-backlog-container .ghx-plan-dropzone,
.ghx-marker,
.ghx-column-headers .ghx-column,
.ghx-controls-filters dd a,
/* Labels & Buttons */
.aui-button,
.aui-style-default .aui-dropdown2-section+.aui-dropdown2-section,
.aui-label, 
.aui-dropdown2.aui-style-default,
/* Sidebar */
.aui-sidebar .aui-sidebar-wrapper,
.aui-sidebar-submenu .aui-sidebar-wrapper,
.aui-sidebar .aui-sidebar-footer, 
.aui-sidebar-submenu .aui-sidebar-footer,
/* Dashboard */
.gadget .dashboard-item-content, 
.gadget .dashboard-item-header,
.date-header,
.activity-item,
.activity-item-grouped .activity-item-summary,
.activity-item-description blockquote,
#activity-stream-show-more,
/* Issues Detail View */
.details-layout .split-view .list-results-panel, 
.details-layout .split-view .list-results-panel .list-panel .issue-list>li,
.toolbar-trigger,
.tabwrap>.tabs.horizontal,
.tabwrap>.tabs.horizontal>li>a, 
.tabwrap>.tabs.horizontal>li>strong,
/* Filter Panel */
.navigator-sidebar,
.filter-panel-wrapper, 
.filter-panel-section,
ul.labels li .lozenge,
.ajs-layer
{
    border-color: #60666a!important;
}

/* Priority None */
img[src*="images/icons/clearPixel.gif"] {
    display:none;
}

/* Report Table Borders */
* body a,
body a * {
    color: #B6AA7B!important;
}

html,
body {
    background-color: #111!important
}
body > *:not(:empty):not(canvas):not([class*="js-card-color-edit"]):not([class*="colorpicker"]) {
    background-color: #222!important
}
body > * > *:not(:empty):not(canvas):not([class*="js-card-color-edit"]):not([class*="colorpicker"]) {
    background-color: #222!important
}
body > * > * > *:not(:empty):not(canvas):not([class*="js-card-color-edit"]):not([class*="colorpicker"]) {
    background-color: transparent
}
body > * > * > * > *:not(:empty):not(canvas):not([class*="js-card-color-edit"]):not([class*="colorpicker"]) {
    background-color: #181818!important;
    text-shadow: none;
}
body > * > * > * > * > *:not(:empty):not(canvas):not([class*="js-card-color-edit"]):not([class*="colorpicker"]) {
    background-color: #181818!important
}
body > * > * > * > * > * *:not([class^="tm-label-circle"]):not([class^="tm-status-dropdown"]):not([class^="ghx-grabber"]):not([class*="ghx-active"]):not([class*="aui-icon"]):not([class*="aui-badge"]):not(canvas):not([class*="js-card-color-edit"]):not([class*="colorpicker"]):not([class*="progressbar__entry"]):not([class*="progressbar"]):not([class*="progressbar_full"]):not([class*="progressbar-container"]) {
    background-color: #181818!important;
    text-shadow: none;
}
.aui-label{
    text-shadow: none!important;
}
ul.labels li .lozenge {
    overflow: hidden;
}

/* Status Lozenges */
.aui-lozenge.jira-issue-status-lozenge-blue-gray {
    background-color: #4a6785!important;
    border-color: #4a6785!important;
}

.aui-lozenge.jira-issue-status-lozenge-green {
    background-color: #14892c!important;
    border-color: #14892c!important;
}
.aui-lozenge.jira-issue-status-lozenge-yellow {
    background-color: #ffd351!important;
    border-color: #ffd351!important;
}



#ghx-pool div.ghx-selected > .ghx-issue-content,
#ghx-pool div.ghx-selected > .ghx-issue-content div,
#ghx-pool div.ghx-selected > .ghx-issue-content span,
#ghx-pool div.ghx-selected > .ghx-issue-content > .ghx-extra-fields,
#content > div.aui-sidebar.projects-sidebar.fade-in > div > div.aui-sidebar-body > nav > div > div > ul > li.aui-nav-selected > a,
#content > div.aui-sidebar.projects-sidebar.fade-in > div > div.aui-sidebar-body > nav > div > div > ul > li.aui-nav-selected > a > .aui-nav-item-label,
#ghx-content-group > div.ghx-sprint-group div.js-issue.ghx-selected div,
#ghx-content-group > div.ghx-sprint-group div.js-issue.ghx-selected span:not(.aui-label) {
    background-color: #292929!important;
}
#ghx-content-group > div.ghx-sprint-group div.js-issue.ghx-selected.ghx-selected-primary div,
#ghx-content-group > div.ghx-sprint-group div.js-issue.ghx-selected.ghx-selected-primary span:not(.aui-label) {
    background-color: #333!important;
}
.ghx-backlog-container .ghx-backlog-header {
    border: 1px dashed transparent;
}

/** Inline Rich Editors */
body#tinymce {
    background-color: #181818!important;
    text-shadow: none;
}

/* Issue Detail Editable Field Hover */
.editable-field .overlay-icon.aui-iconfont-edit {
    color: #b0b0b0!important;
    cursor: pointer;
}
.editable-field .overlay-icon {
    background-color: #282828;
}

/* Growl messages in top-right*/
.aui-message {
    background-color: #181818!important;
}
#content .aui-sidebar + .aui-page-header,
#content .aui-sidebar-submenu + .aui-page-header {
    background-color: #181818!important;
}

/* Dropdown button icons */
.aui-button.aui-button.ghx-dropdown-trigger:after {
    filter: invert(100%);
    -webkit-filter: invert(100%);
}
.aui-badge.ghx-statistic-badge {
    color: #c0c0c0!important;
}

/* Agile - Not Started, In Progress, Completed point header badges */
.ghx-backlog-header.js-sprint-header .aui-badge {
    color: #bfbfbf!important;
}

/* Create Button */
#create_link {
    background-color: #1b4673!important;
}

/* Agile - Quick Filters - Active Selection */
.ghx-controls-filters.js-quickfilter-selector .js-quickfilter-button.ghx-active {
    background-color: #1b4673!important
}

/* Agile - Reports */
#ghx-chart-legend * {
    background-color: transparent!important;
    border-color: initial;
}

/* Agile Poker */
/* Async Estimation - Selected Point Value Voting Button */
#app > div > div > nav > .session-layout-issue > div > div.session-layout-issue-details .voting-select-button[is-selected]{
    background-color: #565656!important;
    border-width: 2px;
    border-color: #999!important;
}

body table:not(:empty) {
    background-color: #282828!important
}
body table > tbody > tr:nth-child(even) {
    background-color: rgba(0, 0, 0, .2)!important
}
body table > tbody > tr > td:nth-child(odd) {
    background-color: rgba(0, 0, 0, .1)!important
}
body *:empty:not([class*="icon"]):not([class^="tm-"]):not([class^="ghx-grabber"]):not(canvas):not([class*="progressbar__entry"]) {
    background-color: #111!important
}
body p:not(:empty),
body p *,
body h1,
body h1 *,
body h2,
body h2 *,
body h3,
body h3 *,
body h4,
body h4 *,
body h5,
body h5 *,
body strong > *,
body b > *,
body em > *,
body i > *,
body span > *:not(img):not([class*=js-card-color-edit]) {
    background: transparent none!important
}
body h1,
body h1 *,
body h2,
body h2 *,
p > strong:only-of-type,
p > b:only-of-type {
    color: #a98!important
}
body h3,
body h3 *,
body h4,
body h4 * {
    color: #aaa!important
}
*[onclick] {
    color: #79a!important
}
*[onclick]:hover {
    color: #99a8aa!important
}
body hr {
    background: #666 none!important;
    color: #666!important;
    border: 1px solid #666!important;
    height: 1px!important;
    overflow: hidden!important;
    display: block!important
}

/* body a:visited, body a:visited *{color: #666!important;} */
body a:hover,
body a:hover * {
    color: #D9C077!important;
    text-decoration: underline!important
}
body span > img {
    visibility: visible!important
}
body input[type],
input,
body textarea,
body select {
    -moz-appearance: none!important;
    color: #bbb!important;
    border-radius: 4px !important;
    border-width: 1px!important;
    border-color: rgba(100, 100, 100, .5)!important;
    border-style: solid!important;
    background: -moz-linear-gradient( top, #303030 40%, #383838, #343434) #333 !important;
    box-shadow: inset 1px 1px 1px #111!important
}
body input[type="submit"],
body input[type="button"],
body input[type="image"],
body input[type="reset"],
body button {
    -moz-appearance: none!important;
    color: #bbb!important;
    border-radius: 4px !important;
    border-width: 1px!important;
    border-color: rgba(100, 100, 100, .5)!important;
    border-style: solid!important;
    background: -moz-linear-gradient( top, #404040 45%, #282828 55%) #333 !important;
    box-shadow: 1px 1px 1px #111!important
}
body input[type] > *,
body textarea > * {
    -moz-appearance: none!important;
    background: transparent none !important;
    color: #bbb!important;
    border-style: solid!important;
    border-width: 0px!important;
}
body select * {
    background-color: transparent !important;
    color: #bbb!important;
    border-style: solid!important;
    border-width: 0px!important;
}
pre:not(:empty),
code:not(:empty),
cite:not(:empty),
pre:not(:empty) *,
code:not(:empty) *,
cite:not(:empty) * {
    background-image: url(data:image/gif;base64,R0lGODlhBAAEAIAAABERESIiIiH5BAAAAAAALAAAAAAEAAQAAAIGTACXaHkFADs=)!important;
    color: #bcc8dc!important;
}
.aui-toolbar .toolbar-group .toolbar-trigger,
.aui-toolbar .aui-dd-parent a.aui-dd-trigger {
    text-shadow: 0 0px 0 white;
}
.aui-inline-dialog .buttons-container.content-footer input.cell-add {
    background: #000!important;
}
.aui-sidebar .aui-nav > li > .aui-nav-item > .aui-icon,
.aui-sidebar-submenu .aui-nav > li > .aui-nav-item > .aui-icon {
    filter: invert(100%);
    -webkit-filter: invert(100%);
}
a.js-quickfilter-button.last.ghx-active,
a.js-quickfilter-button.first.ghx-active,
a.js-quickfilter-button.ghx-active,
.ghx-controls-filters dd a.ghx-active {
    background: #3572b0!important;
}
.editable-field .overlay-icon {
    color: transparent!important;
}
.tm-wc-item:hover .editable-field .overlay-icon:after,
.tm-description:hover .editable-field .overlay-icon:after {
    content: "\f142";
    color: #b6aa7b;
    font-family: "Atlassian Icons";
    left: 10px;
    position: absolute;
    top: 10%;
}
.ghx-swimlane-header:after,
.ghx-swimlane-header.ghx-selected:after {
    visibility: hidden;
}
.ghx-grabber {
    filter: brightness(60%);
    -webkit-filter: brightness(60%);
}
.tm-label-circle {
    filter: brightness(60%) contrast(300%);
    -webkit-filter: brightness(60%) contrast(300%);
}
.tm-status-dropdown {
    filter: brightness(60%) contrast(150%);
    -webkit-filter: brightness(60%) contrast(150%);
}
::-webkit-scrollbar {
    width: 12px;
    /* This is more usable for users trying to click it. */
    background-color: rgba(0, 0, 0, 0);
    -webkit-border-radius: 100px;
}

/* hover effect for both scrollbar area, and scrollbar 'thumb' */
::-webkit-scrollbar:hover {
    background-color: rgba(0, 0, 0, 0.09);
}

/* The scrollbar 'thumb' ...that marque oval shape in a scrollbar */
::-webkit-scrollbar-thumb:vertical {
    /* This is the EXACT color of Mac OS scrollbars. 
     Yes, I pulled out digital color meter */
    background: rgba(0, 0, 0, 0.5);
    -webkit-border-radius: 100px;
}
::-webkit-scrollbar-thumb:vertical:active {
    background: rgba(0, 0, 0, 0.61);
    /* Some darker color when you click it */
    -webkit-border-radius: 100px;
}
}
