css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

  html, body {
    background: #111!important;
    color: #D2D2D2!important;
  }
  #navigation {
    background-color: #262832!important;
    min-width: 220px;
    padding: 0 20px 100px 42px;
}
  #nav-view {
    display: flex;
    flex-direction: row;
    background-color: #111;
    padding-top: 30px;
}
  .style_card {
    width: 220px;
    border-radius: 4px;
    background-color: #222!important;
    box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.08);
    margin-right: 20px;
    margin-bottom: 20px;
}
  
  .fb_reset {
    background: #111!important;
    color: #DDD0D0!important;
}
  #footer {
    width: 100%;
    background-color: #111!important;
    padding-top: 33px;
    padding-bottom: 32px;
    display: flex;
}
  h1 {
    font-size: 30px;
    color: #8599ad!important;
}
  .linksBox .navigation_link:last-child {
    box-shadow: 0 1px 0 0 #3d3e3f, 0 0 0 0 #40464E;
}
  .navigation_title {
    font-size: 16px;
    height: 19px;
    color: #a0a9b1;
    margin: 0 0 0 10px;
}
  .screenshot_wrapper {
    width: 220px;
    height: 128px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-bottom: 1px solid #333!important;
}
  .navigation_link:hover {
    background-color: #32353b;
    box-shadow: 0 -1px 0 0 #dfe1e3, 0 1px 0 0 #dee0e3;
    cursor: pointer;
}
  .white_button {
    border-radius: 4px;
    background-color: #29292a;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.2);
    border: solid 1px #183757;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 48px;
    width: 87px;
    font-size: 16px;
    color: #7495b7;
    font-weight: bold;
    cursor: pointer;
}
  .white_button:hover {
    border-radius: 4px;
    background-color: #29293a;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.2);
    border: solid 1px #183757;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 48px;
    width: 87px;
    font-size: 16px;
    color: #7495b7;
    font-weight: bold;
    cursor: pointer;
}
  #style_installed_button {
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    border-radius: 4px;
    background-color: #29292a;
    border: solid 1px #2ecc71;
    width: 150px;
    height: 48px;
    font-size: 16px;
    font-weight: 600;
    color: #2ecc71;
    margin-left: 11px;
    cursor: pointer;
}
   #style_installed_button:hover {
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    border-radius: 4px;
    background-color: #29293a;
    border: solid 1px #2ecc71;
    width: 150px;
    height: 48px;
    font-size: 16px;
    font-weight: 600;
    color: #2ecc71;
    margin-left: 11px;
    cursor: pointer;
}
  #css_button {
    width: 171px;
    height: 43px;
    border-radius: 4px;
    background-color: #29292a;
    display: flex;
    flex-direction: row;
    font-size: 14px;
    font-weight: bold;
    align-items: center;
    justify-content: center;
    color: #3498db;
    margin-top: 30px;
    cursor: pointer;
}
  #css_button:hover {
    width: 171px;
    height: 43px;
    border-radius: 4px;
    background-color: #29293a;
    display: flex;
    flex-direction: row;
    font-size: 14px;
    font-weight: bold;
    align-items: center;
    justify-content: center;
    color: #3498db;
    margin-top: 30px;
    cursor: pointer;
}
  #notes_textarea {
    background-color: transparent;
    border: none;
    resize: vertical;
    overflow-y: scroll;
    font-size: 15px;
    color: #2c3e50;
    max-height: 300px;
    width: 700px;
}
  #send_feedback_button {
    width: 172px;
    height: 48px;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    border-radius: 4px;
    border: solid 1px #222;
    background-color: #29292a;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.2);
    color: #2c3e50;
    font-size: 16px;
    font-weight: bold;
    cursor: pointer;
    margin-top: 10px;
    margin-bottom: 18px;
}
  #send_feedback_button:hover {
    width: 172px;
    height: 48px;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    border-radius: 4px;
    border: solid 1px #222;
    background-color: #29293a;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.2);
    color: #2c3e50;
    font-size: 16px;
    font-weight: bold;
    cursor: pointer;
    margin-top: 10px;
    margin-bottom: 18px;
}
  .see_more {
    width: 102px;
    height: 36px;
    border-radius: 4px;
    background-color: #29292a;
    font-size: 14px;
    font-weight: bold;
    color: #3498db;
    margin-top: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
}
  .see_more:hover {
    width: 102px;
    height: 36px;
    border-radius: 4px;
    background-color: #29293a;
    font-size: 14px;
    font-weight: bold;
    color: #3498db;
    margin-top: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
}
  h4 a {
    color: #aaa !important;
  }
  h4 a:hover {
    color: #aaa !important;
    text-decoration: none;
}
}
