css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://gym-k.com/prof_shun.html") { 
   /*-----------------名前-----------------*/
  #name{
   font-size:0;
   padding-top: 24px !important;
   padding-bottom: 35px !important;
  }
  #name::before{
    font-size:18px;
    content: "俊（Shun）タクヤ（Takuya）俊介（Shunsuke）コウ（Ko）";
  }
    /*-----------------コメント欄-----------------*/
 #comment > p:nth-child(1){
    font-size:0;
   padding-bottom:  10px !important;
  }
  #comment > p:nth-child(1):before{
    font-size: 13px; 
    content: "さｰふぁｰ系まっちょ参上！ 　本式的にじむけいに大復帰!!ばるく上がるしたでござる筋肉美は圧巻!!ひたすら筋肉受け。 　 ぼでぃびるとさｰふぃんにて鍛ゑたるっくすは今風にて話し者も優しげ！　 ところがじゃ夜の営みははｰどにてばっくは締りがよくぞよがり狂ったり！　ＳもＭも可能なひい面もあり！映像出演有　髪型はろん毛でござる。";
  }
  
  /*-----------------身長/体重/年齢-----------------*/
  #data > dd:nth-child(2) ,  #data > dd:nth-child(4) ,  #data > dd:nth-child(6) ,  #data > dd:nth-child(8) ,  #data > dd:nth-child(10) ,  #data > dd:nth-child(12) ,  #data > dd:nth-child(14) ,  #data > dd:nth-child(16) ,  #data > dd:nth-child(18){
    font-size:0;
    margin-top: -15.6px !important;
    margin-bottom: 6.5px !important;
    padding-bottom: 6.5px !important;
  }
  #data > dd:nth-child(2)::before , #data > dd:nth-child(4)::before , #data > dd:nth-child(6)::before , #data > dd:nth-child(8)::before , #data > dd:nth-child(10)::before , #data > dd:nth-child(12)::before , #data > dd:nth-child(14)::before , #data > dd:nth-child(16)::before , #data > dd:nth-child(18)::before{
    font-size: 13px; 
    margin-bottom: 6.5px;
    margin-top: 9px;
    padding-bottom: 6.5px;
    padding-left: 143px;
    
  }
  #data > dd:nth-child(2)::before{
 content: "112015cm×107kg×9315歳";   
  }
  
   /*-----------------BWH-----------------*/
  #data > dd:nth-child(4):before{
    content: "B110cm×W72cm×H90cm";
  }
  
/*-----------------Pサイズ-----------------*/
  #data > dd:nth-child(6):before{
    content: "デママラ";
  }
/*-----------------足サイズ-----------------*/
  #data > dd:nth-child(8):before{
    content: "364364cm";
  }

  /*-----------------体型-----------------*/
  #data > dd:nth-child(10):before{
    content: "腹筋マッチョ";
  }
  
  /*-----------------スポーツ歴-----------------*/
  #data > dd:nth-child(12):before{
    content: "ジム/水泳/";
  }
  
  /*-----------------ヒゲ-----------------*/
  #data > dd:nth-child(14):before{
    content: "無";
  }
  /*-----------------絡みタチウケ-----------------*/
  #data > dd:nth-child(16):before{
    content: "タチ◎/ウケ◎";
  }
    /*-----------------バックタチウケ-----------------*/
  #data > dd:nth-child(18):before{
    content: "ウンコ";
  }
    /*-----------------シフト表-----------------*/
  :root{
    --shift:"5月のシフト\A\A完売";
    --star:"★";
  }
  
  #p_shun > td:nth-child(2) , #p_shun > td:nth-child(3) ,  #p_shun > td:nth-child(4) ,  #p_shun > td:nth-child(5) , #p_shun > td:nth-child(6) , #p_shun > td:nth-child(7) , #p_shun > td:nth-child(8){
    font-size: 0px !important;
  }
  #p_shun > td:nth-child(2)::before , #p_shun > td:nth-child(3)::before,#p_shun > td:nth-child(4)::before , #p_shun > td:nth-child(5)::before ,  #p_shun > td:nth-child(6)::before , #p_shun > td:nth-child(7)::before,#p_shun > td:nth-child(8)::before{
    font-size: 12.0833px !important;
    font-weight: bold !important;
    white-space: pre !important;
  }
#p_shun > td:nth-child(2)::before , #p_shun > td:nth-child(4)::before ,  #p_shun > td:nth-child(7)::before{
color:rgb(238,68,68) !important;
  content: var(--shift);
  }
   #p_shun > td:nth-child(3)::before, #p_shun > td:nth-child(5)::before , #p_shun > td:nth-child(6)::before , #p_shun > td:nth-child(8)::before{
   color: rgb(51,51,51) !important;
    content: var(--star);
  }
}
