css:
@-moz-document url("chrome://speeddial/content/speeddial.xul") {
  #speeddialTabbox-panelContainer {
    background-color: #f3f5f5;
  }
  
  .sd-tab,
  .sd-tab[selected="true"] {
    width: 100% !important;
    height: 40px !important;
    text-align: center !important;
    border: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
    background-color: #eef0f0 !important;
    cursor: pointer;
    text-transform: uppercase !important;
    font-size: 11px !important;
    color: #444;
    font-weight: bold !important;
  }
  
  .sd-tabs-left,
  .sd-tabs-right {
    border-bottom: 0 !important;
    background-color: #eef0f0 !important;
    min-width: 10.5% !important;
  }
  
  #speeddialTabbox-tabs .scrollbox-innerbox {
    display: flex;
  }
  
  .sd-tab[selected="true"] {
    background-color: #fff !important;
    cursor: default;
  }

  .thumbnail{
    opacity: .8 !important;
    transition: all .25s ease !important;
  }

  .thumbnail:hover  {
    opacity: 1 !important; 
  }

  .speeddial-container { 
    background-color: transparent !important;
    border: 1px solid #e9e9e9 !important;
    transition: all .25s ease !important;
  }

  .speeddial-container:hover { 
    border-color: #fff !important; 
  }
  
  .speeddial-margin-container {
    transition: all .25s ease !important;
    max-width: 300px;
    max-height: 200px;
  }

  .speeddial-margin-container:hover {
    box-shadow: 0 0 7px rgba(44,49,60,.4) !important;
  }

  .speeddial-container[assigned="f"] {
    background-image: none !important
  }

  .thumbnail-title,
  .speeddial-top-middle,
  .speeddial-bottom-middle,
  .speeddial-top-middle,
  .speeddial-middle-left,
  .speeddial-middle-right,
  .speeddial-bottom-middle,
  .speeddial-top-left,
  .speeddial-top-right,
  .speeddial-bottom-left,
  .speeddial-bottom-right {
       display: none;
  }
}
